INSERT INTO `broadcast_text_locale` VALUES (85495, 'enUS', 'Have you seen Brightstone? My latest assistant is more than a handful.$b$bShe does everything to the extreme - drinking, talking, rushing from one task to another...$b$bShe is a damn fine architect though, and probably the brightest student I\'ve ever had.$b$bI\'ll give her that.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85496, 'enUS', '', 'Commander! I trust you are prepared to have your mind blown away by my designs.$b$bI am thinking... wood! Yes, wood! Lots of it. In BOLD arrangement the way that only the orcs of the original horde could conceive.$b$bAnd spikes! Yes, spikes. We must not forget the spikes. That is how you know it is Horde.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85497, 'enUS', 'Lieutenant Thorn is a shining example to us all. Never have I seen such a dedicated leader.$B$BI haven\'t worked up the courage to speak to her, but perhaps someday, she will know my name.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85498, 'enUS', 'You need a tree murdered, commander?', '', 26124);
