INSERT INTO `broadcast_text_locale` VALUES (85525, 'enUS', 'I have dedicated my life to preserving priceless artifacts and then quickly selling them for profit.$b$bIt\'s a difficult job, but I like money. Know what I\'m sayin?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85526, 'enUS', 'If you find anything that looks like it needs some preserving let me know, chances are I can sell it somebody who likes old stuff.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85527, 'enUS', 'You know the old saying?$b$bOne goblin\'s junk, is another goblin\'s garbage, so sell it to someone stupid enough to buy it.$b$bMy dear old mother told me that one, right before she sold me to that caravan of worgen.$b$bStill not sure how I feel about that.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85528, 'enUS', '<The wyvern looks at you expectantly.>', '', 26124);
