INSERT INTO `broadcast_text_locale` VALUES (85649, 'enUS', 'Blackhand has cast |TInterface\\Icons\\ability_rogue_hungerforblood.blp:20|t|cFFFF0404|Hspell:168261|h[Marked for Death]|h|r on you. Hide behind the Iron Stars to avoid the attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85650, 'enUS', 'What?! GGRAAAHHH!!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85651, 'enUS', '', 'I don\'t want to say we were unprepared, but we are short on supplies here in Ashran. We just didn\'t bring enough through the portal.$B$BHopefully Sprocket\'s team can get us the heavy machinery we need to win this battle against the Horde.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85652, 'enUS', 'Are you alright? That was a close call.', '', 26124);
