INSERT INTO `broadcast_text_locale` VALUES (85654, 'enUS', 'No tree can withstand the force of my blows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85655, 'enUS', 'This monster tree will be obliterated in no time, commander. Stand by!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85656, 'enUS', 'I\'m a mobile lumber mill, built for shredding wood. Stand back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85657, 'enUS', 'I could do better.', 'I could do better.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85658, 'enUS', '', 'I have been waiting all day for some repairs to be done. But that\'s okay, there are plenty of things to do to pass the time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85659, 'enUS', '', 'Fellworth got our group lost by finding a \"shortcut\" through the hollow and now my boots are full of slime and mud!\n\nI think I will be charge of the map from now on.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85660, 'enUS', '', 'Your flight master insisted you fly back.', 26124);
