INSERT INTO `broadcast_text_locale` VALUES (85662, 'enUS', 'Very well, Birchus, let\'s see if this works.  Here\'s the Heart Seed.', 'Very well, Birchus, let\'s see if this works.  Here\'s the Heart Seed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85663, 'enUS', 'You will not be disappointed. Finally my long adventure is over! Arise Dionor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85664, 'enUS', 'We weren\'t lost; I was just taking the adventurous route!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85665, 'enUS', 'So, what do you do for a living?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85666, 'enUS', 'Well, I study and translate ancient mogu scrolls.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85667, 'enUS', 'Yeah, I know, tell that to my C.O.', '', 26124);
