INSERT INTO `broadcast_text_locale` VALUES (85669, 'enUS', '', 'Let me check the map, Fellworth. I will decide where we travel next.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85670, 'enUS', 'Patrols have been increased throughout the city.  We will not succumb to attacks by these savages.', 'Patrols have been increased throughout the city.  We will not succumb to attacks by these savages.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85671, 'enUS', 'Defenders of Karabor stand ready!', 'Defenders of Karabor stand ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85672, 'enUS', 'Our people nor our city shall fall to the savages!', 'Our people nor our city shall fall to the savages!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85673, 'enUS', 'It is now we must come together, stronger than ever.', 'It is now we must come together, stronger than ever.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85674, 'enUS', 'It bein\' just my luck, mon! I be findin\' da best fishin\' spot down \'ere, but only after da elementals and da ogres broke up all my gear. \n\nNow I\'m needin\' a new rod, reel, and da special voodoo lure so I can land da big ones hidin\' in da weeds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85675, 'enUS', 'I think I can help you. (Give Abu\'gar his rod, reel and lure)', 'I think I can help you. (Give Abu\'gar his rod, reel and lure)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85676, 'enUS', 'Hey, commander! You sure you aren\'t part Laughing Skull?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85677, 'enUS', 'You have proven that you have the strength to survive even the most dangerous of situations. Your technique in combat is unlike anyone I have seen. I am glad to call you friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85678, 'enUS', 'Ey, boss-mon. Take a load off. Relax.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85679, 'enUS', 'May the Light\'s power resonate within your actions. We shall not let the hatred of our savage enemies overcome our will to survive. Carry forth and return our people to health!', 'May the Light\'s power resonate within your actions. We shall not let the hatred of our savage enemies overcome our will to survive. Carry forth and return our people to health!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85680, 'enUS', '', 'So it is true. The heart CAN infuse life into these creatures.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85681, 'enUS', '', 'Ah, hero... I knew you would come. You are just in time to witness the heart\'s power.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85682, 'enUS', '', 'This magnaron walked Draenor before the first orc took breath. And now, it... and an endless army of these destroyers... bend to MY will!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85683, 'enUS', '', 'Follow me, $n. She\'s up this way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85684, 'enUS', 'Foreman, change this building into a tankworks.', 'Foreman, change this building into a tankworks.', 26124);
