INSERT INTO `broadcast_text_locale` VALUES (85686, 'enUS', 'That orc... Kargath. Even at my full strength, he would prove a challenge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85687, 'enUS', 'My children, it will take more than a fading memory from the past to overcome the challenges you face today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85688, 'enUS', 'Switching the building will cost 5,000 gold.\n\nAre you sure?', 'Switching the building will cost 5,000 gold.\n\nAre you sure?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85689, 'enUS', 'You must strike your own path. Lay the past to rest and raise new champions.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85690, 'enUS', 'So far the Alliance found them all over Ashran. Killing any creature you can find will no doubt increase your chances for discovery.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85691, 'enUS', '', 'We got wind that there are hidden artifacts here, in this place called Ashran. Artifacts from a remnant Highmaul Ogre clan.$B$BThey could be the key to our success against the Iron Horde.', 26124);
