INSERT INTO `broadcast_text_locale` VALUES (85808, 'enUS', '', 'Next time... I will reduce ALL of them to ash!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85809, 'enUS', '', '%s lets out a long, drawn out sigh.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85810, 'enUS', 'The enemy battleship is just off the coast.  We will hold off Blackhand, it\'s up to you to disable that boat and save Shattrath!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85811, 'enUS', 'What progress do you have to report? This better not be like last time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85812, 'enUS', '', 'We have learned how to dominate the minds of gronn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85813, 'enUS', 'Good. Finish your work and bring them to me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85814, 'enUS', '', 'Yes, Commander Gar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85815, 'enUS', 'Greetings, $n. Here to do some lumberjacking?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85816, 'enUS', 'I\'d like to submit a work order.', 'I\'d like to submit a work order.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85817, 'enUS', 'Place your hand on Nisha\'s head.', 'Place your hand on Nisha\'s head.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85818, 'enUS', '<Nisha flutters her wings, ready to go scouting for Rexxar.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85819, 'enUS', '<Kolrigg\'s eyes are closed and his skin is pale.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85820, 'enUS', 'Administer the antidote to Kolrigg.', 'Administer the antidote to Kolrigg.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85821, 'enUS', '', '<Lunzul\'s eyes are closed and her skin is pale.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85822, 'enUS', 'Administer the antidote to Lunzul.', 'Administer the antidote to Lunzul.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85823, 'enUS', 'Oi, me head.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85824, 'enUS', 'Ah, commander, I can\'t thank ye enough. I\'ll get the ol\' brewery up and runnin\' in two shakes of a talbuk\'s tail.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85825, 'enUS', '', 'My head be hurtin\' somethin\' terrible.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85826, 'enUS', '', 'Bless ya, boss mon. I be gettin\' da tavern up and runnin\' faster than you be believin\'.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85827, 'enUS', 'Your Alchemy Lab has been damaged!', 'Your Alchemy Lab has been damaged!', 26124);
