INSERT INTO `broadcast_text_locale` VALUES (85832, 'enUS', 'Your Stables have been damaged!', 'Your Stables have been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85833, 'enUS', 'Your Sparring Arena has been damaged!', 'Your Sparring Arena has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85834, 'enUS', 'Your Salvage Yard has been damaged!', 'Your Salvage Yard has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85835, 'enUS', 'Your Mage Tower has been damaged!', 'Your Mage Tower has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85836, 'enUS', 'Your Lumber Mill has been damaged!', 'Your Lumber Mill has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85837, 'enUS', 'Your Tannery has been damaged!', 'Your Tannery has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85838, 'enUS', 'Your Gem Boutique has been damaged!', 'Your Gem Boutique has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85839, 'enUS', 'Your Scribe\'s Quarters has been damaged!', 'Your Scribe\'s Quarters has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85840, 'enUS', 'Your Inn has been damaged!', 'Your Inn has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85841, 'enUS', 'Your Engineering Works has been damaged!', 'Your Engineering Works has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85842, 'enUS', 'The Iron Maidens have fallen? They were my favorite! Find these invaders and bring me their heads!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85843, 'enUS', 'Your Enchanter\'s Study has been damaged!', 'Your Enchanter\'s Study has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85844, 'enUS', 'That beast was irreplaceable. Where are the attackers now? I want them dead. DEAD!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85845, 'enUS', 'Your Blacksmith has been damaged!', 'Your Blacksmith has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85846, 'enUS', 'Your Barracks has been damaged!', 'Your Barracks has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85847, 'enUS', 'Your Barn has been damaged!', 'Your Barn has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85848, 'enUS', 'You? You\'re the dogs who have been wreaking havoc throughout my foundry? Step forward! I will put you in your place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85849, 'enUS', 'Your Armory has been damaged!', 'Your Armory has been damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85850, 'enUS', 'I will hang your broken bodies from the gates of this foundry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85851, 'enUS', 'You destroyed my furnace.... Now, my furnace will destroy YOU.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85852, 'enUS', 'Open the flumes! Flood the room with molten slag.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85853, 'enUS', 'I\'ll burn you alive, even if I have to flood the whole foundry...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85854, 'enUS', 'I\'ll bring this whole place down around your heads!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85855, 'enUS', 'I\'ll tear this place apart if I have to!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85856, 'enUS', 'We will rebuild this place... but no one will be able to put YOU back together.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85857, 'enUS', 'You are slow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85858, 'enUS', 'You are weak.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85859, 'enUS', 'You should\'ve ducked.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85860, 'enUS', 'There\'s no place to hide, now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85861, 'enUS', 'Coward!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85862, 'enUS', 'You hide like children!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85863, 'enUS', 'Get out here and fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85864, 'enUS', 'Everyone DOWN.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85865, 'enUS', 'Snipers! Gun them down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85866, 'enUS', 'Soldiers! Open fire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85867, 'enUS', 'Blackrock warriors - fire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85868, 'enUS', 'Bring in the siegemaker.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85869, 'enUS', 'Siege machines! Tear this place apart!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85870, 'enUS', 'Machines, open fire! Destroy them at any cost!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85871, 'enUS', 'Burn - you will all BURN!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85872, 'enUS', 'This foundry\'s molten heart will devour you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85873, 'enUS', 'Your bodies will fuel the flames.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85874, 'enUS', 'The furnace will consume you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85875, 'enUS', 'The hotter the flame, the stronger the steel...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85876, 'enUS', 'This cauldron is your end.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85877, 'enUS', 'Your time runs out.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85878, 'enUS', 'This crucible is your molten grave!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85879, 'enUS', 'Weak.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85880, 'enUS', 'You have no discipline.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85881, 'enUS', 'Our weapons are superior.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85882, 'enUS', 'Pah! Dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85883, 'enUS', 'That\'s all you can muster?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85884, 'enUS', 'I will finish this myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85885, 'enUS', 'This one should go out with a bang...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85886, 'enUS', 'This one\'s packing a little extra heat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85887, 'enUS', 'This prototype has double armor plating.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85888, 'enUS', 'The fastest machine in our arsenal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85889, 'enUS', 'We are... the Warchief\'s weapon... Without us, he...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85890, 'enUS', 'A wounded defender has perished!', 'A wounded defender has perished!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85891, 'enUS', 'Prepare to battle the Primals.', 'Prepare to battle the Primals.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85892, 'enUS', 'Try and keep the peon from dying. They\'re like monster magnets!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85893, 'enUS', '', '<A voice fills your thoughts.>$b$bWe must hurry before the children of Rukhmar can regroup their forces.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85894, 'enUS', 'Fine, but I\'ll have my eye on her.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85895, 'enUS', '', 'Much better, oaf! I am here to see the commander.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85896, 'enUS', 'Please, don\'t wander too far. We need you here, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85897, 'enUS', 'Shelly? Is that you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85898, 'enUS', 'Yes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85899, 'enUS', 'Don\'t trust them, Shelly! They are lying to you. They will eat you. They will eat us all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85900, 'enUS', 'I always did hate vegetables...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85901, 'enUS', '', 'Hi Mom!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85902, 'enUS', 'Well done, friend. It is rare for one to emerge from that cave alive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85903, 'enUS', 'Fall in line, maggots! Looks like we\'ve got company.', 'Fall in line, maggots! Looks like we\'ve got company.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85904, 'enUS', 'CRUSH THEM!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85905, 'enUS', '', 'Rubee just loves her treats.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85906, 'enUS', 'Calm yourself, Zoggosh. We will do no such thing. If these weaklings are so eager to die, then we should oblige.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85907, 'enUS', 'Zoggosh, do not question my authority. This isn\'t just ANY gronn. This is Skulloc, Son of Gruul. The blood of a champion courses through his veins. I\'m not concerned with these... paltry whelps.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85908, 'enUS', 'And what exactly do you think they\'re going to do with orcish iron? Fall on it and die, probably. I\'m not worried in the least.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85909, 'enUS', 'Hah. Would you look at that. Maybe I... underestimated them. Zoggosh! Prepare Skulloc! Let them have a taste of what he has to offer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85910, 'enUS', 'FIRE... FIRE! AIM, THEN FIRE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85911, 'enUS', 'What is wrong with you?! Keep firing you oaf!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85912, 'enUS', 'Argghhhh! Forget it! They\'ll never make it past the tracks. Makogg, Ahri\'ok - front and center! There\'s some new meat for you to tenderize.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85913, 'enUS', 'That was an insignificant loss. Let them exhaust themselves, preying against our weak -- against those who are unworthy of serving in Blackhand\'s army! Blood and honor, Zoggosh. That is all that orcs understand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85914, 'enUS', 'That is what I do, Zoggosh. I think good.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85915, 'enUS', 'I\'m not playing these games anymore. Bring in the saberon and we\'ll be done with it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85916, 'enUS', 'Put a muzzle on it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85917, 'enUS', 'I didn\'t mean the cat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85918, 'enUS', 'How dare you march into MY DOCKS and so brazenly set foot on MY SHIP. Nothing will stop the Iron Horde, least of all you.  Enjoy your death, weaklings. Zoggosh, man the machine turret!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85919, 'enUS', 'I don\'t have the 10 timber required to place a work order. Where can I find more timber?', 'I don\'t have the 10 timber required to place a work order. Where can I find more timber?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85920, 'enUS', 'Zoggosh, ready your axe. This ends now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85921, 'enUS', 'So WEAK!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85922, 'enUS', 'Bow before the might of the Iron Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85923, 'enUS', 'In... conceivable....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85924, 'enUS', 'Sir, they\'ve breached the gates! Nok\'gar... He\'s dead!! We should pick up anchor and set sail for Talador right now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85925, 'enUS', 'But sir, this is the last of the gronn that we have! Blackhand will have our hides if we show up empty handed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85926, 'enUS', 'Yessir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85927, 'enUS', 'Sir! They\'re tampering with the Iron Stars!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85928, 'enUS', 'Igniting the cannons, sir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85929, 'enUS', 'Sir, he\'s out of ammunition! I\'m reloading, I\'m reloading!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85930, 'enUS', 'Sir, half of our army has been taken out! Don\'t you think we should--', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85931, 'enUS', 'Ahhh. Good thinking, sir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85932, 'enUS', 'Captain, they\'re almost here! What do we do now?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85933, 'enUS', 'Uhhh... Are you sure you want to let that thing out of its cage? I don\'t think we have fed it since we found it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85934, 'enUS', 'Good idea, sir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85935, 'enUS', 'Oh.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85936, 'enUS', 'Aye aye, sir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85937, 'enUS', 'Yes, captain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85938, 'enUS', 'Koramar, no! I will avenge you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85939, 'enUS', '', 'Right away, captain! It will be my pleasure.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85940, 'enUS', 'Hah. Ditto.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85941, 'enUS', '', 'Ooooh! Is there killin\' ta be done? Lemme at\'em!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85942, 'enUS', 'Rooby Roo looks at you, then runs off!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85943, 'enUS', 'Iron Horde Killed: $1i (+$2i VP)', 'Iron Horde Killed: $1i (+$2i VP)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85944, 'enUS', 'Scout! What do you have to report?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85945, 'enUS', 'Looks like the Iron Horde is making their move. Grunts! Get the peons to shelter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85946, 'enUS', 'To arms! To your posts! We face a worthy foe today...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85947, 'enUS', 'Sergeant! Smoke on the horizon, and war drums in the distance!', 'Sergeant! Smoke on the horizon, and war drums in the distance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85948, 'enUS', 'We\'ll have to face their scouts first.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85949, 'enUS', 'Here they come...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85950, 'enUS', 'Rooby Roo is hungry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85951, 'enUS', '', 'To become an Exarch, I must first pass through a series of trials.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85952, 'enUS', '', 'I am allowed one champion to accompany me along this journey. I would like that person to be you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85953, 'enUS', '', 'I am ready. Come, the Exarch Council awaits.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85954, 'enUS', 'They were just sent to soften us up...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85955, 'enUS', 'Spread out! They\'ll be coming from all angles!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85956, 'enUS', 'Rukhmar sheds her feathers, igniting all Piles of Ash to fight one last time!', 'Rukhmar sheds her feathers, igniting all Piles of Ash to fight one last time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85957, 'enUS', 'We.... we made it...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85958, 'enUS', '', '|TInterface\\Icons\\Ability_Mage_MoltenArmor.blp:20|tArchmage Sol begins casting Fire spells!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85959, 'enUS', '', '|TInterface\\Icons\\Spell_Frost_FrostArmor02.blp:20|tArchmage Sol begins casting Frost spells!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85960, 'enUS', '', '|TInterface\\Icons\\Spell_MageArmor.blp:20|tArchmage Sol begins casting Arcane spells!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85961, 'enUS', 'Rooby Roo looks at you, then slowly saunters off.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85962, 'enUS', 'Rooby Roo barks. There\'s something in there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85963, 'enUS', '', 'Is... is it dead?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85964, 'enUS', 'I was promised... eternal life...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85965, 'enUS', 'We are nearly free of Tanaan\'s grasp. Stay focused now, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85966, 'enUS', '', 'A-a-a-about t-time, $n. I-it\'s f-freezing out here. No wonder we draenei s-s-settled in warmer areas.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85967, 'enUS', 'Let\'s get out of here.', 'Let\'s get out of here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85968, 'enUS', 'I\'m ready to face Everburn.', 'I\'m ready to face Everburn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85969, 'enUS', '', 'Are you ready, hero? It is just up this way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85970, 'enUS', '', 'Steel yourself. This is not the lair of an ordinary beast.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85971, 'enUS', '', 'Come, fury! My courage shall not falter before you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85973, 'enUS', '', 'Well done, champion! Akama\'s task is finished. I cannot wait to see his face when we report on our success.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85974, 'enUS', 'I will tear you and your garrison apart.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85975, 'enUS', 'A soldier\'s duty... is never done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85976, 'enUS', 'Your time... will come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85977, 'enUS', 'You\'re a fool. Just like Taylor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85978, 'enUS', 'I turned the Admiral\'s \"followers\" against him. Perhaps I shall pay your garrison a visit next?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85979, 'enUS', 'The dark one promised me so much... if I succeeded...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85980, 'enUS', '<Rooby Roo looks hungry. Perhaps her owner has something that would help.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85981, 'enUS', 'Your bags have been pickpocketed by |c00FFFF00|Hspell:168798|h[Pick Pocket]|h|r. You\'ve lost 5 |c00FFFFFF|Hcurrency:944|h[Artifact Fragments]|h|r.', 'Your bags have been pickpocketed by |c00FFFF00|Hspell:168798|h[Pick Pocket]|h|r. You\'ve lost 5 |c00FFFFFF|Hcurrency:944|h[Artifact Fragments]|h|r.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85982, 'enUS', 'Were it not for you, this would be Nisha\'s resting place as well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85983, 'enUS', 'Oregorger has gone insane from hunger!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85984, 'enUS', 'Very good! Did you rehearse when I wasn\'t looking?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85985, 'enUS', 'Correct, you are doing fine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85986, 'enUS', 'Fantastic! The power of the ritual is increasing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85987, 'enUS', 'Perfect! Oh, we are so close to success!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85988, 'enUS', 'Come on, Bryan. Snap out of it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85989, 'enUS', 'Snap out of it, mon. Come back to us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85990, 'enUS', '%s does not detect any nearby enemies, and initiates |cFFFF0404|Hspell:168929|h[Cannon Barrage!]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85991, 'enUS', 'It came... in the night...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85992, 'enUS', '', 'This is it. Garrosh Hellscream sits smugly on his throne just beyond these gates.$B$BWe can\'t let him escape this time, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85993, 'enUS', 'Can\'t you see I am trying to fish here?', 'Can\'t you see I am trying to fish here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85994, 'enUS', 'You look like an able fisherman, do you think you can help us out?\n\nWe have established a small garrison nearby, but we are finding it difficult to feed our troops. Any fishing advice you could give us would really help.', 'You look like an able fisherman, do you think you can help us out?\n\nWe have established a small garrison nearby, but we are finding it difficult to feed our troops. Any fishing advice you could give us would really help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85995, 'enUS', 'At this point, we are just waiting for an opening. Once the stream of enemies abates, we make our move.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85996, 'enUS', '<The archmage focuses intently on the portal.>$b$bLook. Their numbers are abating, however briefly. This may be our chance.$b$bAre you prepared to face whatever\'s on the other side of that portal?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85997, 'enUS', 'FOR AZEROTH!', 'FOR AZEROTH!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85998, 'enUS', 'Tread carefully. If the rumors I hear are true, you will find yourself in grave danger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85999, 'enUS', 'You have shown great courage and recovered an invaluable artifact. Lok\'tar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86000, 'enUS', 'I know not what you will find out there, but if you encounter any of the Iron Horde, show them no mercy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86001, 'enUS', 'The heart of the magnaron... the power we now wield against the Iron Horde is immense.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86003, 'enUS', 'Miss Pinchwhistle! The \"Mother Lode,\" it just crashed into the mountains south of here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86004, 'enUS', '', 'What!? They were only supposed to be observing; we need to get a rescue team down there pronto!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86005, 'enUS', '', 'Whoa! What was that?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86006, 'enUS', 'Speak with Ka\'alu when you are ready to fly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86007, 'enUS', 'There\'s timber to be found across all of Draenor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86008, 'enUS', 'There\'s timber all across this world, commander, but I would recommend that you harvest in Gorgrond or Talador.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86009, 'enUS', 'There\'s timber all across this world, commander, but I would recommend that you harvest in the Spires of Arak or Talador.$B$BBoth of those regions are overflowing with timber to chop down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86010, 'enUS', 'Nagrand is the final frontier for harvesting timber, $g sir:ma\'am;.$B$BOf course the rest of Draenor is wide open to your keen harvesting skills.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86011, 'enUS', '', 'I can\'t believe Baros is gone... If you will excuse me commander I really need to focus on my work right now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86012, 'enUS', '', 'Poor Thorn, she\'s so heartbroken. I don\'t think she will ever leave her worgen form again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86013, 'enUS', 'Many leagues of deadly land yet stand between us and the Blackrock.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86014, 'enUS', 'Gorgrond is no place for armies. This land swallows all who enter. We will be fortunate to even lay eyes upon the Iron Horde base.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86015, 'enUS', '', 'We should have brought an army to fight the Iron Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86016, 'enUS', '', 'Life is short, commander. It is important to seize the moment.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86017, 'enUS', '', 'Baros was such a sweet man.$b$bIf only I would have paid more attention to him when he was alive. I\'ll never forgive myself for letting him slip through my fingers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86018, 'enUS', '', 'How can I help you, commander?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86019, 'enUS', '', 'Ha! Your size makes you weak, giant.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86020, 'enUS', 'Tell me, commander, what do you think of Lieutenant Thorn? Whenever she\'s around, I get so flustered.$b$bI\'m just... not really good with women. Which she is an exceptional example of. A woman, that is. Although she is also a worgen. Which I also like. Oh, that long, silky fur...$B$BSigh...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86021, 'enUS', '', 'Commander, I was just wondering. Has Baros mentioned me by any chance? What? Oh... no reason.$b$b<Thorn blushes>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86022, 'enUS', 'Logging is just a hobby. My true calling is singing and dancing. Me and my group, \'N Swing, are going to be rich and famous some day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86023, 'enUS', 'There\'s timber all across this world, commander, but I would recommend that you start your search in Gorgrond.$B$BIf you\'re a risk taker you can try Shadowmoon Valley, but I hear the Alliance controls that region. You might run into some trouble if you\'re not careful.$B$BFrostfire isn\'t so great for trees, but if you\'re desperate you can look around. You might find a few small timber.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86024, 'enUS', 'There\'s timber all across this world, commander, but I would recommend that you start your search in Shadowmoon Valley or Gorgrond.$B$BIf you\'re a risk taker you can try Frostfire Ridge, but I hear the Horde controls that region. You might run into some trouble if you\'re not careful.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86025, 'enUS', 'Fury of Earth, grant these heroes your protection!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86026, 'enUS', 'Fury of Fire, ignite our fists and weapons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86027, 'enUS', 'Fury of Wind, honor these soldiers with your presence!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86028, 'enUS', 'Fury of Water, heal our injured!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86029, 'enUS', 'I thought I was done for. Thank you, hero.', 'I thought I was done for. Thank you, hero.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86030, 'enUS', 'The mist took me by surprise. Thank you for helping me.', 'The mist took me by surprise. Thank you for helping me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86031, 'enUS', 'Thank you, stranger. What brought this mist upon us so suddenly?', 'Thank you, stranger. What brought this mist upon us so suddenly?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86032, 'enUS', 'My gratitude to you, hero. I thought my fate was surely sealed.', 'My gratitude to you, hero. I thought my fate was surely sealed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86033, 'enUS', 'Bless you, stranger. I can find my way back from here.', 'Bless you, stranger. I can find my way back from here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86034, 'enUS', 'The creatures native to this swamp seem unaffected by the poison mist.$B$BThere must be something about the clackers and crabs that make them especially resilient to this strange phenomenon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86035, 'enUS', 'Luckily, we\'ve discovered that the native mushroom species have immense restorative properties.$B$BWhile we search for the cause of the mist, we have been eating the mushrooms to recover our strength and combat the poison.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86036, 'enUS', 'We currently have $9311w collected. We need $9707w total.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86037, 'enUS', 'We have activated the |c00FFFF00|Hspell:178538|h[Mage Portal]|h|r! Good work!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86038, 'enUS', 'The Axe of Serathil compels you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86039, 'enUS', 'The Mage Portal has been destroyed by the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86040, 'enUS', 'Ha! Now I fight... you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86041, 'enUS', 'I am sorry! The axe whispers and I must obey!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86042, 'enUS', 'The |c00FFFF00|Hspell:178537|h[Knight Riders]|h|r are out for duty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86043, 'enUS', 'The |c00FFFF00|Hspell:178537|h[Knight Riders]|h|r have been slain by the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86044, 'enUS', 'My mind is not my own! Serathil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86045, 'enUS', '', 'So how many Alliance have you slain today, $n?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86046, 'enUS', '', 'Tag you\'re it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86047, 'enUS', '', 'Foolish little rodent, you are too late! The Heart of Gorgorek is mine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86048, 'enUS', '', 'Unfortunately for you, my blade hungers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86049, 'enUS', '', 'Race you around!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86050, 'enUS', '', 'Warlord, we have it under control.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86051, 'enUS', 'Race you back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86052, 'enUS', 'I win!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86053, 'enUS', '', 'It... is free. Warlord, we have failed...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86054, 'enUS', '', 'Hey, no fair.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86055, 'enUS', '', 'I don\'t have time for this. Your end is near, hero. The next time we meet, be prepared for death.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86056, 'enUS', '1, 2, 3...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86057, 'enUS', 'Twenty! Ready or not, here I come!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86058, 'enUS', 'Found you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86059, 'enUS', '', 'No peeking.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86060, 'enUS', '', 'Race you back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86061, 'enUS', '', 'Syd, Brae!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86062, 'enUS', '', 'Now, I want you to come home before dark.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86063, 'enUS', '', 'And you stay out of trouble!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86064, 'enUS', '', 'Bye Mom.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86065, 'enUS', 'Yes, mom.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86066, 'enUS', 'Caught anything?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86067, 'enUS', 'Any bites?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86068, 'enUS', 'So, still...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86069, 'enUS', 'Maybe we should move on?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86070, 'enUS', 'Nah.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86071, 'enUS', 'Same answer as the last two times.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86072, 'enUS', 'The Matriarch is safe. You have proven to be a worthy ally; I look forward to fighting alongside you again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86073, 'enUS', 'Haha...haha... AHHAHAHAHA! Didn\'t anyone ever tell you not to play with fire?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86074, 'enUS', 'They took Corporal Thukmar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86080, 'enUS', 'The skies are not out of reach. No, not out of reach.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86081, 'enUS', 'The dusk will rise to the stars.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86082, 'enUS', 'My muzzle\'s never missed its mark! The prize is as good as mine!', 'My muzzle\'s never missed its mark! The prize is as good as mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86083, 'enUS', 'Holy light, did ya hafta get the worst-smelling one of the bunch?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86084, 'enUS', 'Aw, ain\'t his fault he\'s stinky, is it fella?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86085, 'enUS', 'Tommy Joe, last time you took a bath \nwas before the Cataclysm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86086, 'enUS', 'Even in the light, there is shadow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86087, 'enUS', 'Caught yerself a fiesty one here, Commander!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86088, 'enUS', 'Look at him go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86089, 'enUS', 'Oh... So close that time!', 'Oh... So close that time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86090, 'enUS', 'Guess you can\'t make milk without breakin\' a few eggs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86091, 'enUS', 'I wonder how the other competitors are doing.', 'I wonder how the other competitors are doing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86092, 'enUS', 'Folks back at the garrison will appreciate this one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86093, 'enUS', 'I wanna eat it already, how about just a little bite?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86094, 'enUS', 'Another shot, another miss.', 'Another shot, another miss.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86095, 'enUS', 'Tommy Joe, last time you tried that you ended up with a bad case of broken face.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86096, 'enUS', 'I should really work on my aim if I expect to win the competition!', 'I should really work on my aim if I expect to win the competition!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86097, 'enUS', 'Ya know, haulin\' these things back ain\'t a walk in the park.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86098, 'enUS', 'A wolf nearly ate my foot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86099, 'enUS', 'Back in my day, wolves would eat both of our feet, both ways to and from Ironforge, in the snow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86100, 'enUS', 'This one\'s even bigger than the last one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86101, 'enUS', 'Bigger\'n three cows tied together!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86102, 'enUS', 'Yep, sure bigger\'n... wait, what?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86103, 'enUS', 'Come take a look at my newest goods!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86104, 'enUS', 'Come try out the best tools in Draenor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86105, 'enUS', 'Rare items from the fabled Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86106, 'enUS', 'Someday, I will have a greenhouse of my own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86107, 'enUS', 'Just two more years, then I can branch out on my own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86108, 'enUS', 'I am glad Jayciel made it out of Karabor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86109, 'enUS', 'Can I get another Moonshell Claw Bait?', 'Can I get another Moonshell Claw Bait?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86110, 'enUS', 'Tomen? Are you in there?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86111, 'enUS', 'Who\'s that hollering?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86112, 'enUS', 'Berethal? It has been a long time. What brings you back here?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86113, 'enUS', 'I heard about the attacks and came out to help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86114, 'enUS', 'Well it is good to see you. What can I get you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86115, 'enUS', 'Just two packages for now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86116, 'enUS', 'You think yourself a worthy vessel for my power? Prove it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86117, 'enUS', 'Impressive...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86118, 'enUS', '', 'I\'ve been waiting for you. I\'m going to have to cut you now -- and it\'s going to burn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86119, 'enUS', 'Fool, you challenge me? I will boil your insides.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86120, 'enUS', 'So you are the one Azuka spoke of. So puny, I thought this would be a challenge.  Prepare to die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86121, 'enUS', 'Long have my people suffered the Curse of Sethe, and long have we sought a cure.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86122, 'enUS', '', 'Some sort of veil has descended on this outpost. We need to find the cause.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86123, 'enUS', 'Let\'s do some investigating.', 'Let\'s do some investigating.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86124, 'enUS', 'Begin your spell.  I\'ll protect you!', 'Begin your spell.  I\'ll protect you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86125, 'enUS', '', 'So be it. Katsuo, show this \'gladiator\' the might of the Burning Blade.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86126, 'enUS', 'The chest is empty, the heart has been stolen.', 'The chest is empty, the heart has been stolen.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86127, 'enUS', '', 'You are fortunate. The honor of the arena stays my hand.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86128, 'enUS', 'Sorceries, rituals and rites. Unspeakable acts of darkness and desperation, all for naught.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86129, 'enUS', '', 'Who dares to challenge my clan?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86130, 'enUS', 'We now must face the Iron Horde fleet. They have a powerful weapon aboard a battleship just off the coast. I know a spell that can clear the way, but it will take all my focus to channel it. During my incantation, I will be vulnerable. It will be up to you and our allies to protect me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86131, 'enUS', 'Continue your search for Azuka Bladefury.', 'Continue your search for Azuka Bladefury.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86132, 'enUS', 'We found refuge in the shadows, yes, but also shame. We lost our way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86133, 'enUS', '', 'Impossible! Insolent whelp. You dare spit in the face of my clan\'s honor?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86134, 'enUS', '', 'This just became personal. Let me see how you fight firsthand.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86135, 'enUS', 'I led my people astray. You would have been fair to shun them as others do.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86136, 'enUS', 'And yet, you showed them kindness. Curious.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86137, 'enUS', 'Did we import roses from Azeroth to the garrison farm? I don\'t remember.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86138, 'enUS', '', 'I\'ll set up a patrol. We need to know if any more members of the Burning Blade have infiltrated the garrison.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86139, 'enUS', '', 'Baros, you fool... you should have told me you loved me sooner. It didn\'t have to end like this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86140, 'enUS', '', 'This is not over. The Iron Horde... will... pay.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86141, 'enUS', '', 'These mists are quite dangerous. I will try to keep you safe, but use the mushrooms to heal yourself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86142, 'enUS', '', 'There! That creature must be the cause of the poisonous mist!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86143, 'enUS', '', 'Excellent teamwork, hero! We have cured the swamp of its curse. I will go on ahead, so meet me when you are done here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86144, 'enUS', 'I have half a dozen rangari unaccounted for. Please find them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86145, 'enUS', 'Thank you for helping my people, friend. I will be sure to send word of your kindness to Exarch Naielle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86146, 'enUS', 'Mr. Jones! Is that you?', 'Mr. Jones! Is that you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86147, 'enUS', 'You gave them strength. Though the curse may forever weigh upon their bones, I see them standing tall and proud.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86148, 'enUS', 'You have succeeded where I failed. You have set my people free.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86149, 'enUS', 'Truly, you are their Talon King.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86150, 'enUS', 'Truly, you are their Talon Queen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86151, 'enUS', 'Reporting in!', 'Reporting in!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86152, 'enUS', 'You\'re to show me Ashran?', 'You\'re to show me Ashran?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86153, 'enUS', 'Xeri\'tac descends from her web!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86154, 'enUS', '|TInterface\\Icons\\Ability_Racial_Cannibalize.blp:20|tXeri\'tac starts to |cFFFF0000|Hspell:169248|h[Consume]|h|r a Pale Orc!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86155, 'enUS', '|TInterface\\Icons\\spell_festergutgas.blp:20|tA Pale Orc has |cFFFF0000|Hspell:169233|h[Inhaled]|h|r Toxic Gas! Their power will increase the longer they are alive.', '|TInterface\\Icons\\spell_festergutgas.blp:20|tA Pale Orc has |cFFFF0000|Hspell:169233|h[Inhaled]|h|r Toxic Gas! Their power will increase the longer they are alive.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86156, 'enUS', 'So. Thirsty.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86157, 'enUS', 'Returned to the earth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86158, 'enUS', 'Feed my roots.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86159, 'enUS', 'No!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86160, 'enUS', 'I. Cannot. Be. Stopped.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86161, 'enUS', 'Refreshed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86163, 'enUS', '+$i Points from $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86164, 'enUS', 'Come, he has not yet reached his full strength.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86165, 'enUS', 'I will handle Sethe, you take care of the cultists!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86166, 'enUS', 'Behold, my rebirth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86167, 'enUS', 'My blood will blacken the seas!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86168, 'enUS', 'My flesh shall fester until the very sky rots with it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86169, 'enUS', 'My hatred is... undying...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86170, 'enUS', '*sneeze* There\'s something in the air...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86171, 'enUS', 'Through this rocky ground? Impossible. Sound the alarm!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86172, 'enUS', 'To arms! To your posts! And be wary of new wild growth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86173, 'enUS', 'Sergeant, strange plants are growing inside the garrison.', 'Sergeant, strange plants are growing inside the garrison.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86174, 'enUS', 'Apexis crystals are common among the most dangerous parts of Draenor. You will find them immensely useful as you augment your equipment.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86175, 'enUS', 'We\'ll cart this sucker back to base immediately, commander!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86176, 'enUS', 'I think this is the beginning of a very powerful partnership, my friend. Good luck.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86177, 'enUS', 'I brought my favorite rope!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86178, 'enUS', 'Oh no, not Clyde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86179, 'enUS', 'Interestin\' markings on this little guy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86180, 'enUS', 'Aww, look at his little tuft of fur!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86181, 'enUS', 'Now don\'t be touchin\' that, Tommy Joe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86182, 'enUS', '', 'Be careful, champion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86183, 'enUS', 'You create a diversion to the west while I move the rest of us south.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86184, 'enUS', 'Yrel, Maraad, and Duotan need your help on the flagship.  Blackhand will not go down without a fight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86185, 'enUS', 'Who dare challenge Lug\'dol?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86186, 'enUS', 'I snap da neck of anyone who challenge me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86187, 'enUS', 'Nobody can beat Lug\'dol. Lug\'dol da best!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86188, 'enUS', 'Nobody left to fight. Lug\'dol sad.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86189, 'enUS', 'I\'m in da running to be da next blood champion. You dead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86190, 'enUS', 'I\'m gonna be da next blood champion. You die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86191, 'enUS', 'Your head will be my good luck charm to be the next blood champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86192, 'enUS', 'You just another kill towards me becoming da blood champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86193, 'enUS', 'I... lose?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86194, 'enUS', '', 'We have activated the |c00FFFF00|Hspell:178539|h[Mage Portal]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86195, 'enUS', 'It no end like dis...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86196, 'enUS', 'But you so small...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86197, 'enUS', '', 'The |c00FFFF00|Hspell:178539|h[Mage Portal]|h|r has been destroyed by the Alliance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86198, 'enUS', 'Lug\'dol... finished.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86199, 'enUS', 'Enough of dis. Lug\'dol SMASH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86200, 'enUS', 'The |c00FFFF00|Hspell:178536|h[Wolf Riders]|h|r are out for duty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86201, 'enUS', 'The |c00FFFF00|Hspell:178536|h[Wolf Riders]|h|r have been slain by the Alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86202, 'enUS', 'The |c00FFFF00|Hspell:178534|h[Warlock Gateway]|h|r has been activated! Check your map for the locations.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86203, 'enUS', 'The |c00FFFF00|Hspell:178534|h[Warlock Gateway]|h|r has been destroyed by the Alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86204, 'enUS', '|c00FFFF00|Hspell:178359|h[Kronus]|h|r has arisen!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86205, 'enUS', 'You fools! The Alliance has killed |c00FFFF00|Hspell:178359|h[Kronus]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86206, 'enUS', 'We\'ve managed to contain this building. You should be safe from the mist in here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86207, 'enUS', 'The mine entrance is boarded up while work goes on inside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86208, 'enUS', '', 'We shall fight it, together.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86209, 'enUS', '', 'Your size works against you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86210, 'enUS', '', 'I remember your true face.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86211, 'enUS', '', 'Yes! Burn it... sister.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86212, 'enUS', '', 'For the light!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86213, 'enUS', '', 'We do not fear you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86214, 'enUS', '', 'Be at peace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86215, 'enUS', 'Oh yeah, this conversation never happened. Gotta be an unbiased promoter and all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86216, 'enUS', '', 'Watch your flank, sister.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86217, 'enUS', 'Coming right up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86218, 'enUS', 'This is the life. I missed good old Nagrand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86219, 'enUS', 'These will work great. Thank you Tomen, I hope to get back to them soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86220, 'enUS', 'See you at the ring, champ.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86221, 'enUS', 'Here you go. I hope you can get back to your family in Shattrath soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86222, 'enUS', 'Our campaign is gaining momentum. Both the Alliance and Horde have established garrisons on the mainland. But we must be wary of counter-attacks...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86223, 'enUS', 'I\'m ready. Start casting!', 'I\'m ready. Start casting!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86224, 'enUS', 'Invasive Thornblooms Killed: $1i (+$2i VP)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86225, 'enUS', 'Ravager Wasps Killed: $1i (+$2i VP)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86226, 'enUS', 'Podlings Killed: $1i (+$2i VP)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86227, 'enUS', 'Plant Hydras Killed: $1i (+$2i VP)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86228, 'enUS', 'Lashers Killed: $1i (+$2i VP)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86229, 'enUS', 'Gnarled Ancient Killed: $1i (+$2i VP)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86230, 'enUS', 'Botanis Killed: $1i (+$2i VP)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86231, 'enUS', 'Ancients Killed: $1i (+$2i VP)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86232, 'enUS', '', 'I can\'t wait to explore Draenor Percy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86233, 'enUS', 'Roadblock removed!', 'Roadblock removed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86234, 'enUS', '', 'Ooh, another trick!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86235, 'enUS', '', 'That was great!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86236, 'enUS', '', 'So what is he?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86237, 'enUS', '', 'Bark* Bark*', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86238, 'enUS', '', 'Percy is a pug.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86239, 'enUS', '', 'A... what?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86240, 'enUS', '', 'Hmm.. well, a pug is a type of dog we have on Azeroth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86241, 'enUS', '', 'It\'s a shame what happened to his face.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86243, 'enUS', '', 'Ruff*!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86244, 'enUS', '', 'I am Magister Serena. Nice to meet you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86245, 'enUS', 'Commander, our aerial squadron has weakened the magnaron, but we\'re pinned out here. We need your help to assault the main compound.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86246, 'enUS', 'I don\'t like da look a\' dis ting, hero.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86247, 'enUS', 'Is this him?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86248, 'enUS', 'Betrayed by Garrosh the moment they arrived on Draenor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86249, 'enUS', '', 'It is. The final resting place of Kairozdormu.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86250, 'enUS', '', 'Ah, welcome, Vindicator Yrel. I see you have come for Maladaar\'s trial.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86251, 'enUS', '', 'The spirits sense your purpose. They already grow eager to challenge you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86252, 'enUS', '', 'It is my honor to face the heroes of the ancient past. They will not find me lacking.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86253, 'enUS', '', 'Good luck, Lady Yrel.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86254, 'enUS', 'Yrel, this is how I always wished to see you. Do not let me down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86255, 'enUS', '', 'Greetings, teacher. Let me show you what I have learned in my travels.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86256, 'enUS', 'You have exceeded all my hopes, Yrel. You may proceed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86257, 'enUS', '', 'Now, to lay Maraad\'s ashes to rest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86258, 'enUS', 'Yrel... how you have grown since last we met. But... is it enough? Are you worthy of leading our people?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86259, 'enUS', 'Now, it is my turn to test your strength.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86260, 'enUS', 'When you face a more powerful foe, will you stand strong?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86261, 'enUS', '', 'Do not fear, for I will stand!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86262, 'enUS', 'When our people look to you, will you be ready to command?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86263, 'enUS', '', 'I shall not let them down!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86264, 'enUS', 'When all hope is lost, will you... still... fight?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86265, 'enUS', '', 'I shall never yield!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86266, 'enUS', '', 'I... can...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86267, 'enUS', 'When you look inside yourself and see only darkness, can you still find the Light?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86268, 'enUS', 'What was that?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86269, 'enUS', '', 'Because... in this world... we are never...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86270, 'enUS', '', 'Hero! Use the hammer, now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86271, 'enUS', 'Yrel... it is true. The student has surpassed the teacher. You are ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86272, 'enUS', 'If only Prophet Velen could see you now... he would be proud.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86273, 'enUS', 'Rise, Yrel. It is your charge to unite the draenei. Prepare them for what\'s to come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86274, 'enUS', 'Farewell...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86275, 'enUS', '', 'Maraad, my friend... be at peace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86276, 'enUS', '', 'Thank you, hero. I will meet up with you later. I just need a moment to pay my respects to the ancestors.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86277, 'enUS', 'One final trial awaits, Yrel. US!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86278, 'enUS', 'You have chosen your champion. Now, let us begin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86279, 'enUS', 'Ahh, I am defeated.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86280, 'enUS', '', 'Such strength!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86281, 'enUS', 'I submit!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86282, 'enUS', 'Yrel, today, you have completed the trials of the exarch.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86283, 'enUS', 'You have proven courage, kindness, faith, and strength... pillars upon which our society has arisen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86284, 'enUS', 'However, we measure a leader not only by her merit, but also by the character of her companions.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86285, 'enUS', 'Champion, we have watched you throughout this trial.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86286, 'enUS', 'We saw your bravery in Frostfire, your persistence in Nagrand, and your loyalty in Auchindoun.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86287, 'enUS', 'Your dedication to Lady Yrel proves to us that she is worthy. We did not choose the next Exarch. YOU did.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86288, 'enUS', 'Yrel, do not forget that an exceptional leader inspires greatness in others. Trust in your people, and trust in yourself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86289, 'enUS', 'Now, RISE...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86290, 'enUS', 'EXARCH YREL!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86291, 'enUS', 'Good luck to you, Yrel. Courage comes from the mind and heart.', '', 26124);
