INSERT INTO `broadcast_text_locale` VALUES (86320, 'enUS', 'Hah! You nowhere near tough enough.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86321, 'enUS', 'Draenor is not without its own primal beauty.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86322, 'enUS', 'The three of us all bring different talents to the table. Chromie can manipulate time to heal our wounds, and I am capable of bearing the brunt of the dragon\'s attacks. All of us are worthy fighters.$b$bBut YOU are the tactical expert. We\'ll follow your lead. In what role are you strongest? Say the word and we\'ll begin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86323, 'enUS', 'I\'ll deal out the damage.', 'I\'ll deal out the damage.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86324, 'enUS', 'I\'ll heal us.', 'I\'ll heal us.', 26124);
