INSERT INTO `broadcast_text_locale` VALUES (86343, 'enUS', 'Step right up, ladies and gentle-ogres. Witness utter carnage and devastation in the one and only Ring of Blood! In this once in a lifetime battle for the coveted Blood Champion title we have $n from parts unknown versus Katsuo of the Burning Blade!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86344, 'enUS', 'Let the Blood Champion title fight BEGIN!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86345, 'enUS', 'Not even close, baby! What a slaughter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86346, 'enUS', 'Finish him! Oh, wait, you did. All over the arena floor, too. We need a cleanup over here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86347, 'enUS', 'Oh, for the love of... I just had this place cleaned! Can you kill a little bit neater next time?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86348, 'enUS', 'What a one-sided fight! Absolutely unfair. Not even remotely close!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86349, 'enUS', 'Wodin, toss me the scraper. It\'s a mess down here!', '', 26124);
