INSERT INTO `broadcast_text_locale` VALUES (86754, 'enUS', '', 'Goren teeth make an excellent tool for cutting through the thickest armor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86755, 'enUS', '', 'Hello, commander. It has been a pleasure seeing you in action.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86756, 'enUS', 'I was almost back to base with a sack of those artifact fragments when some Horde stole them from me. Watch your back at all times, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86757, 'enUS', 'Search him for the gavel.', 'Search him for the gavel.', 26124);
