INSERT INTO `broadcast_text_locale` VALUES (86760, 'enUS', 'You riding or biting today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86761, 'enUS', 'I got dis $p!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86762, 'enUS', 'By the elements... what has Cho\'gall done?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86763, 'enUS', 'Hold fast, $n! The naaru is transforming into a void god... we have no choice but to stand and fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86764, 'enUS', '', 'Auchindoun is safe... for now. Fan out and find Teron\'gor and Gul\'dan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86765, 'enUS', 'The great furies must be informed of what has transpired here. Quickly, follow me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86766, 'enUS', 'I sense a cool draft on my skin and the smell of earth and leaves... there must be an opening to the outside!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86767, 'enUS', 'The naaru... this cannot be!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86768, 'enUS', 'K\'ure is transforming into a void god... and we are all that stands between it and the destruction of this land!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86769, 'enUS', 'My heart aches over the loss of K\'ure... but there is little time to mourn. We must find a way out of this place. Follow me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86770, 'enUS', '', 'Good, let\'s get in there!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86771, 'enUS', '%s calls for help from its mother.', '', 26124);
