INSERT INTO `broadcast_text_locale` VALUES (86773, 'enUS', '%s chirps excitedly and flutters away.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86774, 'enUS', 'Dis battle is jus\' gettin\' started. Let no Iron Horde survive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86775, 'enUS', 'Commander, our rylak riders have weakened da magnaron, but we\'re pinned outside. We need ya help ta break inta da main compound.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86776, 'enUS', 'Greetings, Commander! You will find today\'s glorious invention waiting for you on the workbench.', 'Greetings, Commander! You will find today\'s glorious invention waiting for you on the workbench.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86777, 'enUS', 'Tell me about how inventions work.', 'Tell me about how inventions work.', 26124);
