INSERT INTO `broadcast_text_locale` VALUES (88069, 'enUS', 'And to think... that could have been me.', 'And to think... that could have been me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (88070, 'enUS', 'A most unfortunate end.', 'A most unfortunate end.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (88071, 'enUS', 'Your sacrifice is a valuable lesson to us all.', 'Your sacrifice is a valuable lesson to us all.', 26124);
