INSERT INTO `broadcast_text_locale` VALUES (88951, 'enUS', 'A fine choice. Blood elves are a blight upon this world and must be destroyed or driven out of Draenor.$B$BThe path before you will be beset on all sides by those that would seek to stop you in your righteous mission. There can be only one outcome: victory or death.$B$BAre you certain about this choice?', '', 26124);
