INSERT INTO `broadcast_text_locale` VALUES (88955, 'enUS', 'Orc...$B$B<Kuros spits.>$B$BYou would do all denizens of this world a great service by ridding these lands of orc.$B$BThe path before you will be beset on all sides by those that would seek to stop you in your righteous mission. There can be only one outcome: victory or death.$B$BAre you certain about this choice?', '', 26124);
