INSERT INTO `broadcast_text_locale` VALUES (89990, 'enUS', 'You now stand upon hallowed ground. In this place, upon the arena floor, the greatest gladiators of Draenor have spilled blood. Most have given up their lives for such a great honor. Today, you will do the same.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (89991, 'enUS', 'You will fight. You will kill. You will salute! 100 LIVES. Live or die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (89992, 'enUS', 'To the last gladiator standing goes honor, glory and riches!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (89993, 'enUS', 'Down goes $n! Dat $r left notin\' but doodoo an blood on da floor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (89994, 'enUS', 'What was dat?! Sometin\' exploded on da floor. I tink it was $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (89995, 'enUS', '$n down! Dat remind me of da time when dat orc got his stones crushed by dat big elekk. Dat was a good week at da circus!', '', 26124);
