INSERT INTO `broadcast_text_locale` VALUES (90001, 'enUS', '', 'Oh, this should be interesting.  You don\'t mind if I take notes, do you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90002, 'enUS', '', 'It\'s easy to see why objects such as these would have been highly prized by their cultures!  Er, sorry about your pets.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90003, 'enUS', '', 'Very interesting results.  Thanks for helping me test them out!', 26124);
