INSERT INTO `broadcast_text_locale` VALUES (90005, 'enUS', '', 'In its native culture, that pet might have been revered as a martyr.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90006, 'enUS', '', 'Huh, I kinda thought that might happen.', 26124);
