INSERT INTO `broadcast_text_locale` VALUES (90009, 'enUS', '', 'All in the name of science.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90010, 'enUS', 'You will feed my roots.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90011, 'enUS', '', 'I\'ve got bandages in my field kit if you need some...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90012, 'enUS', '', 'You have to respect the ingenuity of such an ancient device.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90013, 'enUS', 'The plants will feast on you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90014, 'enUS', '', 'I\'ll add a special thanks to your pet when I publish this research.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90015, 'enUS', '', 'Oh, you must mean my father, Belloc.  What he and the Reliquary do can hardly be called \'archaeology\'.  Grave robbing is more like it.$b$bTrue archaeology is the search for fact, not treasure.  I want to learn about the past, study where we came from, and rediscover lost knowledge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90016, 'enUS', 'I may fall... but the Overgrowth will take you eventually...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90017, 'enUS', 'Brightblade?  That name sounds familiar...', 'Brightblade?  That name sounds familiar...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90018, 'enUS', '', 'You don\'t look like you\'re very good at pet battles.  Maybe you should come back when you\'re better.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90019, 'enUS', '', 'Here we go!', 26124);
