INSERT INTO `broadcast_text_locale` VALUES (90104, 'enUS', 'The |cFFE86B17|Hspell:175519|h[Essence of Flame]|h|r is knocked from your hands!', 'The |cFFE86B17|Hspell:175519|h[Essence of Flame]|h|r is knocked from your hands!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90105, 'enUS', 'The darkness... it flows within me. It seeps from every vein.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90106, 'enUS', 'Come, strangers! Cross into the realm of the Shadow Lands! I shall deliver you to your death.', '', 26124);
