INSERT INTO `broadcast_text_locale` VALUES (90111, 'enUS', 'Feel the void\'s cold embrace.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90112, 'enUS', 'My power... Will ripple... Across eternity.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90113, 'enUS', 'Your soul shall serve me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90114, 'enUS', 'You bend to my will!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90115, 'enUS', '', 'Rivers of the damned, RISE! RISE in the name of darkness, RISE in the name of Ner\'zhul! The Shadowmoon beckons...', 26124);
