INSERT INTO `broadcast_text_locale` VALUES (90117, 'enUS', '', 'For Ner\'zhul...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90118, 'enUS', '', 'Mmm, yes. The PERFECT sacrifice.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90119, 'enUS', '', 'Your souls will belong to ME.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90120, 'enUS', '', 'Restless spirits, HEED my command!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90121, 'enUS', '', 'Another spirit for the army!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90122, 'enUS', '', 'Embrace the eternal darkness...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90123, 'enUS', '', 'The dead... Shall rise again...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90124, 'enUS', 'Let\'s do this!', 'Let\'s do this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90125, 'enUS', 'Rekindle the |cFFE86B17|Hspell:175519|h[Essence of Flame]|h|r at a Kindling Flame!', 'Rekindle the |cFFE86B17|Hspell:175519|h[Essence of Flame]|h|r at a Kindling Flame!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90126, 'enUS', 'Thank you, $n. May the Furies of Frostfire burn within you!', '', 26124);
