INSERT INTO `broadcast_text_locale` VALUES (90128, 'enUS', '$n, I will remember your help. These wounds won\'t stop a good fight!', '$n, I will remember your help. These wounds won\'t stop a good fight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90129, 'enUS', 'That\'s more like it. I\'m injured, but you\'ve given me a chance to redeem myself.', 'That\'s more like it. I\'m injured, but you\'ve given me a chance to redeem myself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90130, 'enUS', 'By Sha\'tar, thanks for your aid. I\'m wounded, but you\'ve given me a chance to fight on!', 'By Sha\'tar, thanks for your aid. I\'m wounded, but you\'ve given me a chance to fight on!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90131, 'enUS', '$n, I will remember your name. These wounds are no greater than my anger - let us fight on!', '$n, I will remember your name. These wounds are no greater than my anger - let us fight on!', 26124);
