INSERT INTO `broadcast_text_locale` VALUES (90135, 'enUS', 'My wounds are too great, friend. I must leave, but I hope we meet again.', 'My wounds are too great, friend. I must leave, but I hope we meet again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90136, 'enUS', 'I need another drink.', '', 26124);
