INSERT INTO `broadcast_text_locale` VALUES (90172, 'enUS', 'You look like you could use an updated wardrobe.  \n\nLuckily for you, I may just be able to teach you how to make some of the finest tailored garments in Draenor.\n\nCare to take a look at my patterns?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90173, 'enUS', 'The draenei crystalsmiths closely guard the secrets to harnessing the latent power of the gems of Draenor. \n\nI, however, have managed to access these secrets and can share them with a talented jewelcrafter.\n\nFor a price, of course.', '', 26124);
