INSERT INTO `broadcast_text_locale` VALUES (90340, 'enUS', '', 'If this artifact that Harrison is seeking is as powerful as he seems to think, I hope it remains undiscovered.\n\nRegardless of intentions, the wielding of great power often comes with drastic consequences.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90341, 'enUS', 'But hey, the Alliance aren\'t all bad. If you want a linguistic adventure, go drinking with a dwarf!', '', 26124);
