INSERT INTO `broadcast_text_locale` VALUES (91261, 'enUS', 'This... this is true pain...', 'This... this is true pain...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91262, 'enUS', 'You will not leave here alive.', 'You will not leave here alive.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91263, 'enUS', 'I have never felt... more alive...', 'I have never felt... more alive...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91264, 'enUS', 'Put me on a hook...', 'Put me on a hook...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91265, 'enUS', 'This one is mine!', 'This one is mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91266, 'enUS', 'Just like in the arena!', 'Just like in the arena!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91267, 'enUS', 'I\'ll bleed you good!', 'I\'ll bleed you good!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91268, 'enUS', 'You must enjoy pain.', 'You must enjoy pain.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91269, 'enUS', 'Die, you little runt!', 'Die, you little runt!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91270, 'enUS', '', 'Taste the fury!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91271, 'enUS', '', 'Small in stature, grand in talent!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91272, 'enUS', '', 'You bore me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91273, 'enUS', 'My power is unlimited!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91274, 'enUS', 'Bwahahahaha!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91275, 'enUS', 'B-b-b-b-bladestorm!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91276, 'enUS', 'Not in my house!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91277, 'enUS', '', 'Don\'t bother trying to duel Zurge, he\'s such a sore loser.$B$BDon\'t tell him I said that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91278, 'enUS', 'Orders are orders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91279, 'enUS', 'If only Max were here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91280, 'enUS', 'I told Mare Wildrunner I\'d go explore any part of Ashran other than that Altar area. Ogres I can deal with, but ghost ones? Sorry but it gives me the willies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91281, 'enUS', 'I don\'t know how you can stand talking to that animal Zurge. He\'s so uncivil, even for an Orc.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91282, 'enUS', 'You\'re dead weight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91283, 'enUS', 'As for you, well you\'re easy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91284, 'enUS', 'I was carrying a sack full of the artifact fragments back to Warspear when some Alliance scum took them from me. Watch your back out there, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91285, 'enUS', 'You slow me down, I\'m gonna drop you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91286, 'enUS', 'Me lightning!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91287, 'enUS', 'You no lightning! You thunder! A force of nature and energy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91288, 'enUS', 'Oh! Yes. Me thunder, naturally.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91289, 'enUS', 'Yes, it comes naturally to you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91290, 'enUS', 'But you lightning, we come together... sparks fly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91291, 'enUS', 'KIIILLLLLLLL!!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91292, 'enUS', 'DIIIIIEEEEE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91293, 'enUS', 'DEAAATTTH!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91294, 'enUS', 'Test, failed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91295, 'enUS', 'Your skills are not up to par.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91296, 'enUS', 'I consider your effort, incomplete.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91297, 'enUS', 'You are nothing to me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91298, 'enUS', 'You have failed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91299, 'enUS', 'Drown in shadow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91300, 'enUS', 'Greetings, commander. Some willing recruits may still wish to join our garrison. I will gather the contracts for you, so that you may peruse them at your leisure.', 'Greetings, commander. Some willing recruits may still wish to join our garrison. I will gather the contracts for you, so that you may peruse them at your leisure.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91301, 'enUS', 'I don\'t have time to chat. Grand Marshal Tremblade is in desperate need of tanks to help in the battle against the Horde.$B$BWhere did I put that Arclight Spanner...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91302, 'enUS', '', 'If you see any Horde out there, use some of Bixxie Rocketsocket\'s relief flares. Alliance in the area are sure to come assist you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91303, 'enUS', 'We have a number of mounts that still need to be trained. They aren\'t as hardy as your mounts, though, and will tire out after a few battles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91304, 'enUS', 'Do you have any mounts left to train?', 'Do you have any mounts left to train?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91305, 'enUS', 'I\'d like the talbuk-in-training.', 'I\'d like the talbuk-in-training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91306, 'enUS', 'I\'d like the clefthoof-in-training.', 'I\'d like the clefthoof-in-training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91307, 'enUS', 'I\'d like the boar-in-training.', 'I\'d like the boar-in-training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91308, 'enUS', 'I\'d like the elekk-in-training.', 'I\'d like the elekk-in-training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91309, 'enUS', 'I\'d like the wolf-in-training.', 'I\'d like the wolf-in-training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91310, 'enUS', 'I\'d like the riverbeast-in-training.', 'I\'d like the riverbeast-in-training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91311, 'enUS', '', 'Harrison Jones is convinced the Artifact may lie within the mines. If that\'s true, the next stage of this battle will be fought underground.$B$BWhat new dangers await us in there? That\'s what I need to find out.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91312, 'enUS', '', 'While the battle against the Horde indeed lies within what we\'re calling the Road of Glory, we must also focus our efforts at trying to find the Artifact. Who knows where it may be.$B$BLeave no stone unturned. We must find the Artifact before the Horde.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91313, 'enUS', 'How are we supposed to win this battle against the Horde without siege engines?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91314, 'enUS', 'If only we had an airship. We could take out Warspear from the skies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91315, 'enUS', 'We must focus all of our efforts into the mines. I agree with Dr. Jones, the Artifact has to be there. It has to be!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91316, 'enUS', 'It is as we feared, we must venture into the mines. Who knows what ancient dangers are within there...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91317, 'enUS', 'Truce? With the Alliance? Never.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91318, 'enUS', 'Rogue spies have informed us that the Alliance are building machinery used to drill through rocks. Do you think its for the mines?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91319, 'enUS', 'Have you seen how many Death Knights are within our ranks? Why is it that we still can\'t penetrate Stormshield? Are they not using Necrotic Plague?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91320, 'enUS', 'If only we were able to bring a Zeppelin through the portal. We could take out Stormshield from the skies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91321, 'enUS', 'Your suffering will be legendary.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91322, 'enUS', 'Arise, my minions! Rend their flesh.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91323, 'enUS', 'VERMIN! I WILL TURN YOUR WORLD TO ASH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91324, 'enUS', 'Orgrim\'s warpath stops here. I will see to it that my old friends meets the fate he has earned.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91325, 'enUS', 'Shattrath will not fall again. Let us go put these Iron Horde dogs in the ground.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91326, 'enUS', '', 'Everything Maraad did... he did to protect our people.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91327, 'enUS', '', 'He paid the ultimate price for our freedom. We can never forget his sacrifice.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91328, 'enUS', '', 'Thank you for everything you did, $n. Without you... none of us would have survived.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91329, 'enUS', 'Orgrim did what he thought was right. He did not know the path he was following.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91330, 'enUS', 'I will never forget you... old friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91331, 'enUS', 'I won\'t forget your help, $n. Without your aid, we all would have fallen to Blackhand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91332, 'enUS', 'Thank you! We could never have defeated the golem alone.', 'Thank you! We could never have defeated the golem alone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91333, 'enUS', 'What can I do fer ya?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91334, 'enUS', 'You not worthy of ring!', 'You not worthy of ring!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91335, 'enUS', 'I crush you!', 'I crush you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91336, 'enUS', 'Ha! You no gladiator!', 'Ha! You no gladiator!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91337, 'enUS', 'You came to wrong place, runt!', 'You came to wrong place, runt!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91338, 'enUS', 'Me smash you!', 'Me smash you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91339, 'enUS', 'You die in ring...', 'You die in ring...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91340, 'enUS', 'I strong... you stronger...', 'I strong... you stronger...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91341, 'enUS', 'Me... smash... you...', 'Me... smash... you...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91342, 'enUS', 'For Auchindoun!', 'For Auchindoun!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91343, 'enUS', 'Light grant us strength!', 'Light grant us strength!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91344, 'enUS', 'Spirits watch over us!', 'Spirits watch over us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91345, 'enUS', 'We will stand against the darkness.', 'We will stand against the darkness.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91346, 'enUS', 'Forward march. For Auchindoun!', 'Forward march. For Auchindoun!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91347, 'enUS', 'The Light... fades...', 'The Light... fades...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91348, 'enUS', 'Auchindoun welcome me...', 'Auchindoun welcome me...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91349, 'enUS', 'The darkness comes for us all...', 'The darkness comes for us all...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91350, 'enUS', 'Want to see a magic trick?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91352, 'enUS', 'This ought to be entertaining.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91353, 'enUS', 'I hope you\'re wearing fire resistant armor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91354, 'enUS', 'You might feel a slight burning sensation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91355, 'enUS', 'I did try to warn you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91356, 'enUS', 'Is it warm out here, or is it just me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91357, 'enUS', 'Now, now, don\'t get all burned out on me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91358, 'enUS', 'It was inevitable, really.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91359, 'enUS', 'Pleasure to see you, commander.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91360, 'enUS', 'Any spells you need cast, commander? Portals you need opened? Diabolical schemes you need carried out?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91361, 'enUS', 'Lovely time for a stroll around the garrison, commander.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91362, 'enUS', 'Honored as always, commander.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91363, 'enUS', 'Not... cool...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91364, 'enUS', 'I\'ve been snuffed out...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91365, 'enUS', '', 'I won\'t go easy on you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91366, 'enUS', '', 'You want to play rough, huh?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91367, 'enUS', '', 'Freeze!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91368, 'enUS', '', 'I\'ve spotted one. Over here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91369, 'enUS', '', 'You got what you deserved.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91370, 'enUS', '', 'You aren\'t so tough.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91371, 'enUS', '', 'Maybe you\'ll think twice next time! Wait....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91372, 'enUS', '', 'I did it! I got one!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91373, 'enUS', '', 'Hey, commander! Got any missions I could go on? I\'m ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91374, 'enUS', '', 'Nice to see you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91375, 'enUS', '', 'Anything I can do for you, commander?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91376, 'enUS', '', 'Commander! It\'s an honor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91377, 'enUS', '', 'Is it warm in here? Or is it just me...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91378, 'enUS', '', 'I... tried my best...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91379, 'enUS', '', 'I\'m sorry commander...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91380, 'enUS', '(Beast Mastery) Show Gara the Shadowberries.', '(Beast Mastery) Show Gara the Shadowberries.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91381, 'enUS', '<Gara sniffs the berries, then looks at you expectedly.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91382, 'enUS', 'My soul... feels at peace... thank you.', 'My soul... feels at peace... thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91383, 'enUS', '(Beast Mastery) I am not with the Shadowmoon, and I want to help this wolf!', '(Beast Mastery) I am not with the Shadowmoon, and I want to help this wolf!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91384, 'enUS', 'Gara was my wolf, back before we forsook shamanism for these... dark magics. She wouldn\'t even look at me, not after I was tainted by the void.', 'Gara was my wolf, back before we forsook shamanism for these... dark magics. She wouldn\'t even look at me, not after I was tainted by the void.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91385, 'enUS', 'How dare you set her soul at rest. Om\'ra\'s soul belongs to the VOID!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91386, 'enUS', 'I shall take this one as compensation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91387, 'enUS', 'Your soul will fill the void!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91388, 'enUS', 'You can never escape from the void! Your souls will be devoured!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91389, 'enUS', 'She is being swallowed by the void! You must help her! There is only one way, tame her, now! Maybe you can find a way to reverse the process, and put her soul at rest, as you have done mine!', 'She is being swallowed by the void! You must help her! There is only one way, tame her, now! Maybe you can find a way to reverse the process, and put her soul at rest, as you have done mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91390, 'enUS', 'You\'ve done it... thank you. Goodbye, Gara, my soul is at peace now. I hope... that you find this peace some day, too. I am so sorry, my dearest Gara.', 'You\'ve done it... thank you. Goodbye, Gara, my soul is at peace now. I hope... that you find this peace some day, too. I am so sorry, my dearest Gara.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91391, 'enUS', 'You instinctively use Deterrance to avoid the attack!', 'You instinctively use Deterrance to avoid the attack!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91392, 'enUS', 'Your skill at tracking allows you to remain in this realm!', 'Your skill at tracking allows you to remain in this realm!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91393, 'enUS', 'You are pulled into the Void Realm!', 'You are pulled into the Void Realm!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91394, 'enUS', 'I will consume all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91395, 'enUS', 'The fight against the Warsong clan is honorable, but I don\'t think Hemet has taken very well to the change of priorities here in Nagrand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91396, 'enUS', 'Fool! You dare challenge the might of the Spiteleaves? Die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91397, 'enUS', 'Winter... has... come for me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91398, 'enUS', 'The master... will burn your soul!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91399, 'enUS', 'The hour is near... soon you will die...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91400, 'enUS', 'Our master will devour your soul...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91401, 'enUS', 'You cannot stop the destruction...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91402, 'enUS', 'Noooo... it is you who should die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91403, 'enUS', 'No! No... nooo... the darkness...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91404, 'enUS', 'The void is coming... it will destroy you all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91405, 'enUS', 'You fool... we\'ve already... won...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91406, 'enUS', 'Hey... what were you expecting? Frostmourne or something?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91407, 'enUS', 'Cut down to size.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91408, 'enUS', 'And not a hair out of place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91409, 'enUS', 'That\'s why I\'m here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91410, 'enUS', '', 'You are not welcome here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91411, 'enUS', '', 'I am unimpressed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91412, 'enUS', '', 'You had no chance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91413, 'enUS', 'I\'d like to go back to the nest.', 'I\'d like to go back to the nest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91414, 'enUS', 'Grrrawr I thirst for blood!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91415, 'enUS', 'Well met.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91416, 'enUS', 'Whoops! My bad.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91417, 'enUS', 'Globalized!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91418, 'enUS', 'Making plays!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91419, 'enUS', 'I would like to place a work order for leather.', 'I would like to place a work order for leather.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91420, 'enUS', 'I would like to place a large work order for leather.', 'I would like to place a large work order for leather.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91421, 'enUS', 'I would like to place a work order for meat.', 'I would like to place a work order for meat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91422, 'enUS', 'I would like to place a large work order for meat.', 'I would like to place a large work order for meat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91423, 'enUS', 'I would like to place a work order for fur.', 'I would like to place a work order for fur.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91424, 'enUS', 'I would like to place a large work order for fur.', 'I would like to place a large work order for fur.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91425, 'enUS', '<The good doctor reeks like the floor of tavern and wears a face like five miles of bad road. His color is washed out and he\'s making a deliberate effort not to be sick all over your feet.>$b$bHey Com-om-mander! <Urp> S\'was one HELLUVA bender at Pinchwhistle last night. But I got home all-right for ready! Duty! Yessir. <Belch> Man you really did a number on this garrison since yessserday. With um. Spikes. Furs. And... snow? Did you do that?  I\'mma sit down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91426, 'enUS', 'Jeron Emberfall has been slain by the Alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91427, 'enUS', 'Rylai Crestfall has been slain by the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91428, 'enUS', 'Do you need an analysis? I can have it ready for you by the end of the week.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91429, 'enUS', 'Once we\'re done here, whaddaya say we go to shore and grab a drink?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91430, 'enUS', 'If you find yourself within the Dark Woods, keep an eye out for the blooming Song Flower. They have powerful magical properties.$B$BYou may have to fight off a few of the Arakkoa for them though.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91431, 'enUS', 'Tell me about the mounts-in-training.', 'Tell me about the mounts-in-training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91432, 'enUS', 'The Night Elves are attempting to resurrect an Ancient to be used against us using the power of the Artifacts found here in Ashran.$B$BIf you see any alliance out there, take any fragments they carry by any force necessary.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91433, 'enUS', 'We\'ve been following your lead, and capturing mounts to train up for the rest of the garrison. You\'re welcome to take one out for the day, just ask and I\'ll hand you the whistle.$B$BKeep in mind that they\'re not as well-trained as your own mount, and will tire out after only a few battles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91434, 'enUS', 'We have been following your lead, and capturing mounts to train for use in the garrison. Of course, you are welcome to take one out for the day, simply tell me which whistle you would prefer.$B$BBe aware that they are not as well-trained as your own mount, and will tire out after only a few battles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91435, 'enUS', '', 'The flames coming out of the braziers near the Ring of Conquest may look ordinary at first glance. However, I believe they have been enchanted by ancient Highmaul magic.$B$BI was strangely drawn to them. Touching one, I was suddenly filled with magical energy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91436, 'enUS', '', 'Protect our Priests in the Road of Glory if you see them under attack.$B$BI\'ve trained them myself, their healing power is unmatched this side of the Dark Portal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91437, 'enUS', 'I am SUPER excited to be here today!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91438, 'enUS', '', 'Who goes there? Keep your hands and weapons where I can see them.', 26124);
