INSERT INTO `broadcast_text_locale` VALUES (91448, 'enUS', 'You\'ve got quite the mouth on you, Ooka. Maybe I should stuff it full of your blookin mank chow.', 'You\'ve got quite the mouth on you, Ooka. Maybe I should stuff it full of your blookin mank chow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91449, 'enUS', 'Ah, visitors. It\'s been a long time since Mardum, Allari.', '', 26124);
