INSERT INTO `broadcast_text_locale` VALUES (92130, 'enUS', 'Shadowmoon Reavers Killed: $1i (+$2i VP)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (92131, 'enUS', 'I\'m out of banners, commander! I shall take my leave.', '', 26124);
