INSERT INTO `broadcast_text_locale` VALUES (92930, 'enUS', 'You\'ll have to hold on a minute, pal. I can\'t start the next fight until this one\'s finished.$B$BI may be a profiteering heartless fight promoter leading countless innocents to their untimely deaths, but I have a conscience for coin\'s sake.', '', 26124);
