INSERT INTO `broadcast_text_locale` VALUES (93036, 'enUS', '', 'Give me that orb, Khadgar. I will destroy that hideous thing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (93037, 'enUS', '', 'Haven\'t you given up already, mage?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (93038, 'enUS', '', '%s gasps in pain.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (93039, 'enUS', '', 'Gul\'dan... he\'s not in my head anymore...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (93040, 'enUS', '', 'I owe everything to you! To both of you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (93041, 'enUS', '', 'I will do whatever it takes to stop Gul\'dan. Wherever you lead, I will follow... commander!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (93042, 'enUS', 'Let\'s try this again. For Garona!', 'Let\'s try this again. For Garona!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (93043, 'enUS', '', 'Lookin\' to learn something?', 26124);
