INSERT INTO `broadcast_text_locale` VALUES (93858, 'enUS', '', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ENCOUNTERJOURNAL\\UI-EJ-BOSS-COMMANDER SARANNIS.blp:50:80|t$b[Lady Liadrin] Champion, move ahead and find Tirion. We will cover your advance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (93859, 'enUS', '', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ENCOUNTERJOURNAL\\UI-EJ-BOSS-COMMANDER SARANNIS.blp:50:80|t$b[Lady Liadrin] The Ashbringer is somewhere in the crevice ahead - we need to hold these demons off long enough for someone to make it through their lines!', 26124);
