INSERT INTO `broadcast_text_locale` VALUES (94033, 'enUS', '|TInterface\\Icons\\spell_fel_elementaldevastation.blp:20|t$n\'s |cFFFF0000|Hspell:182008|h[Latent Energy]|h|r reacts violently as they are struck by a |cFFFF0000|Hspell:181498|h[Wake of Destruction]|h|r!', '|TInterface\\Icons\\spell_fel_elementaldevastation.blp:20|t$n\'s |cFFFF0000|Hspell:182008|h[Latent Energy]|h|r reacts violently as they are struck by a |cFFFF0000|Hspell:181498|h[Wake of Destruction]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (94034, 'enUS', 'This is ours now, back to the sea with you!', '', 26124);
