INSERT INTO `broadcast_text_locale` VALUES (94114, 'enUS', '|TInterface\\Icons\\spell_arcane_teleportdalarancrater.blp:20|tWith no one to absorb their |cFFFF0000|Hspell:182427|h[Impact]|h|r, $n perishes.', '', 26124);
