INSERT INTO `broadcast_text_locale` VALUES (95263, 'enUS', '|TInterface\\Icons\\spell_nature_mirrorimage.blp:20|t %s begins to duplicate into |cFFFF0404|Hspell:183734|h[Mirror Images]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95264, 'enUS', '|TInterface\\Icons\\ability_foundryraid_demolition.blp:20|t %s crashes around using |cFFFF0404|Hspell:184366|h[Demolishing Leap]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95269, 'enUS', 'Leave me! My research must continue undisturbed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95270, 'enUS', 'This new job is m-much better... no m-mistress to boss me around.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95271, 'enUS', 'M-more cleaning to be d-done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95272, 'enUS', 'Oh, I m-missed a spot...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95273, 'enUS', 'Ah, m-more dirt. It\'s a m-mirror into my soul.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95274, 'enUS', 'The satyrs will pay for this!', 'The satyrs will pay for this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95275, 'enUS', 'I thought I was done for!', 'I thought I was done for!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95276, 'enUS', 'You have my thanks!', 'You have my thanks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95277, 'enUS', 'The satyrs... they came out of nowhere!', 'The satyrs... they came out of nowhere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95278, 'enUS', 'I would be dead were it not for your aid!', 'I would be dead were it not for your aid!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95279, 'enUS', 'Please... Val\'sharah must be saved!', 'Please... Val\'sharah must be saved!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95280, 'enUS', 'That was only the beginning! Line \'em up, boys!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95281, 'enUS', 'I\'m gonna gather the troops! Hold out as long as you can!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95282, 'enUS', 'Another wave incoming!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95283, 'enUS', 'Nice weapon you have there, $c. Speak with Jace and Allari if you\'d like to help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95284, 'enUS', 'Make ready, hero. We shall soon stand in the presence of the Lord of the Forest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95285, 'enUS', '', 'You have my thanks, stranger. It is good to have found aid in such dire circumstances.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95286, 'enUS', 'Nightmane sent me to find you. Can you walk?', 'Nightmane sent me to find you. Can you walk?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95287, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95288, 'enUS', 'If you make it past the beast seek Baldir. Through Baldir you may speak with Helira. Goodluck, $c.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95289, 'enUS', '', 'You are too late... The Dark Lady is already gone...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95290, 'enUS', '<The small creature shivers in fear. It appears too distraught to speak with you.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95291, 'enUS', 'Queen Ashildir is on the Eternal Battlefield. I suggest you move quickly before Helira changes her mind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95292, 'enUS', '', 'We\'ll tend to the wounded up here. Go quickly. Wake the Archdruid.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95293, 'enUS', '<The drake fixes you with a serpentine stare.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95294, 'enUS', 'Advance. Destroy the Illidari. Take Azsuna.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95295, 'enUS', 'We are the LEGION. You cannot prevail.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95296, 'enUS', 'Too late, \"heroes\". Your world is lost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95297, 'enUS', 'Grovel on your knees. Beg for your lives!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95298, 'enUS', 'You and your world will soon perish.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95299, 'enUS', 'Your titan gods have abandoned you. Lay down your arms. There is no hope left.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95300, 'enUS', 'That weapon will not save you. How can such a tiny thing prevail against the might of the Burning Legion?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95301, 'enUS', 'Salt the earth. Boil their seas. Drive them before you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95302, 'enUS', 'You should have joined us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95303, 'enUS', 'Back to the Twisting Nether...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95304, 'enUS', 'The console displays a number 1.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95305, 'enUS', 'Enough, parasites! You deal with me now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95306, 'enUS', 'I should be on the battlefield with the others.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95307, 'enUS', 'As soon as I get free I\'m going to kill the first kvaldir I see.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95308, 'enUS', 'I am worthy. I know I\'m worthy...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95309, 'enUS', 'Be ready champion, I know not what lies beyond this barrier.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95310, 'enUS', 'Almost there...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95311, 'enUS', 'It is done. Go. Stop Gul\'dan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95312, 'enUS', 'Guarm senses something.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95313, 'enUS', 'Be blessed, young one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95314, 'enUS', 'Ashildir is here among us? Soon we will be free.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95315, 'enUS', 'You have brought hope. Helira will not hold me here any longer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95316, 'enUS', 'Bloodbeard will feel my wrath.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95317, 'enUS', 'My queen has returned. The mystics will pay for their deception.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95318, 'enUS', 'I... I can think clearly again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95319, 'enUS', 'The goddess Helira has spoken. You will not defeat me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95320, 'enUS', 'No.... impossible. Helira, spare me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95321, 'enUS', '', 'Bloodbeard you have failed me. Your punishment will be swift.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95322, 'enUS', '', 'You are free to go champion. Leave now before I change my mind.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95323, 'enUS', 'Thank you, traveler. All is not yet lost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95324, 'enUS', 'Elothir be with you, friend of the grove.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95325, 'enUS', 'Here they come!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95326, 'enUS', 'Destroying this barrier will take a great deal of my strength. I fear I may not be able to aid you after it is broken.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95328, 'enUS', 'Begin the ritual please.', 'Begin the ritual please.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95331, 'enUS', '', 'Prepare yourself. Gul\'dan is at his most powerful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95332, 'enUS', '', 'In the name of the prophet. In the name of the light. This place shall be cleansed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95333, 'enUS', '', 'Ugh...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95334, 'enUS', '', 'There... Go! Stop Gul\'dan!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95335, 'enUS', '', 'I... survived the fall.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95336, 'enUS', '', 'Swiftflight! Where is Swiftflight!?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95337, 'enUS', 'Kill!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95338, 'enUS', 'Your soul is complete once more. Helira\'s hold on you is slipping away.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95339, 'enUS', 'Destroy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95340, 'enUS', 'Smash!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95341, 'enUS', 'Drown!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95342, 'enUS', 'Burn to Ashes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95343, 'enUS', 'Crushed by earth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95344, 'enUS', 'None Escape!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95345, 'enUS', 'Destruction!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95346, 'enUS', 'Weak!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95347, 'enUS', 'Break!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95348, 'enUS', 'Shatter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95349, 'enUS', 'Ruin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95350, 'enUS', 'Fragile!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95351, 'enUS', 'Broken!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95352, 'enUS', 'I... am... unmade...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95353, 'enUS', '', 'Oh no! What did they do to you!?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95354, 'enUS', 'A Sargerei Dominator protects Socrethar with |cFFFF0000|Hspell:184053|h[Fel Barrier]|h|r!', 'A Sargerei Dominator protects Socrethar with |cFFFF0000|Hspell:184053|h[Fel Barrier]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95359, 'enUS', '', 'We are free! Until we meet again, champion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95360, 'enUS', 'Four hours up in the air... It\'s time for a rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95361, 'enUS', 'You hit \'em hard, sir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95362, 'enUS', 'I\'m sure the new pilots coming in will do great!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95363, 'enUS', '', 'Legionnaires, move out! Storm those ships and find Sylvanas!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95364, 'enUS', 'I only did what I thought was right.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95365, 'enUS', 'If we do not act quickly against the satyrs, the vale will be lost. We have little time...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95366, 'enUS', 'We must fight our way deeper into the corruption if we are to strike at the heart of this threat!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95367, 'enUS', 'The leader of the satyrs was seen near Saberfang Cavern. I shall go on ahead to confront him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95368, 'enUS', 'When you have completed your mission, meet me at Saberfang Cavern! This corruption ends now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95369, 'enUS', '', '%s |cFFFF0404|Hspell:184476|h[Reaps]|h|r all marked foes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95370, 'enUS', '$p, the Skyfire is being overrun. They need you up there!', '$p, the Skyfire is being overrun. They need you up there!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95371, 'enUS', 'It looks as if woodland sprites have slain this little bear\'s family.$b$bVisibly trembling, the cub looks up at you with desperate eyes.$b$bYou hear rustling in the woods nearby: the forest sprites are coming back.$b$bThe baby bear won\'t survive on its own. Are you willing to rescue it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95372, 'enUS', 'The power of the totem is mine. You won\'t take it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95373, 'enUS', 'Faster, swine! Move faster! You serve Sargeras now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95374, 'enUS', 'Take everything for the Legion! Leave no spoils behind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95375, 'enUS', '', 'I\'ll need some time to look at this, $glad:lass;. I may need to consult with some of my... contemporaries, as well. Come back in a few and I\'ll tell you what I\'ve learned.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95376, 'enUS', '', 'Glad you made it, champion! Better prepare for a bit of action. The Legion seems to have found a power source located in Un\'Goro. We must stop them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95377, 'enUS', '', 'Wait...! Wait for us...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95378, 'enUS', '', 'They hurt our friend Lixi over there. It\'s time for some payback!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95379, 'enUS', '', 'Hey, Porsha! Get a move on it, sister!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95380, 'enUS', 'You... *cough* filthy swine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95381, 'enUS', '', 'Thank you - all of you. Everyone get ready. This isn\'t going to be easy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95382, 'enUS', '*cough* You\'re going to pay for that!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95383, 'enUS', '*cough* Augh my eyes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95384, 'enUS', 'Augh! It burns!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95385, 'enUS', 'I\'ll save you, poor little thing.', 'I\'ll save you, poor little thing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95386, 'enUS', '', 'Stay close! Here we go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95387, 'enUS', 'I\'ll stab you in the knees!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95388, 'enUS', 'You\'ll be sorry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95389, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95390, 'enUS', 'The bigger you are, the more there is to hurt!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95391, 'enUS', 'We will never yield the wilds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95392, 'enUS', 'Bears don\'t like you! Why protect them?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95393, 'enUS', 'Only wanted a little fun. This is... no fun...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95394, 'enUS', 'I enjoy watching you squirm. You make a fitting sacrifice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95395, 'enUS', 'Draenor itself serves the Legion now. See how the elements bend to my will!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95396, 'enUS', 'Cyrukh, what are you doing? Only flesh and blood oppose you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95397, 'enUS', '', 'I see you, $n. What is the plan?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95398, 'enUS', 'You kneel down and hold out a hand. The baby bear runs to you and sniffs at your fingers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95399, 'enUS', 'I\'ll adopt you, little cub.', 'I\'ll adopt you, little cub.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95400, 'enUS', 'Welcome to Ironhorn Enclave.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95401, 'enUS', 'Greetings, miner. Do you seek training?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95402, 'enUS', 'Open your ears, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95403, 'enUS', 'The first ancestor that any of us can remember was Rethu.\n\nRethu Ironhorn was the first of our kind to set hoof on this mountain. He made this plateau his home, and raised his children here. Some say he carved this very camp out of the mountain itself.\n\nHe was a smith, and he was a father, but before any of that... he was a MINER.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95404, 'enUS', 'All stories mention his patience, and most refer to his wisdom. He may have been the greatest miner that our people have ever known, but he was not afraid to share his knowledge with the rest of the herd.\n\nHe trained his sons and daughters, of course, but he also took on apprentices. Greenhoofs who sought direction found themselves ascending Highmountain to learn what he had to teach.\n\nEverything we know, we owe to him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95405, 'enUS', 'In his youth, he walked the land. He carved out caves and canyons. He spoke to the rocks. Some stories say that this rock-speech was the reason he was ejected from his original tribe.\n\nHis knowledge of leystone was legendary. His time on Azeroth, much like ours, was also plagued with demons, and he worked with ore much like the felslate that you see nowadays.\n\nHe was a pioneer, $n. We all walk in his long shadow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95406, 'enUS', 'Nobody knows. He left the enclave while they slept, and never returned. Some say he sailed off in search of new challenges. Others say that he dug too deep and uncovered something he should not.\n\nEither way, his legacy lives on, threefold. He left us this land. He left us his knowlege. And he left a lineage - some of us, myself included, bear his surname.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95407, 'enUS', 'Tell me your story.', 'Tell me your story.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95408, 'enUS', 'What was he like?', 'What was he like?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95409, 'enUS', 'Tell me about Rethu\'s mining.', 'Tell me about Rethu\'s mining.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95410, 'enUS', 'What happened to Rethu?', 'What happened to Rethu?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95411, 'enUS', 'I see.', 'I see.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95412, 'enUS', 'Taste the steely justice-blows of my blade!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95413, 'enUS', 'I say! Know you nothing of honor, dragon?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95414, 'enUS', 'Art that the best thou canst do?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95415, 'enUS', '', 'Destroying this barrier will take a great deal of my strength. I fear I may not be able to aid you after it is broken.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95416, 'enUS', 'Dragons! I\'ll slice off your tails, should I find them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95417, 'enUS', '', 'There are tracks here. Swiftflight must have gone this way!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95419, 'enUS', 'Your beloved Swiftflight is dead! You managed to survive the fall, night elf. You will not be so lucky again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95420, 'enUS', '', 'Hero! Avenge Swiftflight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95421, 'enUS', 'I will crush you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95422, 'enUS', 'Stab it until it\'s dead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95423, 'enUS', 'The magic is ours, thief!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95424, 'enUS', 'My arrow kill you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95425, 'enUS', 'I\'ll poke holes in you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95426, 'enUS', 'I make you bleed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95427, 'enUS', 'Go away, monster of dreams!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95428, 'enUS', 'You dare disrupt our rituals?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95429, 'enUS', 'Creature of dreams, begone!', 'Auughh! Creature of dreams, begone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95430, 'enUS', 'The bear cub sits on your foot and looks up at you trustingly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95431, 'enUS', 'Thanks, boss... but two hundred gold? What a rip-off!', 'Thanks, boss... but two hundred gold? What a rip-off!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95432, 'enUS', 'Yeowch! That\'s gonna leave a mark!', 'Yeowch! That\'s gonna leave a mark!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95433, 'enUS', '', 'Thank you, hero. I could not have done this alone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95434, 'enUS', 'What is this magic?! You\'ll pay for this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95435, 'enUS', 'What?! My Power!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95436, 'enUS', 'Justice has been laid. Heroic deeds of valor, performed. I must now attend to matters of CHIVALRY!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95437, 'enUS', 'A noble hero never forgets what beauty he fights for.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95438, 'enUS', 'Know this, my worship: I nearly died this day. But my mortality was not threatened by any dragon-wound.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95439, 'enUS', 'NAY! I nearly died, amidst that clanging melee, simply from unsatisfied desire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95440, 'enUS', 'While dragons bled on the ground, my heart bled for thee!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95441, 'enUS', 'Wouldst thou consummate our secret love with a smile, or, perhaps, a wave?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95442, 'enUS', 'The rustling sounds grow louder. The forest sprites are approaching the cave entrance! Prepare for their attack.$b$bThe baby bear looks helpless and frightened.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95443, 'enUS', 'Squires, heroes, and damsels alike! Bring thineselves here, and be protected by me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95444, 'enUS', 'You mortals dare stand against the might of the Legion?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95445, 'enUS', 'Your time has come - and now it shall end.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95451, 'enUS', '<The druid speaks softly in his sleep.>$B$BThey will never find my treasure. I have hidden it too well.$B$B<He begins mumbling inaudibly afterwards.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95452, 'enUS', 'The center control zone is now active!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95453, 'enUS', 'The Alliance captured the center control zone', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95454, 'enUS', 'The Horde have captured the center control zone', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95456, 'enUS', '<Gently shake the sleeping druid.>', '<Gently shake the sleeping druid.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95457, 'enUS', '', 'There\'s a war outside. That means lives to save and money to be made, $n!\n\nWhadda ya say to taking my Nitro-Electric Jumpstarter and helping the Bruisers on the front shake it off? Don\'t forget to leave \'em the bill!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95458, 'enUS', '', 'They\'re not dead yet, $n. That means they\'re potential customers!\n\nIt\'s simple. You take my Nitro-Electric Jumpstarter and use it on some Bruisers who look a bit worse for wear. Then, they pay me! I\'m gonna be rich!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95459, 'enUS', 'I\'ll take a Nitro-Electric Jumpstarter, Nurse.', 'I\'ll take a Nitro-Electric Jumpstarter, Nurse.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95460, 'enUS', 'Champions! You must not let them reach the Caverns!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95461, 'enUS', 'Still holding out hope Hellscream? Your precious Horde will serve the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95462, 'enUS', 'Prepare yourselves. My powers can only hold them for so long...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95463, 'enUS', 'We... will.. NEVER be slaves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95464, 'enUS', 'See how they struggle, not even their combined might will be enough to save you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95465, 'enUS', 'I will have your skull for my helm when I next face your foul legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95466, 'enUS', 'No matter, we will crush you all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95467, 'enUS', 'Heh Heh Heh, they are besting you demon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95468, 'enUS', 'Finish him heroes, that I may drive that cursed axe down his throat and cut out his fel heart.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95469, 'enUS', 'My thanks to you, now leave me.  I have a trophy to carve.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95470, 'enUS', '', 'Even the greatest heroes have tae rest every now and again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95471, 'enUS', 'Show me what goods you have for sale.', 'Show me what goods you have for sale.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95472, 'enUS', '[Outro cinematic of Skyfire flying away in flames, shaking from explosions.]', '[Outro cinematic of Skyfire flying away in flames, shaking from explosions.]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95473, 'enUS', 'No one will ever think to look behind the dishes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95474, 'enUS', '[Outro cinematic of Skyfire flying away in flames, shaking from explosions.]', '[Outro cinematic of Skyfire flying away in flames, shaking from explosions.]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95475, 'enUS', 'Fools! You have invoked the wrath of the Forsaken!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95476, 'enUS', 'Burn! Hahaha!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95477, 'enUS', '<The druid continues to mumble in his sleep, occasionally tossing and turning.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95478, 'enUS', 'Aspect of Ursol', 'Aspect of Ursol', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95479, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ICONS\\ACHIEVEMENT_CHARACTER_ORC_MALE.BLP:40|t[High Overlord Saurfang] Your invasion stops now, demon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95480, 'enUS', '|TInterface\\Icons\\spell_shadow_auraofdarkness:20|tYou have |cFFFF0000|Hspell:181099|h[Mark of Doom]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95481, 'enUS', '|TInterface\\Icons\\ability_ironmaidens_convulsiveshadows:20|tYou are afflicted by |cFFFF0000|Hspell:181799|h[Shadowforce]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95482, 'enUS', '|TInterface\\Icons\\ability_ironmaidens_convulsiveshadows:20|t%s begins to cast |cFFFF0000|Hspell:182084|h[Empowered Shadowforce]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95483, 'enUS', 'Aspect of Shakama', 'Aspect of Shakama', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95484, 'enUS', 'Aspect of the Moon', 'Aspect of the Moon', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95485, 'enUS', '', 'A valorous sacrifice! But it is not yet your time...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95486, 'enUS', '', 'Keep your eyes on the skies. The infernals could start falling again at any moment.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95487, 'enUS', '', 'I will place three shards of felslate into your mining arm: one in your shoulder, one in the elbow, and one in the wrist. Small shards, but jagged shards of stone, nonetheless.$b$bThis may hurt a bit.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95488, 'enUS', '', 'You handled that well enough.$b$bThe next one goes into your elbow. Are you ready?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95489, 'enUS', '', 'We are nearly done. As is often the case, three is the magic number. You do not want any less felslate in your body... and you CERTAINLY don\'t want any more.$b$bThe final piece goes directly into your wrist. Ready?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95490, 'enUS', '', 'Understandable. The risk is high... but if you wish to improve your mining skill, there is no other way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95491, 'enUS', '', 'Don\'t waste my time then. Go back to mining leystone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95492, 'enUS', '', 'We\'ve nearly finished the procedure, miner. No point in backing out now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95493, 'enUS', '', 'We demon hunters toe a dangerous line. Only you can determine where that line lays.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95494, 'enUS', 'I am ready. Drive the felslate shard into my shoulder.', 'I am ready. Drive the felslate shard into my shoulder.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95495, 'enUS', 'Actually, I\'m having second thoughts. I don\'t think I want to do this.', 'Actually, I\'m having second thoughts. I don\'t think I want to do this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95496, 'enUS', 'Yes. Hammer the felslate into my elbow.', 'Yes. Hammer the felslate into my elbow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95497, 'enUS', 'I don\'t want to do this anymore.', 'I don\'t want to do this anymore.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95498, 'enUS', 'Ready. Imbue my wrist with felslate.', 'Ready. Imbue my wrist with felslate.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95499, 'enUS', 'This is too much. I\'m not a demon hunter like you - I have no tolerance for such demonic augmentation.', 'This is too much. I\'m not a demon hunter like you - I have no tolerance for such demonic augmentation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95500, 'enUS', 'This is too risky. I am a demon hunter, Nal\'ryssa... I have already demon\'s blood in my veins. I may lose control.', 'This is too risky. I am a demon hunter, Nal\'ryssa... I have already demon\'s blood in my veins. I may lose control.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95501, 'enUS', 'CLUCK! Threat detected! Extermination commencing.', 'CLUCK! Threat detected! Extermination commencing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95502, 'enUS', 'Hardware overdrive enabled. Frz... gr... malfunction!', 'Hardware overdrive enabled. Frz... gr... malfunction!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95503, 'enUS', 'Engaging. Entity type: Unknown.', 'Engaging. Entity type: Unknown.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95504, 'enUS', 'Ha! This one\'s still got spirit!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95505, 'enUS', 'This one\'s slow! Somebody give me a dagger!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95506, 'enUS', 'You won\'t get rid of me that easily!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95507, 'enUS', 'Come on! You can\'t be that tired!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95508, 'enUS', '$n prepares a Sugar-Crusted Fish Feast!', '$n prepares a Sugar-Crusted Fish Feast!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95509, 'enUS', '$n prepares a Fancy Darkmoon Feast!', '$n prepares a Fancy Darkmoon Feast!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95510, 'enUS', 'Kraw, my cousin, harbors a thirst for power. Now, the tribe follows him into madness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95511, 'enUS', 'An Icebound Dreadnought freezes the gun solid!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95512, 'enUS', 'Please find my nephew before he goes mad like the others!', 'Please find my nephew before he goes mad like the others!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95513, 'enUS', 'Rockets, er... transportation systems ready to go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95514, 'enUS', 'Next victim! Ha! Hey, I\'m only kiddin\'...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95515, 'enUS', 'No sign of Sylvanas here. We\'ll clean this up - find a way over to the next ship!', 'No sign of Sylvanas here. We\'ll clean this up - find a way over to the next ship!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95516, 'enUS', 'We\'ll take it from here. Keep looking for the Windrunner!', 'We\'ll take it from here. Keep looking for the Windrunner!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95517, 'enUS', 'I go to my uncle. Thank you, kind stranger!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95518, 'enUS', '|TInterface\\Icons\\ACHIEVEMENT_CHARACTER_NIGHTELF_MALE.BLP:50|tWell done, $n. The druids of the claw are on our side, but our work is not yet finished.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95519, 'enUS', '<The stone emanates demonic energy. Speak the name of its owner, and she will appear.>', '<The stone emanates demonic energy. Speak the name of its owner, and she will appear.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95520, 'enUS', 'Rin\'thissa.', 'Rin\'thissa.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95521, 'enUS', 'Lyrelle.', 'Lyrelle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95522, 'enUS', 'Ondri.', 'Ondri.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95523, 'enUS', 'Can I ask your help, stranger?$B$BMy tribe is the Smolderhide. Recently, my brothers found bad magic. They have gone mad with rage. They attack the druids that are our friends.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95524, 'enUS', 'Thank you for saving my nephew. Hopefully, we can rebuild our village.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95525, 'enUS', 'Parachutes are downstairs, $Gsir:ma\'am;.', 'Parachutes are downstairs, $Gsir:ma\'am;.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95526, 'enUS', 'I thank you, $c. If you had not saved me, I would have lost my mind... like the others.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95527, 'enUS', 'The legionnaires are deploying from the rear deck. They\'ll be glad to fight alongside you!', 'The legionnaires are deploying from the rear deck. They\'ll be glad to fight alongside you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95528, 'enUS', '<Deep lacerations cover the arms and face of this druid. Unfortunately, there is nothing more you can do for her.>', '<Deep lacerations cover the arms and face of this druid. Unfortunately, there is nothing more you can do for her.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95529, 'enUS', 'May you rest in peace. I will inform the Archdruid that you fought bravely to the end.', 'May you rest in peace. I will inform the Archdruid that you fought bravely to the end.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95530, 'enUS', 'I was overwhelmed by the enemy. I was sure that I would die here... but not without a fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95531, 'enUS', 'Nightmane sent me to find you. Are you wounded?', 'Nightmane sent me to find you. Are you wounded?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95532, 'enUS', '', 'Tell Nightmane that I still live, thanks to your aid.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95533, 'enUS', 'I\'ll return to safety as soon as I recover my strength. Thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95534, 'enUS', '', 'My sisters are out there somewhere, $p. They\'re more demon than elf now, but they are still miners. Let me know if you see any sign of them in your travels.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95535, 'enUS', 'Applying', 'Applying', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95536, 'enUS', 'Interested in building some ships?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95537, 'enUS', 'I would like to build a carrier.', 'I would like to build a carrier.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95538, 'enUS', 'Start construction on our first destroyer.', 'Start construction on our first destroyer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95539, 'enUS', 'I would like to build a submarine.', 'I would like to build a submarine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95540, 'enUS', '', 'Yes?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95541, 'enUS', 'Show me what missions you have prepared.', 'Show me what missions you have prepared.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95542, 'enUS', '', 'Let me know when you\'re ready to split up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95543, 'enUS', 'Slay these unholy creatures! For the Alliance!', 'Slay these unholy creatures! For the Alliance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95544, 'enUS', 'Watch yourselves - that doesn\'t look like Westfall Stew in those cauldrons...', 'Watch yourselves - that doesn\'t look like Westfall Stew in those cauldrons...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95545, 'enUS', 'Royal guards - this is the Windrunner! Seize the captain!', 'Royal guards - this is the Windrunner! Seize the captain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95546, 'enUS', '|cFFFF0000%s|r is about to finish casting |cFFFF0000|Hspell:181912|h[Focused Blast]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95547, 'enUS', '', 'And your ship is already doomed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95548, 'enUS', 'Redeem your darkmoon daggermaw here for fabulous prizes!', 'Redeem your darkmoon daggermaw here for fabulous prizes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95549, 'enUS', 'Fishing prizes for all ages!', 'Fishing prizes for all ages!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95550, 'enUS', 'Try your rod against the crafty darkmoon daggermaw today!', 'Try your rod against the crafty darkmoon daggermaw today!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95551, 'enUS', 'Fresh-caught prizes right here, only a rod and reel away!', 'Fresh-caught prizes right here, only a rod and reel away!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95552, 'enUS', 'Ahh, Val\'sharah...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95553, 'enUS', '', 'Wait! She may have information!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95554, 'enUS', '', 'So many bats...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95555, 'enUS', 'Your pathetic efforts have accomplished nothing, mortals! Our citadels anchor Sargeras to this world. Your end is inevitable!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95556, 'enUS', 'You were always weak, Iroxus! You will be flayed for millennia for failing Sargeras!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95557, 'enUS', 'You dare to resist Sargeras\' will on this pathetic world? I will see to your end myself, mortals!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95558, 'enUS', 'Need equipment for your ship?  I\'m your man!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95559, 'enUS', 'What kind of naval equipment do you have?', 'What kind of naval equipment do you have?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95560, 'enUS', 'Are you sure you want to buy th\'gear I\'ve got for sale? Slugg over there has all th\' latest and greatest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95561, 'enUS', 'I would like to buy outdated equipment from you.', 'I would like to buy outdated equipment from you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95562, 'enUS', 'Are you sure you want to buy th\'gear I\'ve got for sale? Crunchpaw has th\' best stuff these days.\n\nB\'tween the two of us, I heard she didn\'t get her nickname from fightin\' the Horde.\n\nDon\'t tell her I said that.\n\nActually, I\'d prefer y\'din\'t even mention that we spoke. I\'m not on her good side right now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95563, 'enUS', 'Well met.', 'Well met.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95564, 'enUS', 'Legendary: Gul\'dan Ascendant', 'Legendary: Gul\'dan Ascendant', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95565, 'enUS', '', 'We will find someone with knowledge of the Pillars of Creation. You have my word.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95566, 'enUS', '', 'You are welcome to what shelter we can provide, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95567, 'enUS', 'You should have stayed home!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95568, 'enUS', 'The master will mend my flesh again and again. You have not won. You cannot win. We are endless! We are Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95569, 'enUS', 'Our transport is ready. The invasion of Tanaan can begin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95570, 'enUS', 'Durotan - are the Frostwolves prepared?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95571, 'enUS', 'Yes. We will assault the main gate while your ships circle around to the eastern shore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95572, 'enUS', 'We\'re countin\' on you, commander. Bring victory to da Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95573, 'enUS', '', 'Durotan and I have spoken. I will accompany your naval assault and protect you while we establish a foothold in Tanaan Jungle.$b$bI am ready. Are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95574, 'enUS', 'Come now. Tell me you\'re at least considering it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95575, 'enUS', 'Twenty-seven times in the past two hours. The same talk.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95578, 'enUS', 'Damn it, Doug, look at me. We can\'t stay here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95579, 'enUS', 'We\'ll hold out if we all do our part. Get back to work.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95580, 'enUS', '', '|cFFFF0000%s|r attempts to remove any harmful effects on herself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95581, 'enUS', '', 'I have spoken with the other Exarchs and everything is prepared. I will accompany your naval mission and support you while we establish a rear base. Maladaar has volunteered to lead the diversionary assault at the main gate.$b$bI\'m as ready as I\'ll ever be. Are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95582, 'enUS', 'Yrel - are your people prepared?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95583, 'enUS', '', 'Yes. The draenei will lead an assault on the main gates while your navy circles around to the rear.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95584, 'enUS', 'Godspeed, Commander!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95585, 'enUS', 'There\'s a ghost riding a big cat in the ruins.', 'There\'s a ghost riding a big cat in the ruins.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95586, 'enUS', '', 'There is an ancient blue dragon just north of here. If anyone will know of the Pillars, it will be him.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95587, 'enUS', 'I\'ve been feeling a powerful magical presence in that same direction - it may be the dragon you found. We should investigate at once.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95588, 'enUS', 'I got to visit the coffin maker yesterday. Brilliant!', 'I got to visit the coffin maker yesterday. Brilliant!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95589, 'enUS', 'There were lights in the castle last night. Did you see them?', 'There were lights in the castle last night. Did you see them?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95590, 'enUS', 'Have you seen a dead body? I have.', 'Have you seen a dead body? I have.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95591, 'enUS', 'Da caught a two-headed fish a few days ago. It was all purple and oozy.', 'Da caught a two-headed fish a few days ago. It was all purple and oozy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95592, 'enUS', 'Sis won\'t take me night fishing anymore. I never get to do anything fun.', 'Sis won\'t take me night fishing anymore. I never get to do anything fun.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95593, 'enUS', '', 'Yes, my liege. The camps are being prepared as we speak.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95594, 'enUS', '', 'You should finish your business with Kayn in the meantime.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95595, 'enUS', '', 'Our strength is unmatched. These beings have yet to withstand our assaults.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95596, 'enUS', '', 'Our grasp expands over the lands. The knowledge will soon be ours.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95597, 'enUS', '', 'A truly delicate planet. The inhabitants are quite... fragile.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95598, 'enUS', 'Nathanos\' Rangers launch |TInterface\\Icons\\inv_ammo_arrow_04:20|t |cFFFF0404|Hspell:182466|h[Plague Arrows]|h|r.  Lead Thane Wildsky into them!', 'Nathanos\' Rangers launch |TInterface\\Icons\\inv_ammo_arrow_04:20|t%s |cFFFF0404|Hspell:182466|h[Plague Arrows]|h|r.  Lead Thane Wildsky into them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95599, 'enUS', 'Move out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95600, 'enUS', 'Charge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95601, 'enUS', 'Attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95602, 'enUS', 'Ow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95603, 'enUS', 'Reporting for duty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95604, 'enUS', 'Ready to go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95605, 'enUS', '', 'Move out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95606, 'enUS', '', 'Charge!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95607, 'enUS', '', 'Attack!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95608, 'enUS', '', 'Ow!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95609, 'enUS', '', 'Reporting for duty!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95610, 'enUS', '', 'Ready to go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95611, 'enUS', 'Ahh! Damn you, demon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95612, 'enUS', 'Whoever you are, aid me, I beg you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95613, 'enUS', 'Over here! Please help me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95614, 'enUS', 'In the name of Elune! Save me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95615, 'enUS', '', 'Main cannons, prepare for a second volley as we breach the cloud-line.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95618, 'enUS', 'Ssssmoke won\'t stop me, pitiful creature!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95620, 'enUS', 'Old woman doesssn\'t sssssscare me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95623, 'enUS', 'Ha! That Granny is blind as a bat!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95624, 'enUS', 'Wassss that ssssupposed to hurt?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95625, 'enUS', '%s finishes off its victim, then departs in search of fresh prey.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95626, 'enUS', 'I... um... well, this is terribly embarassing, but I\'ve misplaced the Branch of Nordrassil.', 'I... um... well, this is terribly embarassing, but I\'ve misplaced the Branch of Nordrassil.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95627, 'enUS', 'Did that traitor of a prince send you? So be it, I\'ll show no mercy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95628, 'enUS', 'What... what have I done?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95629, 'enUS', '%s lunges at new prey.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95630, 'enUS', '|cFFFF0000%s|r begins to cast |cFFFF0000|Hspell:186045|h[Solar Chakram]|h|r at you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95631, 'enUS', 'I will restrain my hand against these civilians. They do not know who I am, and I am in no danger from them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95632, 'enUS', 'I must make them heed my words, not my sword. Novices will often resort to force, but persuasion is a master\'s weapon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95633, 'enUS', '|cFFFF0000%s|r is creating an energy well to empower his allies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95634, 'enUS', '|cFFFF0000%s|r is being drained of energy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95635, 'enUS', '', 'You\'ve led well so far, $n - I see no reason to change direction now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95636, 'enUS', '', 'I\'m off to Dalaran to find paladins worthy of joining our cause. Knights, stay here in case $n needs anything while I\'m gone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95637, 'enUS', 'They should have been here by - ah, there they are.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95638, 'enUS', '', 'Elune bless my arrow. Let my aim be true!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95639, 'enUS', '', 'Invader, this world will not fall to your kind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95640, 'enUS', '', 'Interlopers! The kaldorei protect these lands.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95641, 'enUS', '', 'Countless worlds have you destroyed, but we will not fall so easily.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95642, 'enUS', '', 'In Azshara\'s name, begone from this place!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95643, 'enUS', '', 'My blade is sharp, and my arrow, swift. We will not be easy targets.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95644, 'enUS', 'Taste steel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95645, 'enUS', 'No obstacle shall stand in our way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95646, 'enUS', 'Bandu thoribas!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95647, 'enUS', 'Tor ilisar\'thera\'nal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95648, 'enUS', 'In the master\'s name!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95649, 'enUS', 'You shall not live to see another moon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95650, 'enUS', '', 'Amelia over there has much bett\'r gear than what I can offer yeh if you\'re lookin\' to take on the Horde.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95651, 'enUS', '', 'Y\'should speak with Holly McTilla if you\'re lookin\' to square off against the best the Horde has to offer. Also, ask her who does her hair for me would ya?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95654, 'enUS', 'Lion\'s Watch', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95655, 'enUS', 'Vol\'mar', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95656, 'enUS', '', 'Ainderu! What do you make of this cloth?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95657, 'enUS', 'Looks like night elf cloth to... wait a moment...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95658, 'enUS', 'No... those are ancient Highborne patterns!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95659, 'enUS', '', 'Fascinating...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95660, 'enUS', 'While the Nightfallen look much like I do, they are craven, twisted ley-addicts who will stab you in the back to get a taste of your mana. I am a friendly tailor who has simply fallen on hard times. Note the difference!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95661, 'enUS', 'We will not stand idle while the Legion threatens our home. Our blades are yours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95662, 'enUS', 'Come forth, Incindrius! Show these fools the might of the Burning Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95663, 'enUS', '', 'We will always answer the Light\'s call. Let us know where we are needed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95664, 'enUS', 'This equipment isn\'t as good as what Fobbly Kickfix sells. I suggest you arm yourself with her wares first.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95665, 'enUS', 'You should speak with Malukah Lightsong if you intend to buy the best equipment available.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95666, 'enUS', '', 'Cladd Dawnstrider is the one you seek if you wish to claim the highest quality rewards for your honor in battle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95667, 'enUS', '', 'Tae\'lox Soulshrivel is the one you seek if you wish to claim the highest quality rewards for your honor in battle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95668, 'enUS', '', 'Yeah yeah, I know, my stuff isn\'t as good as what Tae\'lox is sellin\'. But ya know what, it\'s still prettier, right? Whaddaya mean you think it looks the same? It\'s CLEARLY different! You ungrateful bum! You\'re lucky my contract is still being enforced.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95669, 'enUS', 'The quality of my wares is inferior to those sold by Malukah Lightsong. You should talk to her if you intend to take on the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95670, 'enUS', 'The quality of my equipment is inferior to that of the lovely Malukah Lightsong. Speak wit her if you intend to buy the best equipment available.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95671, 'enUS', 'I regrettably inform you that the finest arms and armaments for use in the field of battle are now sold by Ms. Crunchpaw. Don\'t worry, I\'m hard at work figuring out how to modify what I do have to be even better. I\'m thinking... extra arms! That you control with your mind!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95672, 'enUS', 'I would like to build a battleship.', 'I would like to build a battleship.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95673, 'enUS', 'I would like to build a transport.', 'I would like to build a transport.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95674, 'enUS', '', 'I can\'t believe that peasant Holly McTilla was trusted with the finest weapons and armor. A dwarf! Honestly, it\'s no wonder we haven\'t taken this island yet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95675, 'enUS', 'Woof!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95676, 'enUS', 'Rrrrruff!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95677, 'enUS', '%s whines piteously.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95678, 'enUS', 'The shipyard is currently full, try back once the current ship completes.', 'The shipyard is currently full, try back once the current ship completes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95679, 'enUS', 'Let us make haste. We must find the Legion before they strike again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95680, 'enUS', '|c00FFFF00|Hspell:186255|h[Event: Apexis Marks]|h|r begins at the Ashran Excavation in 3 minutes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95681, 'enUS', '', 'We should hurry. The Legion could attack at any moment.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95682, 'enUS', 'Please, take your time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95683, 'enUS', 'I\'ll just be waiting here patiently to be summoned to Dalaran.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95684, 'enUS', 'Try not to forget me, or how helpful I was!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95685, 'enUS', '|c00FFFF00|Hspell:178022|h[Event: Stadium Racing]|h|r has begun at the Amphitheater of Annihilation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95686, 'enUS', '|c00FFFF00|Hspell:186255|h[Event: Apexis Marks]|h|r has begun at the Ashran Excavation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95687, 'enUS', '|c00FFFF00|Hspell:178019|h[Event: Collect Empowered Ore]|h|r has begun at the Molten Quarry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95688, 'enUS', '|c00FFFF00|Hspell:178021|h[Event: Ogre Fires]|h|r has begun at Brute\'s Rise!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95689, 'enUS', '|c00FFFF00|Hspell:178020|h[Event: Risen Spirits]|h|r has begun at the Ashmaul Burial Grounds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95690, 'enUS', '', 'There\'s no time to waste. Let\'s move!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95691, 'enUS', 'I need somebody summoned up to Dalaran. Can you help me?', 'I need somebody summoned up to Dalaran. Can you help me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95692, 'enUS', '', 'A summon? Sure! Where did you say he was?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95693, 'enUS', '', 'Hold on a sec... there! I\'ve got him!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95694, 'enUS', '$n! Such a pleasure to see you again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95695, 'enUS', 'So... this is Dalaran.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95696, 'enUS', 'This is a lovely tailoring shop you have here. Do you mind if I look around?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95697, 'enUS', '', 'Not at all. Make yourself at home.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95698, 'enUS', '', 'I\'m sure there is much we can learn from one another.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95699, 'enUS', 'Thank you, $n. I need to... acquaint myself with this shop before I can train you any further.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95700, 'enUS', '', 'Welcome to Cartier and Company Fine Jewelry, $c. I can tell you\'re a customer with an appreciation for the finer things.$b$bWhat can I show you today?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95701, 'enUS', 'Something very expensive, please.', 'Something very expensive, please.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95702, 'enUS', 'I was told you can sharpen these needles for me.', 'I was told you can sharpen these needles for me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95703, 'enUS', '', 'Done! Tell Tanithria that she owes me another silk pillow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95704, 'enUS', '', 'Easy enough...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95705, 'enUS', 'Careful, $n. Cordana has declared Khadgar\'s tower off-limits. She\'s acting odd.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95706, 'enUS', 'We will send Gul\'dan\'s filth to the bottom of the sea!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95707, 'enUS', 'Farahlon is mysterious land. Only send a crew you trust... or can afford to lose.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95708, 'enUS', 'Aid me, champions! This is not our final hour! We must survive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95709, 'enUS', 'Never! This world will stand against the legion and prevail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95710, 'enUS', 'The citadel is breached! Gul\'dan is within. I will establish a foothold here - press forward!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95711, 'enUS', 'We will plumb the depths of Draenor for all its secrets!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95712, 'enUS', 'I never believed Gul\'dan would go this far... We must press on. A portal lies within these ruins that will take us further into the citadel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95713, 'enUS', 'The portal is ready, champion. Steel yourselves, I know not what further atrocities Gul\'dan has unleashed in this place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95714, 'enUS', 'This entire citadel seethes with Gul\'dan\'s corruption! A second portal is nearby. Keep his demons at bay while I break the seals and attune the portal to his location.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95715, 'enUS', 'I\'ve breached the first seal, soon the portal will take us to Gul\'dan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95716, 'enUS', 'Give Cordana my best. She\'s been quite distant lately.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95717, 'enUS', 'Another seal is open! Oh, I am good.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95718, 'enUS', 'The portal is nearly complete, the next seal has been removed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95719, 'enUS', 'The final seal has fallen! My work here is almost finished.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95720, 'enUS', 'The portal is unlocked! It will take us directly to Gul\'dan. Now he will pay for his crimes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95721, 'enUS', 'Gul\'dan has fled to the Dark Portal! Quickly, champions! He has no place left to run - we will finish this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95722, 'enUS', 'And save me his skull. For... reasons.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95723, 'enUS', '', 'Books. Yes, Gul\'dan\'s tomes. Are you sure Khadgar didn\'t try to use them?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95724, 'enUS', '', 'Books of power... books of knowledge... books written in the language of chaos... Don\'t you worry, champion, all will be destroyed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95725, 'enUS', '', 'Now, give me your ring. I need it. Give it here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95726, 'enUS', 'That\'ll do for now. I think I\'d like to... explore your city some more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95727, 'enUS', '', 'Then I will TAKE it from you! For the Glory of Gul\'dan!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95728, 'enUS', '', 'The Orb of Dominion spoke to me. I saw the TRUTH.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95729, 'enUS', '', 'Khadgar is just a child, swaggering around, torturing his prisoners, playing with lives, dabbling in magics he pretends to comprehend.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95730, 'enUS', '', 'Such is the power of the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95731, 'enUS', '', 'Very well, walk away. Nothing will stop Gul\'dan, for the Legion stands behind him. Mark my words: Your leaders will betray you. AZEROTH WILL BURN.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95732, 'enUS', 'The Burning Legion comes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95733, 'enUS', 'Go now to Karabor, champion of the Alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95734, 'enUS', 'You face the might of the Burning Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95735, 'enUS', 'The elemental spirits of Draenor await you, champion of the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95736, 'enUS', 'Burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95737, 'enUS', 'Your life ends now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95738, 'enUS', 'Suffer my wrath!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95739, 'enUS', 'My power grows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95740, 'enUS', 'Feel the legion\'s power!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95741, 'enUS', 'All life will be eradicated!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95742, 'enUS', 'Pathetic wretch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95743, 'enUS', 'Oblivion... comes...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95744, 'enUS', '', 'Welcome, commander! Khadgar tells me you\'ve accomplished so many great things...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95745, 'enUS', '', 'Take your knee, champion. It is a rare and special privilege to be blessed by a Naaru.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95746, 'enUS', 'Blessed are the torchbearers, who bring the light to darkness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95747, 'enUS', 'Blessed are the valorous, who stand fast in an unjust world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95748, 'enUS', 'Champion. A great darkness has taken root on Draenor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95749, 'enUS', 'The righteous will flock to your banner. You must not falter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95750, 'enUS', 'Go now, with the Light\'s blessing. Take the fight to our enemies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95751, 'enUS', 'The fate of two worlds hangs in the balance...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95752, 'enUS', '', 'Did you hear anything? Did the Naaru speak to you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95753, 'enUS', '', 'Commander, whatever you should do next, know that you have the full support of the draenei people.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95754, 'enUS', '', 'We stand with you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95755, 'enUS', 'Commander, is that you? Your aura has grown quite strong.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95756, 'enUS', 'The very elements of Draenor tremble, friend. Can you hear them? Listen... listen...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95757, 'enUS', 'Spirits of stone, lend your strength to our champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95758, 'enUS', 'Spirits of air, grant our champion quickness of mind and body.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95759, 'enUS', 'Spirits of water, give our hero the gift of your wisdom.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95760, 'enUS', 'Spirits of flame, lend us your fury, that our enemies will be driven before us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95761, 'enUS', 'Arise, champion. Know that the primal heart of Draenor burns within you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95762, 'enUS', 'Gul\'dan will do all he can to sever our connection to the elements, to deprive us on their power. You must stop him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95763, 'enUS', 'The fate of our world is in your hands, commander. Lok\'tar ogar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95764, 'enUS', 'With every passing moment, Gul\'dan grows in power. But he no longer slinks through the shadows - we know where he is. We know what he\'s doing. Now, we strike! May the Light have mercy on all our souls.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95765, 'enUS', 'Did you think to challenge me, infant? A poor decision.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95766, 'enUS', '', 'Gryphon riders, back them up!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95767, 'enUS', '', 'Hero! <cough> Get those chemical flares away from the ammo!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95768, 'enUS', 'Accept your death. It is bitter, but preferable to what is coming.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95769, 'enUS', 'The last hero to oppose me lies dead at my feet. Join her.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95770, 'enUS', 'You encroach upon our lands. For that, you shall pay dearly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95771, 'enUS', 'Mother Moon, have mercy. Let me rest now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95772, 'enUS', 'You have... won this fight, and yet I... almost pity you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95773, 'enUS', 'Those damned leyweavers had no right to throw me out. No right at all, I say.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95774, 'enUS', 'Lyndras, what are you doing here?', 'Lyndras, what are you doing here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95775, 'enUS', 'My lord will hear of this and know... you are... no mere nuisance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95776, 'enUS', 'I welcome the darkness. I have always... loved... the night.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95777, 'enUS', 'The Ashbringer glows brightly and protects you with |cFFFF0000|Hspell:642|h[Divine Shield]|h|r!', 'The Ashbringer glows brightly and protects you with |cFFFF0000|Hspell:642|h[Divine Shield]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95778, 'enUS', 'Err... I was just uh... looking for new dyes. Yes, that\'s it. New dyes. I\'ll return to the shop immediately.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95779, 'enUS', 'Destiny calls to you, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95784, 'enUS', 'I swear, I\'m staying put. No more exploring the city for me. I\'m happy right here in the tailoring shop.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95785, 'enUS', 'I had thought $p dead in the explosion, but $Ghe:she; survived and slew the demon! Never in all my days have I - there you are! I was just telling your men about your fight with Malgalor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95786, 'enUS', 'Lyndras, please, stop! Get a hold of yourself, man!', 'Lyndras, please, stop! Get a hold of yourself, man!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95787, 'enUS', 'Prove to me that you are worthy of this power! Free my brethren from their ghostly imprisonment, then come to me with their mark upon you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95788, 'enUS', '...What are you doing with the Orb of Dominion, Cordana?', '...What are you doing with the Orb of Dominion, Cordana?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95789, 'enUS', 'Khadgar wants you to destroy these books.', 'Khadgar wants you to destroy these books.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95790, 'enUS', '', 'Nothing.$b$bNothing that concerns you.$b$bI have this under control.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95791, 'enUS', 'A squire from the Argent Crusade? Here?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95796, 'enUS', '', 'Yes, your ring. THE ring! The one Khadgar has been cooking up for you since we arrived on Draenor.$b$bGive it here. I just want to... borrow it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95797, 'enUS', 'No.', 'No.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95798, 'enUS', 'Oh, HELL no!', 'Oh, HELL no!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95799, 'enUS', 'Destroy the Tomes of Chaos to break Cordana\'s shield', 'Destroy the Tomes of Chaos to break Cordana\'s shield', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95803, 'enUS', '', 'Father, please! Stranger, forgive us. We\'re all a tad wary of strangers these days.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95804, 'enUS', '', 'Thorns are a means of defense for green and growing things: fragile flora that would otherwise be defenseless.$B$BDo not curse the thorns. Rejoice in the blossoms they make possible.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95805, 'enUS', '', 'When our hearts falter, we cease to respect the world around us.$B$BNature holds a mirror to our souls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95806, 'enUS', 'Your bones will join the others!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95807, 'enUS', 'Share in our curse!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95808, 'enUS', 'You\'ll never defeat Bloodbeard!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95831, 'enUS', 'We are in luck. Had the Underking learned all of the secrets of the Hammer, we would not be having this conversation.$b$bFor now he is like a child with a new toy. Dangerous, yes, but not as much as he will be the longer he possesses the hammer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95836, 'enUS', 'Dig faster! We must bury this giant before the other challengers arrive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95853, 'enUS', '', 'As with Moonglade, this village and its denizens are under my protection. I only wish I could shelter more of the wilds and its caretakers.$B$BWould that I were strong enough to protect you all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95854, 'enUS', '', 'I have never seen these sprites so agitated.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95855, 'enUS', 'What can you tell me about these Thistleleaf sprites?', 'What can you tell me about these Thistleleaf sprites?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95857, 'enUS', 'May Odyn bless you on your trials.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95858, 'enUS', '', 'You may take your rest here without fear of nightmares.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95859, 'enUS', '', 'The Thistleleaf have always been a minor nuisance. However, lately their behavior has changed from puerile to hostile and destructive.$B$BI do not know what has caused this change, but something must be done to protect these faerie dragons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95860, 'enUS', 'Odyn? I kneel before no god.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95861, 'enUS', 'I will do what I can.', 'I will do what I can.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95862, 'enUS', 'Awww man not again.', 'Awww man not again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95863, 'enUS', '', 'Commander, reinforcements are arriving daily. Use this portal to travel between here and our stronghold in Ashran.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95864, 'enUS', 'Stay out of Violet Hold if you know what\'s good for you, $r. I don\'t like the look of some of the creatures they\'re storing in there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95865, 'enUS', 'Have you seen Lyndras?', 'Have you seen Lyndras?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95866, 'enUS', 'Is he that Nightfallen rat? We caught him snooping around the edges of town. Looked suspicious. He\'s been thrown into Violet Hold for questioning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95867, 'enUS', 'Once I have the Aegis of Aggramar -- the gods will kneel to me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95868, 'enUS', 'We Elderbranch know little of lands beyond our own.$B$BYou are good to have come here to help us, so far from your home.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95869, 'enUS', '', 'Now my warriors! The Halls of Valor are moments away!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95871, 'enUS', 'Your lines are weak, mortals. Our ranks are endless! Lay down your arms and serve Sargeras!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95872, 'enUS', 'There is no honor in death! Your cities will burn. Your armies will break. It is inevitable!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95873, 'enUS', 'Into the breach! Show these weaklings what the might of Sargeras does when brought to bear on an enemy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95874, 'enUS', '', 'Go on and run, I won\'t cower under a tent! If the next shot has my name on it, so be it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95875, 'enUS', '', 'I know your purpose, Banshee Queen! You should turn away, lest you invoke my wrath!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95878, 'enUS', '', 'Refuse, and I will take your gifts by force!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95879, 'enUS', '[Intro cinematic of Skyfire unloading a volley on the fleet from above the clouds.]', '[Intro cinematic of Skyfire unloading a volley on the fleet from above the clouds.]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95880, 'enUS', '', 'Foolish girl! You cannot comprehend the forces you meddle with.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95881, 'enUS', '', 'You will fall to your own arrogance. As did the one who created you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95882, 'enUS', 'You have foolishly rushed to your end. The Black Gate is whole!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95883, 'enUS', 'Behold the might of the legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95884, 'enUS', 'Yes, master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95889, 'enUS', '', 'We will seize the Aegis in time - we must not miss this opportunity to take out Sylvanas!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95890, 'enUS', 'Gilneas demands justice!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95891, 'enUS', '', 'Sylvanas will pay for her crimes...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95892, 'enUS', 'Sound design is the best!', 'Sound design is the best!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95893, 'enUS', 'Any luck contacting the fleet?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95894, 'enUS', 'It\'s no use - the weather systems around Stormheim are too strong. I can\'t get a signal through.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95895, 'enUS', '', 'It is an honor to fight alongside you, $n. From the moment I first encountered you inside that horrible tunnel behind the Dark Portal, your presence has always inspired me.$b$bK\'ara, too, sees your inner strength. Are you ready to speak with the Naaru?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95896, 'enUS', 'I am ready, Yrel.', 'I am ready, Yrel.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95897, 'enUS', '', 'You aren\'t supposed to be up here, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95898, 'enUS', '', 'The others were speaking of |5$r $c that knew how to sew. I assume that you are that $c.$b$bIf Tytallo thinks you\'re ready, then I can show you the Queen\'s Grace Loom.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95899, 'enUS', '', 'Greetings, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95900, 'enUS', '', 'Wh- what is this power?! It.. can\'t be...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95901, 'enUS', 'Tell me about the loom.', 'Tell me about the loom.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95902, 'enUS', '', 'The loom itself, frankly, isn\'t anything special.$b$bIt\'s a thing of beauty, to be sure. It\'s quite old, but at its core it\'s nothing but alderwood and a few nails. We do our best to maintain it.$b$bBut no, the loom itself is immaterial to our methods.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95903, 'enUS', '', 'How attentive of you. Yes, our order has made a bargain with a higher power, in order to gain more skill in our trade.$b$bI am prepared to show you our secret. Behold, the power of the Leyweavers!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95904, 'enUS', 'Tytallo mentioned something about sacrifice.', 'Tytallo mentioned something about sacrifice.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95905, 'enUS', 'Show me your secret.', 'Show me your secret.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95906, 'enUS', '', 'I am nothing like him! Behold!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95907, 'enUS', 'Work quickly! No relic is to fall into mortal hands!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95908, 'enUS', 'Wrest this world\'s secrets from the earth! The Legion will have every drop of power this place has to offer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95909, 'enUS', '', 'A gift from your sister, Helya. Now... kneel!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95910, 'enUS', 'Let\'s go for a joyride!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95917, 'enUS', 'You cursed beasts! After I return to the skies I will have your pathetic races obliterated!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95918, 'enUS', 'Lord Iskar... I\'ve failed you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95919, 'enUS', '', 'We\'ve come to an arrangement with Gul\'dan. We provide him and his forces with supplies, and in return, he\'s granted us a few of his more capable inquisitors.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95920, 'enUS', '', 'Of course, I\'d prefer that the leyweavers operate without anyone\'s assistance, but the quality of this demonically-imbued cloth far surpasses anything we could do on our own.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95921, 'enUS', '', 'We\'re tailors, $n, and so are you. No price is too high if it means a better robe. Am I right?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95922, 'enUS', '|TInterface\\Icons\\Spell_DeathKnight_AntiMagicZone:20|tA nearby Dreadstalker begins to cast |cFFF00000|Hspell:186562|h[Consume Magic]|h|r!', '|TInterface\\Icons\\Spell_DeathKnight_AntiMagicZone:20|tA nearby Dreadstalker begins to cast |cFFF00000|Hspell:186562|h[Consume Magic]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95923, 'enUS', 'I have shown you how to make Imbued Silkweave, and I\'ve also given you the pattern for Imbued Silkweave Bracers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95924, 'enUS', 'Inondra and I can help, but we cannot teach you all the patterns you need. You will need to earn or purchase them from others.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95925, 'enUS', '', 'There are many other leyweavers out wandering the world. I can show you where they are.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95926, 'enUS', 'MY SHINY EYE! MINE! MINE! MINE! MINE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95927, 'enUS', '', 'What is it you need?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95928, 'enUS', '', 'Be careful out there!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95929, 'enUS', 'Where can I learn how to make an Imbued Silkweave Robe?', 'Where can I learn how to make an Imbued Silkweave Robe?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95930, 'enUS', 'Where can I learn how to make an Imbued Silkweave Hood?', 'Where can I learn how to make an Imbued Silkweave Hood?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95931, 'enUS', 'Where can I learn how to make an Imbued Silkweave Cinch?', 'Where can I learn how to make an Imbued Silkweave Cinch?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95932, 'enUS', 'Where can I learn how to make Imbued Silkweave Slippers?', 'Where can I learn how to make Imbued Silkweave Slippers?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95933, 'enUS', 'Where can I learn how to make Imbued Silkweave Gloves?', 'Where can I learn how to make Imbued Silkweave Gloves?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95934, 'enUS', 'Where can I learn how to make Imbued Silkweave Pantaloons?', 'Where can I learn how to make Imbued Silkweave Pantaloons?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95935, 'enUS', 'Where can I learn how to make Imbued Silkweave Epaulets?', 'Where can I learn how to make Imbued Silkweave Epaulets?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95936, 'enUS', 'Where can I learn how to make Imbued Silkweave cloaks?', 'Where can I learn how to make Imbued Silkweave cloaks?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95937, 'enUS', '', 'Leyweaver Mithrogane, just outside this hall, knows that pattern.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95938, 'enUS', '', 'You should speak with Leyweaver Jorjana, in southern Suramar within the Docks District. I\'ll mark her position on your map.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95939, 'enUS', '', 'Leyweaver Erenyi. She\'s currently out researching druidic patterns in Val\'sharah. I\'ll mark her position on your map.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95940, 'enUS', '', 'Speak with Leyweaver Yaphisteia. She walks the roads of Highmountain. I\'ll mark her position on your map.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95941, 'enUS', '', 'I believe Leyweaver Athystro knows that pattern. You\'ll find him just west of here overlooking Felsoul Hold. I\'ll mark his position on your map.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95942, 'enUS', '', 'Leyweaver Tellumi, who is currently traveling in Stormheim, maintains that pattern for our guild. I\'ll mark her position on your map.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95943, 'enUS', '', 'Shoulder armor is the domain of Leyweaver Phaxondus. He prefers to spend his time near the exposed ley lines of Azsuna. I\'ll mark his position on your map.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95944, 'enUS', '', 'You are in luck. I can teach you those patterns myself.$b$bThey will come at a cost. Our clients are demanding, and our shop consumes a great amount of raw materials.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95945, 'enUS', 'OK, how much?', 'OK, how much?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95946, 'enUS', '', 'Someday I hope to see this \"Azeroth\" of yours. What an amazing world it must be.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95947, 'enUS', 'You are of no interest to me. I come from an accomplished lineage of Suramar tailors. You, obviously, do not.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95948, 'enUS', 'How fortuitous! A fellow tailor!$b$bWelcome to the leyweavers\' guild of Suramar. Perhaps you have heard of us?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95949, 'enUS', 'Hello, leyweaver. What can I do for you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95950, 'enUS', '|TInterface\\Icons\\spell_fire_ragnaros_lavaboltgreen.blp:20|t %s has marked you for |cFFF00000|Hspell:182108|h[Artillery]|h|r!', '|TInterface\\Icons\\spell_fire_ragnaros_lavaboltgreen.blp:20|t %s has marked you for |cFFF00000|Hspell:182108|h[Artillery]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95951, 'enUS', 'In-Game Cutscene', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95952, 'enUS', 'Stupid dragon! That\'ll cost you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95953, 'enUS', 'I... will... return...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95954, 'enUS', '', 'Is everyone alright?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95955, 'enUS', 'Singed, but alive. We lost many good troops.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95956, 'enUS', '', 'Steady! Hold this line or Menethil will fall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95957, 'enUS', '', 'The front lines are broken! Hold fast, I\'m sure reinforcements are coming!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95958, 'enUS', '', 'We\'ll have to mourn them later, we need to move before more demons arrive.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95959, 'enUS', 'Agreed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95960, 'enUS', 'Varian\'s forces should have landed just around this hill, let\'s hope they had more luck.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95961, 'enUS', '', 'I\'ve never seen the demons expand this quickly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95962, 'enUS', 'Neither have I. Something has changed, this isn\'t the same legion we\'ve fought before.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95963, 'enUS', 'How... quaint.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95964, 'enUS', 'Simple, but it has an antique charm to it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95965, 'enUS', 'Hey, watch this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95966, 'enUS', 'I do like a plunging neckline.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95967, 'enUS', 'Your hemming is impressive. This pattern, though... I could take it or leave it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95968, 'enUS', 'This should be fun!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95969, 'enUS', 'I really enjoy this pattern, $n. It has a classic feel to it, but the embroidery is so intriguing and unique.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95970, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ICONS\\ACHIEVEMENT_BOSS_ILLIDAN.blp:40|t$bYou are the most loyal of all the Illidari. You will be my eyes until this matter is finished.', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ICONS\\ACHIEVEMENT_BOSS_ILLIDAN.blp:40|t$bYou are the most loyal of all the Illidari. You will be my eyes until this matter is finished.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95971, 'enUS', 'Let\'s try to catch the wind!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95972, 'enUS', 'Faster! Faster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95973, 'enUS', 'You\'ll pay for that, dragon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95974, 'enUS', '', 'Oh no...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95975, 'enUS', 'Crash landing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95976, 'enUS', 'There, there, Feronas. The Warglaives of Kil\'Jaeden were created to destroy demons. You never stood a chance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95977, 'enUS', 'You are but the first to die. The rest your brethren will soon follow. I, Varedis Felsoul, will be your end.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95978, 'enUS', 'Feel my wrath!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95979, 'enUS', '', 'Greetings, traveler. Our glade has seen more peaceful times, but the Druids of the Claw offer what protection we can.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95980, 'enUS', 'This isn\'t possible. There are so many of them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95981, 'enUS', '', 'The streets of Dalaran are perfectly safe, I assure you. It is hardly surprising that someone might attack one of these... things.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95982, 'enUS', 'Ohh! That had to hurt!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95983, 'enUS', 'What a throw!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95984, 'enUS', 'You can\'t escape me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95990, 'enUS', 'For the Legion!', 'For the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95992, 'enUS', 'Your bones will break under my boot, $r!', 'Your bones will break under my boot, $r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95993, 'enUS', 'Demon! Test your might upon me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95994, 'enUS', 'Long live the Legion! Die you worthless $r!', 'Long live the Legion! Die you worthless $r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95996, 'enUS', 'Not so mighty now, are we?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96000, 'enUS', 'It is time to put an end to Varedis. I have arranged for a felbat to meet you at Krasus\' Landing to bring you to Felsoul Hold.', 'It is time to put an end to Varedis. I have arranged for a felbat to meet you at Krasus\' Landing to bring you to Felsoul Hold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96002, 'enUS', 'You are too late! The master\'s plan is already in motion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96004, 'enUS', 'Yes, yes! I will feed upon your fear, and twist it into a living nightmare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96010, 'enUS', 'My eyes... My eyes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96011, 'enUS', 'Hold fast, Alliance! We will not fall this day! Push them back to the portal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96012, 'enUS', 'Return Mannoroth... Yes, yes! Your masters call you to this world once more!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96013, 'enUS', 'Wha- What is this? Gul\'dan what have you done? I am... So weak...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96014, 'enUS', 'Fear the Legion\'s might!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96015, 'enUS', 'Fear not, Mannoroth. The fel gift empowers you... Make them suffer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96016, 'enUS', 'My power returns! I am unleashed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96017, 'enUS', 'Begone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96018, 'enUS', 'These mortals cannot be this strong. Gul\'dan, do something!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96019, 'enUS', 'Let the power flow through you. You are the destructor, the flayer of flesh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96020, 'enUS', 'I... AM... UNSTOPPABLE...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96022, 'enUS', 'Faithless fools. The Legion wills it; it will be done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96023, 'enUS', 'Tremble before the Legion\'s might!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96024, 'enUS', 'You dare challenge the Legion?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96025, 'enUS', 'I will not fall again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96026, 'enUS', 'Die, mongrels.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96027, 'enUS', 'Die, mongrels.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96028, 'enUS', 'Die, mongrels.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96029, 'enUS', 'Fall to your knees!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96030, 'enUS', 'Fall to your knees!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96031, 'enUS', 'The molten hand of the master will scourge this world clean!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96032, 'enUS', 'The Legion will burn this world, as it has countless others!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96033, 'enUS', 'Your flesh will burn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96034, 'enUS', 'Your end has come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96035, 'enUS', 'A worthy effort... but futile.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96036, 'enUS', 'A worthy effort... but futile.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96037, 'enUS', 'A worthy effort... but futile.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96038, 'enUS', 'Squirm... Scream!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96039, 'enUS', 'Squirm... Scream!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96040, 'enUS', 'Squirm... Scream!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96041, 'enUS', 'I am the destructor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96042, 'enUS', 'I am the destructor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96043, 'enUS', 'I am the destructor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96044, 'enUS', 'The Legion will find you... No matter where you flee...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96045, 'enUS', 'None can oppose us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96046, 'enUS', 'None can oppose us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96047, 'enUS', 'Your end has come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96049, 'enUS', '', 'Varian!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96050, 'enUS', 'Interested in building some ships?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96051, 'enUS', '', 'Here, try this pattern out. And use my materials! You\'ll find them lying around the shop.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96052, 'enUS', '', 'Now that you\'ve gathered the required materials, finish the pattern on my finishing table.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96053, 'enUS', '', 'What\'s that? What\'s happening with your thread? Why is the robe SMOKING?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96054, 'enUS', '', 'What a disaster. Look, the thread is just falling right out of the fabric. I\'ve never seen anything like this before.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96055, 'enUS', '', 'Ready yourself, hero. The creature descends!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96056, 'enUS', '', 'We did it! The pygmy owls of this grove are safe thanks to you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96057, 'enUS', '<Ga\'nar\'s spirit - so angry and ferocious in life - has at last found peace. He speaks to you with warmth.>$b$bGive \'em hell, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96058, 'enUS', 'We owe you everything! Surely that last push would have forced us underwater.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96059, 'enUS', 'These chests are full of supplies. You look like you might put them to better use than we will.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96060, 'enUS', 'Help! We\'re going to be pushed back into the sea!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96061, 'enUS', 'Oh thank the Light! We\'ve been holed up here, cut off from Menethil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96062, 'enUS', 'Here they come again! Steel yourselves! Help us fend them off!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96063, 'enUS', 'No... Not again!', 'No... Not again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96064, 'enUS', 'They never stop... there are so many of them!', 'They never stop... there are so many of them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96068, 'enUS', 'Commander. It IS you. Word of your deeds has traveled far and wide. Even the very winds whisper your name.$b$bCome. It is time. As the defender of this world, it is only fitting that Draenor\'s elements bestow their power upon you. Are you ready?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96069, 'enUS', '<Drek\'Thar bows deeply.>$b$bChampion, remember. Those who live with regrets are slaves to the past. Those who live with hope are ready for the future.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96070, 'enUS', 'I am ready, Farseer.', 'I am ready, Farseer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96071, 'enUS', 'They are here. Hurry! Gul\'dan demands we finish this...', 'They are here. Hurry! Gul\'dan demands we finish this...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96072, 'enUS', 'Take me to the Tanaan Jungle.', 'Take me to the Tanaan Jungle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96073, 'enUS', 'Varedis is well guarded. We need to take out the wards projecting that barrier.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96074, 'enUS', 'You\'ve been spotted. Push forward! Quickly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96075, 'enUS', 'There\'s no time to land. You must dive headlong into the fray. Go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96076, 'enUS', 'The Aegis of Aggramar belongs to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96077, 'enUS', 'You dare disturb me, weaklings? Your feeble minds cannot fathom the power of the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96078, 'enUS', 'Not... possible...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96079, 'enUS', 'You challenge me? I\'ll crush you like a bug!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96080, 'enUS', 'I will kill... you... ack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96081, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96082, 'enUS', 'You are not the only one seeking the Aegis. I suggest you hurry.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96083, 'enUS', '', 'It\'s no use, the ammunition has caught fire! Quickly, up the stairs!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96084, 'enUS', 'Allow me to dispose of them, master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96085, 'enUS', '', 'There are demon hunters on my world, doom commander. This is unacceptable.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96086, 'enUS', 'Defeat the other challengers and I will consider you for the trials.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96087, 'enUS', 'Bring me the remains of my fallen brother and I will allow you to enter the vault.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96088, 'enUS', 'Odyn, give me strength to do thy bidding.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96089, 'enUS', '', 'My spiders will feast upon your marrow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96090, 'enUS', '|TInterface\\Icons\\achievement_character_human_female.blp:40|tLining up the shot...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96091, 'enUS', 'Ha ha ha... You fight with purpose. That worthless orc would never be accepted among our ranks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96092, 'enUS', 'You are too late. Soon the Halls of Valor will belong to the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96093, 'enUS', 'I\'m going to enjoy ridding our new home of your kind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96094, 'enUS', '|TInterface\\Icons\\achievement_character_human_female.blp:40|tThis one\'s for Gilneas!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96095, 'enUS', '|TInterface\\Icons\\achievement_character_human_female.blp:40|tTaking him down...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96096, 'enUS', 'Ahh... much better. Odyn has a plan for you, my friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96097, 'enUS', 'Meet me inside the vault. Your trials are about to begin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96098, 'enUS', 'Get a move on it! I can\'t hold this forever.', 'Act quickly! This will only hold for a short time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96099, 'enUS', 'Your first trial begins now. Defend yourself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96100, 'enUS', 'I submit... I can see why Odyn favors you over the God-King. You may continue your trials.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96101, 'enUS', 'I am not your enemy, Mayor. I came here to find someone within Black Rook Hold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96102, 'enUS', 'Stop with your lies, night elf. You\'ll stay where we can keep an eye on you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96103, 'enUS', '<The blades hover before you.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96106, 'enUS', 'Let\'s go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96107, 'enUS', 'Genn, Jaina, it\'s good to see you safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96108, 'enUS', '|TInterface\\Icons\\achievement_boss_hellfire_felmagnaron.blp:20|tKormrok |cFFFF0000|Hspell:186882|h[Enrages]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96109, 'enUS', '', 'Do not let them enter the volcano. The key to the Legion\'s worlds must not fall into their hands, Beliash.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96110, 'enUS', 'Y\'know full well you lot should be inside. Come along!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96111, 'enUS', 'Aww...', 'Aww...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96112, 'enUS', '', 'This day will be your last!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96113, 'enUS', 'Gul\'dan is close! I will send you further into the citadel. We must stop him before this corruption spreads any further.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96114, 'enUS', 'Send me to the Hellfire Antechamber.', 'Send me to the Hellfire Antechamber.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96115, 'enUS', 'Send me to the Court of Blood.', 'Send me to the Court of Blood.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96116, 'enUS', 'Send me to the Nexus of Souls.', 'Send me to the Nexus of Souls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96117, 'enUS', 'Go on. And this time, stay there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96118, 'enUS', 'Send me to the Amphitheater of the Eternal.', 'Send me to the Amphitheater of the Eternal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96119, 'enUS', 'Grownups are so stupid.', 'Grownups are so stupid.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96120, 'enUS', 'Rascals. Don\'t worry: I\'ll keep an eye out for them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96129, 'enUS', 'You\'re mean!', 'You\'re mean!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96130, 'enUS', 'Yep.', 'Yep.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96131, 'enUS', 'Send me to The Black Gate.', 'Send me to The Black Gate.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96132, 'enUS', 'With your base fully constructed and your army in place, we can finally bring this campaign to a close. All that remains is the fortified bastion of Hellfire Citadel...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96134, 'enUS', '%s emerges from the shadows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96135, 'enUS', '', 'Now, now. They can\'t help being lively.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96136, 'enUS', 'Receiving Blessing', 'Receiving Blessing', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96137, 'enUS', 'My blades thirst for Legion blood. Do you know this thirst? Do you desire an end to the legion with every fiber of your being?\n\nDon\'t waste my time, nor yours. Every second the Legion persists is one too many.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96138, 'enUS', '<Throw the crystals into the pool.>', '<Throw the crystals into the pool.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96139, 'enUS', '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185828|h[Rekindles]|h|r a Fel Scorcher!', '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185828|h[Rekindles]|h|r a Fel Scorcher!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96140, 'enUS', '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185835|h[Enkindles]|h|r all nearby Fel Scorchers!', '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185835|h[Enkindles]|h|r all nearby Fel Scorchers!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96142, 'enUS', '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|tThe $n |cFFFF0000|Hspell:185824|h[Detonates]|h|r as it dies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96144, 'enUS', 'I\'m glad that you\'re here, $n.\n\nNow, let\'s be about our business.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96145, 'enUS', 'My eredar are summoning in more demons to attack them from behind, your highness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96146, 'enUS', '', 'Deal with these insects, Beliash.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96148, 'enUS', 'What\'s this?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96149, 'enUS', 'This is a titan construct! It must have been guarding the prize that Sylvanas seeks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96150, 'enUS', 'It seems that we are again a step behind. We must move quickly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96151, 'enUS', 'This world is full of kindling... a storm of fire is upon you, mortals!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96152, 'enUS', 'The shadows will envelop you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96153, 'enUS', 'Behind the veil, all you find is death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96154, 'enUS', '[PH] Sylvanas narrowly escapes, leaving you to fight her newest dark Val\'kyr!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96155, 'enUS', 'Now you dwell in darkness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96156, 'enUS', 'The only truth is your death.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96157, 'enUS', 'Terokk\'s lies are proven false!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96158, 'enUS', 'Curses! The banshee witch escaped! Greymane will not be pleased...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96159, 'enUS', 'And now, the Veil covers all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96160, 'enUS', 'Let us dispatch of this infernal machine. At least we may be able to prevent Sylvanas from creating a new army of Val\'kyr...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96161, 'enUS', '[PH] Crowley rigs explosives to the machine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96162, 'enUS', 'Briefing', 'Briefing', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96163, 'enUS', 'I\'ll return to camp. You bring word of our findings to Greymane. Hurry, $c, this place is about to go down on our heads!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96164, 'enUS', 'I took a pretty bad hit up there... my bird\'s still in good shape though. Bag a few Demons for me, eh?', 'I took a pretty bad hit up there... my bird\'s still in good shape though. Bag a few Demons for me, eh?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96166, 'enUS', '', 'Got \'em in my sights!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96167, 'enUS', '', 'I see \'em. Good job with those stun grenades, $Glad:lass;!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96168, 'enUS', '', 'No way I\'ll miss this shot!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96169, 'enUS', '', 'Got that one, clear as day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96170, 'enUS', '', 'Pow! Right in the kisser!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96171, 'enUS', '', 'Somebody order a bullseye?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96172, 'enUS', '', 'Headshot. Easy as pie.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96173, 'enUS', '', 'Like shootin\' fish in a bloody barrel.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96174, 'enUS', 'Do you think you have won? You know nothing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96175, 'enUS', 'Witness true power!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96176, 'enUS', 'The Veil can not hide my might!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96177, 'enUS', 'Terokk can not protect you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96178, 'enUS', 'You had better learn to fly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96179, 'enUS', 'The winds will punish you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96180, 'enUS', 'It\'s a long way down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96181, 'enUS', '', 'Here, come closer and let me have a good look at you.$B$BAh! A $c, all the way out in these parts? My goodness!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96182, 'enUS', 'After years of heroism, your life is snuffed out by a Hellblaze Imp.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96183, 'enUS', 'Your last moments are agony as you fall before the Hellblaze Fiend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96184, 'enUS', '', 'The last thing you see is a Hellblaze Mistress laughing over your corpse.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96185, 'enUS', 'We must go to Light\'s Hope Chapel at once. Highlord Fordring deserves to be put to rest amongst the ancient heroes of the Light buried there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96186, 'enUS', '', 'What\'s our status, Orc?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96187, 'enUS', 'With his final breath, Khadgar unleashes a mana explosion that decimates everyone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96188, 'enUS', '<This is a tub suitable for dyeing fabric.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96189, 'enUS', 'Fill the tub with amber dye.', 'Fill the tub with amber dye.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96190, 'enUS', 'Dye the silkweave.', 'Dye the silkweave.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96191, 'enUS', '<This is a tub suitable for dyeing fabric. The pattern you are working on requires a combination of green dye and brimstone shavings.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96192, 'enUS', 'Fill the tub with green dye and brimstone shavings.', 'Fill the tub with green dye and brimstone shavings.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96193, 'enUS', 'Tanithria at the tailoring shop needs some brimstone shavings for a pattern.', 'Tanithria at the tailoring shop needs some brimstone shavings for a pattern.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96194, 'enUS', 'Brimstone? What could she possibly want THAT for? Gah, tailors. Here you go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96195, 'enUS', '', 'You should start by filling the dyeing tub. Green dye\'s on the shelf, but you\'ll have to get the brimstone shavings from Alard Schmied in the blacksmithing shop.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96196, 'enUS', 'That will be all for now. Go on, then.', 'That will be all for now. Go on, then.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96197, 'enUS', 'You will be missed, brother.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96198, 'enUS', 'Highlord Tirion Fordring\'s last light fades.', 'Highlord Tirion Fordring\'s last light fades.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96199, 'enUS', 'Greetings Commander!  We have lots of work to do before we get this shipyard operational!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96200, 'enUS', 'Talk to me about naval missions.', 'Talk to me about naval missions.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96201, 'enUS', 'Controlling our fleet is no easy task, Commander.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96202, 'enUS', 'I almost lost the fight against the fel energy inside me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96203, 'enUS', 'Over here, Commander!  Let\'s get started on these naval missions!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96205, 'enUS', 'Communing With Ashildir', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96206, 'enUS', 'Before you stands a statue depicting the leader of the Shieldmaidens in Skold-Ashil. \n\nHer features look familiar...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96207, 'enUS', 'Attempt to commune with the statue.', 'Attempt to commune with the statue.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96208, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96210, 'enUS', 'The power of Xavius is too potent. I do not know what we can do against his Nightmare.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96211, 'enUS', 'The corruption of the Nightmare has seeped into the waters of the river.$B$BStand by, $c. I will call upon the power of Elune and purge this river.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96212, 'enUS', 'I hope this works, druid.', 'I hope this works, druid.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96213, 'enUS', 'Just a few more minutes here...  She will be seaworthy shortly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96214, 'enUS', 'The Portal Lord collapses, leaving its portal exposed! (Click the portal, glow is broken)', 'The Portal Lord collapses, leaving its portal exposed! (Click the portal, glow is broken)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96215, 'enUS', '', 'The Legion is invading the research station! Heroes, rally with me. We must protect the Kirin Tor at all costs! [PH]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96216, 'enUS', '', 'The Fel Lord has fallen, and the legion has retreated! We have pushed them back for now, but we must stay vigilant. [PH]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96217, 'enUS', '[PH] Ashildir tells you about the Val\'kyr.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96218, 'enUS', 'Penelope? Thank goodness you\'re all right! I was so worried!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96219, 'enUS', '', 'Papa, papa, I\'m home! $GMister:Miss; $p saved me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96220, 'enUS', 'We was supposed to be off hours ago... at least we have these brews, eh?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96221, 'enUS', 'A few more o\' these and I might even join this fight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96222, 'enUS', 'You will interfere no longer, mortals. Surrender the relic, or the Legion will burn you to ash and pluck it from your cinders! [PH]', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96223, 'enUS', '', 'I do hope they are still alive. It would break the Mayor\'s heart if we couldn\'t find his little girl.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96224, 'enUS', '', 'Use that flare gun to mark the birds. That\'ll daze \'em long enough for me to take \'em out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96225, 'enUS', 'Cower before the might of the Legion!', 'Cower before the might of the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96226, 'enUS', 'Our numbers are infinite!', 'Our numbers are infinite!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96227, 'enUS', 'The relic shall be ours!', 'The relic shall be ours!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96228, 'enUS', '', 'We can handle the defense of the research station from here. Heroes, close the portals to cut off their line of attack!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96229, 'enUS', '', 'Face your judgment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96232, 'enUS', 'The Light will never leave your side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96233, 'enUS', '', 'Bask in the glory of the Light.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96234, 'enUS', 'We stand united!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96235, 'enUS', 'If it breathes, put it out of its misery.', 'If it breathes, put it out of its misery.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96236, 'enUS', 'These lands will fall to our hand.', 'These lands will fall to our hand.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96237, 'enUS', 'Pathetic! Do these lands offer no challenge?', 'Pathetic! Do these lands offer no challenge?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96238, 'enUS', 'The world crumbles under the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96239, 'enUS', 'Fragile beings serve no purpose under the Legion!', 'Fragile beings serve no purpose under the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96240, 'enUS', 'Hope is your only enemy.', 'Hope is your only enemy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96241, 'enUS', 'No presence is greater, mortals. In time you will see...', 'No presence is greater, mortals. In time you will see...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96242, 'enUS', 'For centuries you have acted as uninvited guests in OUR world.', 'For centuries you have acted as uninvited guests in OUR world.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96243, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96244, 'enUS', '', 'They come for us! They will not find what they seek...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96245, 'enUS', '', 'Gurtogg! Dispatch our enemies...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96246, 'enUS', '', 'The sweet mercy... of death.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96247, 'enUS', '', 'How it struggles when death is near!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96248, 'enUS', '', 'It has fallen. It is mine now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96249, 'enUS', '', 'They have been marked.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96250, 'enUS', '', 'They will carry the darkness with them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96251, 'enUS', '', 'It rips. It tears. It is boundless and eternal...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96252, 'enUS', '', 'The void, it hungers!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96253, 'enUS', '', 'And then the dead shall rise once more.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96255, 'enUS', '', 'The Alliance will be upon us soon, and we have nowhere to run.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96256, 'enUS', 'Iron Horde will CRUSH you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96257, 'enUS', 'ArRRgghhHhh...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96258, 'enUS', 'ArRRgghhHhh...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96259, 'enUS', 'ArRRgghhHhh...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96260, 'enUS', '', 'Do not question me, Nathanos. Now go - make for the Eternity, and bring this hero with you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96261, 'enUS', 'Tell us your plans, imp!', 'Tell us your plans, imp!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96262, 'enUS', 'My Queen, your safety must be our first concern. Surely we can leave a few ships behind to-', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96263, 'enUS', 'Yes, of course. Come along... \"hero.\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96264, 'enUS', 'Me scratch you to bits!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96265, 'enUS', '', 'Organize our defenses beginning with the rear. I want bat-riders in the air and catapults at the ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96266, 'enUS', 'We know you\'re after something. Out with it!', 'We know you\'re after something. Out with it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96267, 'enUS', 'Yes, Dark Lady.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96268, 'enUS', 'Time to feast!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96269, 'enUS', 'Crush you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96270, 'enUS', 'Crush you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96271, 'enUS', 'Drink your blood! Eat your flesh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96272, 'enUS', 'I\'ll rip the meat from your bones!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96273, 'enUS', 'More! I want MORE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96274, 'enUS', 'My blade seeks vengeance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96275, 'enUS', 'To ash...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96276, 'enUS', 'To cinders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96277, 'enUS', 'I walk in shadow...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96278, 'enUS', 'Burn... BURN!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96279, 'enUS', 'A map of the Broken Isles showing the current status of your forces.', 'A map of the Broken Isles showing the current status of your forces.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96280, 'enUS', 'Investigate the map.', 'Investigate the map.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96281, 'enUS', 'No... The taint of Xavius is too strong.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96282, 'enUS', 'Who are you? Stay back - I will protect the Queen with my last breath!', 'Who are you? Stay back - I will protect the Queen with my last breath!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96283, 'enUS', 'Queen Azshara... betrayed us...', 'Queen Azshara... betrayed us...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96284, 'enUS', '', 'I was drawn back to this place, and for a moment I mistook you for the enemy. Be careful, hero. This place reeks of restless spirits.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96289, 'enUS', 'Foul demons! You are no match for the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96290, 'enUS', 'Tirion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96291, 'enUS', 'Have a go with me mortar. I\'ll spot for ye!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96292, 'enUS', 'Master, I fight in your name!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96293, 'enUS', 'Lord Ravencrest died long ago, but I will always revere him as my mentor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96294, 'enUS', 'It appears we both have been wandering for quite some time. Would you care for some company?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96295, 'enUS', 'Glad to see you out here, $n. Care for a lift?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96296, 'enUS', 'These are not parts one should wander alone, $n. Allow me to offer you safe travel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96297, 'enUS', '', 'Lord Ravencrest appointed me as Queen Azshara\'s personal guard.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96298, 'enUS', '', 'The tower beckons, but I fear its call. Hurry, free us before it\'s too late!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96299, 'enUS', '[WALKS FORWARD.] Wait, what is this? His tomb has been disturbed! His ashes are gone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96300, 'enUS', 'Greetings, and thank you for helping to test patch 6.2!\n\nI can teleport you directly into Hellfire Citadel or Tanaan Jungle. This is just a teleport, not a match-making service, so I hope you have a party ready to tackle the challenges waiting inside. If not, you might want to check out our new Group Finder feature to find some allies.\n\nMake sure you have selected the difficulty you want to test before teleporting, since you may not be able to zone out easily if you need to change the difficulty setting!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96301, 'enUS', 'I want to go to Hellfire Citadel (Raid).', 'I want to go to Hellfire Citadel (Raid).', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96302, 'enUS', 'I want to go to Tanaan Jungle.', 'I want to go to Tanaan Jungle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96303, 'enUS', '', 'Entrap them, my children.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96304, 'enUS', 'Activate all three gateways and summon in the rest of our forces.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96305, 'enUS', '', 'Kill them all!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96306, 'enUS', '', 'Follow me, Highlord. You\'ll use the command map from now on to delegate your forces.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96307, 'enUS', '', 'Yes? How might I help you, $n?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96308, 'enUS', 'The Ashbringer... it\'s been a long time since I\'ve seen that blade. You\'re doing the Light\'s work defending our world, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96309, 'enUS', 'These lands are covered with shrines of power that can make the Ashbringer even more powerful. Seek them out on your travels and you will truly become a force to be reckoned with.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96310, 'enUS', 'Walk always with the Light, Highlord, and it will never forsake you. Take my blessing, and you will never truly stand alone. Farewell.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96311, 'enUS', 'How DARE you enter MY sanctuary! You\'ll deal with me now!', 'How DARE you enter MY sanctuary! You\'ll deal with me now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96312, 'enUS', 'Insolence! The Legion won\'t stand for this!', 'Insolence! The Legion won\'t stand for this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96313, 'enUS', 'Begone, pests! It is time you met your end!', 'Begone, pests! It is time you met your end!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96314, 'enUS', '', 'Greetings keeper of the <artifact>. I speak for the Skyhorn Tribe.$b$b', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96315, 'enUS', 'Will the Skyhorn join with Mayla Highmountain?', 'Will the Skyhorn join with Mayla Highmountain?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96316, 'enUS', 'It\'s time we reclaim our lands!', 'It\'s time we reclaim our lands!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96317, 'enUS', 'Thanks to the failure of Highmountain, the harpies have become emboldened and assault my people. The Skyhorn cannot fight a battle on two fronts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96318, 'enUS', 'Their shields are too strong! Protectors, at your will!', 'Their shields are too strong! Protectors, at your will!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96319, 'enUS', 'So you are the one who carries <artifact>. I thought you would be taller.$b$bYou stand before Speaker Ragebull.$b$bI am the voice for the great Bloodtotem Tribe, strength of the earth and the most powerful warriors Highmountain has ever known.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96320, 'enUS', 'Will the Bloodtotem join with Mayla Highmountain?', 'Will the Bloodtotem join with Mayla Highmountain?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96321, 'enUS', 'Ha! Highmountain cannot even defend its own chieftain and you would have us join with them? The Bloodtotem honor strength, not victims.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96322, 'enUS', 'The bearer of <artifact>? This is a great honor.$b$bI speak for the Whitewater Tribe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96323, 'enUS', 'Will the Rivermane join with Mayla Highmountain?', 'Will the Rivermane join with Mayla Highmountain?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96325, 'enUS', '', 'It is with a heavy heart I must decline. The drogbar assault my lands. I am here to ask for aid and yet Mayla has none to give.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96334, 'enUS', '$r creaks and moans, gathering its strength as it prepares for battle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96335, 'enUS', '%s picks up the Wraith Zapper and checks its energy reserves. Good to go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96336, 'enUS', '', 'Well done, hero. Now let us regroup before our enemy is able to.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96337, 'enUS', '<Apparently this brave soul perished moments after trapping something dangerous.>$B$B<Her gun lies nearby. Dare you pick it up and investigate further?>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96338, 'enUS', 'These birds are comin\' from Black Rook Hold. Fourth one I\'ve shot today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96339, 'enUS', '', 'What manner of beast IS this?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96340, 'enUS', 'Sentinels, let\'s give them cover!', 'Sentinels, let\'s give them cover!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96342, 'enUS', '', 'Windgore! Attack this infidel!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96344, 'enUS', 'I want these tauren off my mountain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96351, 'enUS', 'Just inspect the anvil when yer ready to peruse yer options. Take yer time, it\'s nae a decision to rush through!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96353, 'enUS', '', 'You know, they used to call me \"Mean Marlene.\" I could shoot the head off a nail at hundred paces.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96355, 'enUS', 'My daughter is everything to me. I can\'t thank you enough for bringing her home, safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96356, 'enUS', 'Sharpen your blades, warriors! No demon shall pass through our gates.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96357, 'enUS', 'The master commands us...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96358, 'enUS', 'Go to your grave!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96359, 'enUS', 'Join us in eternity...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96360, 'enUS', 'The Hold shall not fall...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96361, 'enUS', 'Dang it! Jammed again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96362, 'enUS', 'Demons won\'t even see it coming!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96363, 'enUS', 'Right between the eyes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96364, 'enUS', 'Did you think to reclaim this land? What we take, we shall keep.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96365, 'enUS', 'Excellent choice, if I do say so myself. We\'ll need a much stronger anvil to upgrade the blade further, but I\'ll find one. You have my word!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96366, 'enUS', 'Demons... hah! Demons don\'t scare me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96367, 'enUS', 'I may be small, but I ain\'t scared of nothin\'!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96368, 'enUS', 'Come and get some, demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96369, 'enUS', 'You discard the Wraith Zapper as you leave the hilltop.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96370, 'enUS', 'It\'s pretty bad up here over the Hill! I\'ll hold the skies as long as I can.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96371, 'enUS', 'Don\'t worry friends. I\'ve got the skies over Sentinel Hill covered!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96372, 'enUS', 'Sorry, demons! The skies over Sentinel Hill belong to us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96373, 'enUS', 'You dare challenge me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96374, 'enUS', 'The skies above Sentinel Hill seem to be thinning out. I\'ll continue my patrols until we\'re sure it\'s safe!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96375, 'enUS', 'It appears to be over, friends! I\'ll keep watch over the Hill a while longer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96377, 'enUS', 'Don\'t blink when you shoot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96378, 'enUS', '', 'Our battle against the harpies is ongoing, but your actions have helped restore some balance to the basin. \n\nWith Morashu\'s spirit reborn, he will once again be able to guide other animals back to nature.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96379, 'enUS', 'Behold, the hilt of Frostmourne. From its broken form you will create a blade without equal! Approach and channel your power into it to create your weapon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96380, 'enUS', 'Good.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96381, 'enUS', 'Excellent shot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96382, 'enUS', 'To arms, men!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96383, 'enUS', 'Move into positions!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96385, 'enUS', 'Heard any pet battle news out of Tanaan Jungle?', 'Heard any pet battle news out of Tanaan Jungle?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96386, 'enUS', '', 'I\'ve heard rumors of no less than 15 very powerful pets, corrupted by the fel energies unleashed by Gul\'dan. Find and defeat them and you\'ll surely find some rewards of value!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96390, 'enUS', 'Please... the others! We were separated... you have to save them!', 'Please... the others! We were separated... you have to save them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96391, 'enUS', 'Oh thank you. Thank you, hero!', 'Oh thank you. Thank you, hero!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96392, 'enUS', 'Why is this happening to us? What are these things?', 'Why is this happening to us? What are these things?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96393, 'enUS', 'Stay calm, everyone. Reinforcements are here. We will protect you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96394, 'enUS', 'We won\'t let them through! You\'re safe, so long as you stay here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96395, 'enUS', 'We don\'t feel safe! What\'s going on out there?', 'We don\'t feel safe! What\'s going on out there?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96397, 'enUS', 'Everything gone... just like that. How can you hope to stop them?', 'Everything gone... just like that. How can you hope to stop them?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96400, 'enUS', 'Is that help I hear? Inside, please hurry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96401, 'enUS', 'So weak. So delicious! Me feed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96402, 'enUS', 'Oh thank the Light! Please, you must stay here to protect us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96403, 'enUS', '', 'Father, look out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96404, 'enUS', '', 'I will join you. Let\'s kill some more demons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96405, 'enUS', '', 'I... will not... give in!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96406, 'enUS', 'Enough! I will reap your souls myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96407, 'enUS', 'So weak... hard to contain... my energy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96408, 'enUS', '<The construct sits motionless on the ground.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96409, 'enUS', '<Insert 1000 Apexis Crystals.>', '<Insert 1000 Apexis Crystals.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96410, 'enUS', 'Repairing the construct will cost 1000 Apexis Crystals. Are you sure?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96411, 'enUS', 'I will destroy all of you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96412, 'enUS', 'Lemme hear your bones crunch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96413, 'enUS', 'We\'re takin\' the mountain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96414, 'enUS', 'Gonna enjoy guttin\' you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96415, 'enUS', '', 'The pain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96416, 'enUS', 'These are drogbar rocks! Drogbar stones!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96418, 'enUS', 'It appears to be in working order. Why don\'t you take a look and see what it can do?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96419, 'enUS', '', 'Help has arrived at last! Elune be praised!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96420, 'enUS', 'Tremble beneath the might of the Burning Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96421, 'enUS', 'Enjoy this moment, mortals... It shall be your last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96422, 'enUS', 'At long last... destruction has come to this pathetic world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96423, 'enUS', 'Yes... struggle... your meager attempts to survive amuse me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96424, 'enUS', 'None shall awake from the Nightmare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96425, 'enUS', 'Rend the flesh from their bones... and drain the life from their mortal bodies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96426, 'enUS', 'Pray to your pathetic goddess... let her listen to your cries of agony!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96427, 'enUS', 'Shaladrassil... it cannot... it must not be true!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96428, 'enUS', 'I sense greater power within you, $n. Have you stolen a demon\'s essence?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96429, 'enUS', 'I would have perished without your aid!', 'I would have perished without your aid!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96430, 'enUS', 'I\'m ready.', 'I\'m ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96431, 'enUS', 'It\'s as if the forest itself has risen against us...', 'It\'s as if the forest itself has risen against us...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96432, 'enUS', 'You have my thanks!', 'You have my thanks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96433, 'enUS', 'I will defend this forest to my dying breath!', 'I will defend this forest to my dying breath!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96434, 'enUS', 'Hope is lost... but I will fight on!', 'Hope is lost... but I will fight on!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96435, 'enUS', '|TInterface\\Icons\\spell_fire_rune.blp:20|tThe |cFFFF0000|Hspell:187658|h[Empowered Explosive Rune]|h|r detonates one last time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96436, 'enUS', 'Thank you!', 'Thank you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96437, 'enUS', 'How kind of you.', 'How kind of you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96438, 'enUS', 'Most appreciated.', 'Most appreciated.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96439, 'enUS', 'I hope to someday repay the favor!', 'I hope to someday repay the favor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96440, 'enUS', 'You\'ve been a great help, $n. Thank you.', 'You\'ve been a great help, $n. Thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96441, 'enUS', '', '$n, over here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96442, 'enUS', 'Beware, brothers! We are discovered!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96443, 'enUS', 'I\'ll handle this interference personally.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96444, 'enUS', 'This... means nothing. You... are... nothing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96445, 'enUS', 'We shall rise again... more numberless than the stars.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96446, 'enUS', 'You fight for a world... that is... already lost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96447, 'enUS', 'The Nightmare is no mere illusion. It was born... from your darkest... truths.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96448, 'enUS', 'Cowards and weaklings! You deserve to burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96449, 'enUS', 'This ends now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96450, 'enUS', '', 'No!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96451, 'enUS', 'Eek! They take candle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96452, 'enUS', 'Is my candle! Why take?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96453, 'enUS', 'Keep calm, fools! Finish defiling the shrine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96454, 'enUS', 'Taking', 'Taking', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96455, 'enUS', 'Shhh... Quit tryin\' ta attract \'em ta me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96456, 'enUS', 'Find yer own hidin\' spot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96457, 'enUS', 'Come and get me when it\'s o\'er!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96458, 'enUS', 'Quit makin\' all that racket! You\'ll draw their attention!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96459, 'enUS', 'Spots claimed! Now if ya don\' mind... tryin\' ta keep me location a secret!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96460, 'enUS', 'It burns!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96461, 'enUS', 'Noooo!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96462, 'enUS', 'Finally, flesh worthy of my pyre!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96463, 'enUS', 'Protocol: Soul Harvest is active!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96464, 'enUS', 'Soul detected. Reaping in progress.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96465, 'enUS', 'Harvesting... unsuccessful.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96466, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ENCOUNTERJOURNAL\\UI-EJ-BOSS-King Ymiron.blp:50:80|t$bAre you strong enough to wield Magnar Icebreaker\'s weapons? We shall see...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96467, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ENCOUNTERJOURNAL\\UI-EJ-BOSS-King Ymiron.blp:50:80|t$bFinally someone worthy of Magnar Icebreaker\'s legacy.$bIf you would claim his legendary weapons, follow... and survive...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96468, 'enUS', 'A 7th Legion Battlemage freezes the catapult solid!', 'A 7th Legion Battlemage freezes the catapult solid!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96469, 'enUS', 'You do not look well, $n... are you alright?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96470, 'enUS', '$n, my thanks for coming..', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96471, 'enUS', '|TInterface\\Icons\\spell_shadow_auraofdarkness:20|t%s begins to cast |cFFFF0000|Hspell:187466|h[Supreme Doom]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96472, 'enUS', 'It is very good to see you, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96473, 'enUS', 'I\'m ready to go to Magnar\'s island.', 'I\'m ready to go to Magnar\'s island.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96474, 'enUS', 'DO NOT TRANSLATE', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96475, 'enUS', 'DO NOT TRANSLATE', '', 26124);
