INSERT INTO `broadcast_text_locale` VALUES (96479, 'enUS', '|TInterface\\Icons\\spell_nature_astralrecalgroup:20|t%s casts |cFFFF0000|Hspell:187702|h[Twisted Reflection]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96480, 'enUS', 'Finally we get some help around here. That is, if you\'re willing to put aside past differences, for now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96481, 'enUS', 'Let\'s team up. If we fight, we only help the demons.', 'Let\'s team up. If we fight, we only help the demons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96482, 'enUS', 'I don\'t work with murderers. The Defias are never to be trusted!', 'I don\'t work with murderers. The Defias are never to be trusted!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96483, 'enUS', 'You high and mighty types are worse than demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96484, 'enUS', 'Now... who is the murderer?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96485, 'enUS', '', 'What is this? The hero of Helheim seeks entrance to the vault?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96486, 'enUS', '', 'I know your worth. I will grant you an audience with Eyir.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96487, 'enUS', '', 'You may proceed, but know that Eyir may not regard you as I do.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96488, 'enUS', '', 'Seek your fate in the vault, $n. May Eyir be your wings.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96489, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ICONS\\Ability_Warlock_EmpoweredImp.blp:50:80|tI smell power behind this door! Let\'s dig it up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96490, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ICONS\\Ability_Warlock_EmpoweredImp.blp:50:80|tOuch ouch! I must get help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96491, 'enUS', 'Ladies and gentlemen, please! Please! We are doing all we can to address the situation.', 'Ladies and gentlemen, please! Please! We are doing all we can to address the situation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96492, 'enUS', 'Rest assured, Sentinel Hill is well protected!', 'Rest assured, Sentinel Hill is well protected!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96493, 'enUS', 'The Legion will not penetrate our defenses. I assure you!', 'The Legion will not penetrate our defenses. I assure you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96494, 'enUS', 'Please, listen a moment. We are forcing them back as we speak!', 'Please, listen a moment. We are forcing them back as we speak!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96495, 'enUS', 'Ladies and gentlemen, we are safe and secure within the walls. There is no need for panic.', 'Ladies and gentlemen, we are safe and secure within the walls. There is no need for panic.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96498, 'enUS', 'DO NOT TRANSLATE', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96499, 'enUS', 'The bold fools are boarding my ship. Come on!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96500, 'enUS', 'Transport', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96501, 'enUS', 'Carrier', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96502, 'enUS', 'Destroyer', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96503, 'enUS', '', 'You defile these grounds!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96504, 'enUS', '', 'Eyir take you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96505, 'enUS', '', 'Eyir, guide my spear!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96506, 'enUS', '', 'You are not worthy of ascension!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96507, 'enUS', '', 'My shield will not falter!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96508, 'enUS', '', 'You are but another test!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96509, 'enUS', '', 'Sisters, to battle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96510, 'enUS', '', 'Eyir, be my wings...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96511, 'enUS', '', 'The Halls of Valor await...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96512, 'enUS', '', 'It was... a glorious death...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96513, 'enUS', '', 'My shield remains... unbroken...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96515, 'enUS', '', 'The battle is upon us, sisters!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96516, 'enUS', '', 'Protect the temple!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96517, 'enUS', '', 'Break upon my shield!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96518, 'enUS', '', 'The temple must not fall...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96519, 'enUS', '', 'Eyir... I have failed...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96521, 'enUS', 'Battleship', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96522, 'enUS', 'Submarine', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96523, 'enUS', 'We must take the fight to them. But it would be rude to show up without a gift... The apothecaries will have just what we need.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96525, 'enUS', 'Transport', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96526, 'enUS', 'Submarine', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96527, 'enUS', 'Battleship', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96528, 'enUS', 'Destroyer', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96529, 'enUS', 'Carrier', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96533, 'enUS', 'It is time to pass my legacy on to someone powerful enough to wield them. Survive my trial and you will be given the strongest sword and shield this world has ever seen...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96535, 'enUS', 'What are the Armaments of the Black Wyrm?', 'What are the Armaments of the Black Wyrm?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96537, 'enUS', 'Do you know of the Earthwarder, Neltharion? He was a glorious beast before he... fell. He was invulnerable to all but the gods in that age. Magnar worshipped him in his youth.\n\nEven though he was a venerated warrior Magnar wanted to be like him, invulnerable. His plan was arrogant. He would storm the nest of Neltharion himself and take something from his legendary hoard.\n\nMagnar and his warband fought Neltharion\'s younglings in a fierce battle. They had just made the nest when the dragon returned. With one breath he wiped away entire force. Magnar grabbed one of his cast off scales, used it to shield himself, and was blown down the mountain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96538, 'enUS', 'So he left with nothing but a scale?', 'So he left with nothing but a scale?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96539, 'enUS', 'Nothing but a scale? Hah! A scale from the most hard to kill dragon in the world!\n\nMagnar realized what he had and spent years learning the forge under masters both titanforged and vrykul. When he was finally skilled enough, he crafted from the scale a shield and sword that have never seen an equal. Those are the Armaments of the Black Wyrm. In the right hands they project the strength and invulnerability of the Earthwarder himself.\n\nTo this day I am still unsure if he was lucky to survive that encounter or if the Black Wyrm judged him worthy enough to live.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96541, 'enUS', 'Xhul\'horac targets you with |cFFFF0000|Hspell:186407|h[Fel Surge]|h|r!', 'Xhul\'horac targets you with |cFFFF0000|Hspell:186407|h[Fel Surge]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96542, 'enUS', 'Filthy worgen... A beast in a hat is still a beast.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96543, 'enUS', 'Dread-Riders, grab as many vials as you can carry and form up on me! We are bringing down that gunship.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96546, 'enUS', '', 'I\'ve heard rumors of no less than 15 very powerful pets, corrupted by the fel energies unleashed by Gul\'dan. Find and defeat them and you\'ll surely find some rewards of value!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96547, 'enUS', 'Heard any pet battle news out of Tanaan Jungle?', 'Heard any pet battle news out of Tanaan Jungle?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96550, 'enUS', 'I am ready to begin the trial.', 'I am ready to begin the trial.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96551, 'enUS', 'That was an exhilarating kill, one that will make a fine chapter in my next novel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96552, 'enUS', 'Xhul\'horac targets you with |cFFFF0000|Hspell:186333|h[Void Surge]|h|r!', 'Xhul\'horac targets you with |cFFFF0000|Hspell:186333|h[Void Surge]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96553, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ENCOUNTERJOURNAL\\UI-EJ-BOSS-King Ymiron.blp:50:80|t$bYou are the one, bring them glory and they will bring glory to you. I will finally take my place in the Halls of Valor, where the brave may fight forever.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96554, 'enUS', 'New directives: Serve Legion. Destroy property.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96555, 'enUS', 'Slash. Burn. Harvest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96556, 'enUS', 'A |cFFFF0000|Hspell:186407|h[Shadowfel Annihilation]|h|r detonates on $n!', 'A |cFFFF0000|Hspell:186407|h[Shadowfel Annihilation]|h|r detonates on $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96558, 'enUS', '', 'I may carry a book instead of a rifle, but I assure you $n, I\'m a hunter at heart.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96559, 'enUS', '<Despite a heavy cloth across his eyes, the wizened seer gazes up at the skies with a contemplative look.>$B$BThe stars shape our destinies, child, granting their blessing to the worthy and shunning the weak.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96560, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ENCOUNTERJOURNAL\\UI-EJ-BOSS-Selin Fireheart.blp:50:80|t$b[Kayn Sunfury] Do not hold back, $n. Show him your wrath.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96561, 'enUS', 'Darksorrow and Ravencrest will have the honor of accompanying you into Suramar, $p. A small force will have the best chance of flying undetected into the hold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96562, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ICONS\\Warlock_Summon_DoomGuard.blp:50:80|t$bI am here Pillik! What insect scared you off?!? Come out of there insect or I will bring everything down upon you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96563, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ICONS\\Warlock_Summon_DoomGuard.blp:50:80|t$bWe didn\'t kill all of you at the Broken Shore? I shall correct that mistake now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96564, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ENCOUNTERJOURNAL\\UI-EJ-BOSS-ARCHIMONDEWOD.blp:50:80|t$B[Taldath the Destroyer] Insolent whelps! Release me now and I may yet spare you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96565, 'enUS', 'FOOL! You dare face me alone?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96566, 'enUS', 'Varedis will hunt down every one of your disgusting ilk. Pity you won\'t be around to witness it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96567, 'enUS', 'The Twinblades... will be your end...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96568, 'enUS', 'Vermin! My lord wields power beyond your comprehension! He will end you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96569, 'enUS', 'The smallest touch adds a just enough.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96570, 'enUS', 'Ingredients as far as the eye can see...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96571, 'enUS', 'Elixirs require such a delicate touch.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96572, 'enUS', 'Potions by day. Potions by night.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96573, 'enUS', 'A touch of this for ailments... or double for a more... sinister use, perhaps.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96574, 'enUS', 'Mmm... such a pleasant aroma.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96575, 'enUS', 'Hold your ground! Show them we will not be broken by such petty sorcery.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96576, 'enUS', 'This one met a grisly end. No sign of Maiev here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96577, 'enUS', '\"Despite our attempts to extract information, the Warden remains stubbornly resistant.\" Could they be talking about Maiev?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96578, 'enUS', 'That\'s Maiev\'s weapon! She was here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96579, 'enUS', '', 'Eyir, mother of the Val\'kyr, I offer you this one chance to submit willingly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96580, 'enUS', 'When the ancient war began, we suffered countless losses. Our cities burned.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96581, 'enUS', 'Lord Ravencrest saved our people and rescued our world from annihilation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96582, 'enUS', 'His loss was tragic for us all. We owe him... everything.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96583, 'enUS', 'If you\'ve come for the warden, you\'re too late!', 'If you\'ve come for the warden, you\'re too late!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96584, 'enUS', 'Lord Ravencrest, I have served you loyally.', 'Lord Ravencrest, I have served you loyally.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96585, 'enUS', 'My loyalty to Lord Ravencrest will not falter.', 'My loyalty to Lord Ravencrest will not falter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96586, 'enUS', 'In death, I see the truth. You are no enemy.', 'In death, I see the truth. You are no enemy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96587, 'enUS', 'You shall never reach Lord Ravencrest\'s tower!', 'You shall never reach Lord Ravencrest\'s tower!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96588, 'enUS', 'You\'ll be thrown into the prisons, just like the others.', 'You\'ll be thrown into the prisons, just like the others.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96593, 'enUS', 'The undead approach! To arms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96595, 'enUS', 'How did you and your sister get separated?', 'How did you and your sister get separated?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96596, 'enUS', 'What did she do?', 'What did she do?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96597, 'enUS', 'So why are you trying to save her?', 'So why are you trying to save her?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96598, 'enUS', 'Maiev and I chose very different paths in life.$B$BNot long after the Legion\'s first defeat on Azeroth, she dedicated her life to the pursuit of justice.$B$BSome would say she\'s gone too far in that pursuit at times.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96599, 'enUS', 'She murdered innocents, hoping to sever our people\'s ties with the Alliance.$B$BStill, I couldn\'t bring myself to kill her.$B$BAt first I thought that I\'d been weak -- that love had left me unable to do what was necessary.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96600, 'enUS', 'I know my sister. She\'d never commit such crimes unless she was under some foul influence.$B$BEven so, rather than investigate fully, I assumed the worst.$B$BI\'ve been a poor brother, but there\'s still time for me to make things right.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96601, 'enUS', 'DO NOT TRANSLATE', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96602, 'enUS', 'I\'ll help you find Maiev.', 'I\'ll help you find Maiev.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96603, 'enUS', 'White Lady, grant me your light so that I may purge these waters of the vile corruption of Xavius.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96604, 'enUS', '', 'Little brother. You came for me. Even after I tried to k--', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96605, 'enUS', 'Foolish druid. The power of your goddess is pitiful. Behold the might of Xavius!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96606, 'enUS', 'DO NOT TRANSLATE', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96607, 'enUS', 'We can\'t go out there! Are you crazy? Have you seen those things?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96608, 'enUS', 'Priom\'s right. Those things will crush us! It\'s just the five of us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96609, 'enUS', 'We\'re completely cut off. We have to wait for reinforcements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96610, 'enUS', '', 'Enough! I won\'t stand for this cowardace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96611, 'enUS', '', 'We\'re dead if we stay here too long. Allen, keep your eyes open for any sign of help! We move as soon as reinforcements are in sight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96612, 'enUS', 'Sergeant! I hear movement outside. It could be our reinforcements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96613, 'enUS', '', 'Excellent! Shields up, everyone. We\'re headed outside! Form up!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96614, 'enUS', 'You have to earn the Legion\'s gift!', 'You have to earn the Legion\'s gift!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96615, 'enUS', 'The Fel Touch is within your grasp!', 'The Fel Touch is within your grasp!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96616, 'enUS', 'We cannot... allow Xavius... to get away with this! We must get to Shaladrassil... and put an end to the demon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96617, 'enUS', 'I will meet you further ahead... We must hurry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96618, 'enUS', 'No! We must press onward... The fate of the world is at stake!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96619, 'enUS', 'Great kill down there! Here comes the air support!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96620, 'enUS', 'Like the wind!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96621, 'enUS', 'Your darkness burns within me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96622, 'enUS', 'Clear the field of these demons and we\'ll get you the birds you\'ll need to attack this citadel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96623, 'enUS', 'That citadel\'s not going to destroy itself! Clear a landing site on the farm and we\'ll get you some birds for the assault!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96624, 'enUS', 'If my memory serves me... there was an outpost not far from here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96625, 'enUS', 'I am everburning!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96626, 'enUS', 'Do not worry about us, commander. We are outnumbered, but they lack our spirit. LOK\'TAR!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96627, 'enUS', 'Go now commander. We will continue to put pressure on this front.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96628, 'enUS', '', 'Rotbeak is just northwest, past the dock. Bring me its head!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96629, 'enUS', 'I will scour every inch of this island until I find someone who knows of the Pillars of Creation. On my honor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96630, 'enUS', 'You have business with the Master you should attend to.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96631, 'enUS', 'Throwing', 'Throwing', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96632, 'enUS', 'The Legion will fall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96633, 'enUS', 'Show me what missions you have prepared.', 'Show me what missions you have prepared.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96634, 'enUS', 'Farahlon is mysterious land. Only send a crew you trust... or can afford to lose.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96635, 'enUS', 'We will plumb the depths of Draenor for all its secrets!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96636, 'enUS', 'Give Cordana my best. She\'s been quite distant lately.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96637, 'enUS', 'With every passing moment, Gul\'dan grows in power. But he no longer slinks through the shadows - we know where he is. We know what he\'s doing. Now, we strike! May the Light have mercy on all our souls.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96640, 'enUS', 'I have safely negotiated entrance into the elder drake\'s lair.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96641, 'enUS', 'I will meet you there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96642, 'enUS', '', 'An ancient blue dragon lies to the north. He is our best chance at finding out more about the Pillars.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96643, 'enUS', 'I will kill every last demon in my way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96644, 'enUS', '', 'A slight setback. I assure you it will not happen again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96645, 'enUS', 'Doom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96646, 'enUS', 'You will die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96647, 'enUS', 'Intruder, your life ends now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96648, 'enUS', 'The Legion conquers all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96649, 'enUS', 'You do not belong here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96650, 'enUS', 'A fatal mistake, mortal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96651, 'enUS', 'Succulent marrow. Crunchy bones.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96652, 'enUS', 'I will rip the fel from your soul.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96653, 'enUS', 'Quickly, grab them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96654, 'enUS', 'Another morsel has come to say, hello.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96655, 'enUS', 'Help! Baddies here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96656, 'enUS', 'We\'ll feast on your flesh and devour your soul.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96657, 'enUS', 'Hey! You\'re not supposed to be here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96658, 'enUS', 'Tasty meats for me to eats.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96659, 'enUS', 'Warn the Brood Queen!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96660, 'enUS', 'I\'ll burn your kneecaps off.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96661, 'enUS', 'The fel you wield will not be enough.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96662, 'enUS', 'You dare attack us here?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96663, 'enUS', 'For the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96664, 'enUS', 'For Brood Queen Tyranna. For the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96665, 'enUS', 'Stand still so I can shoot you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96666, 'enUS', 'Let\'s see if your shields can stop a bullet!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96667, 'enUS', 'Why do you always have to be so blurry?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96668, 'enUS', 'You are too late! The corruption is nearly complete!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96669, 'enUS', 'Xavius is the master of this forest now, $c. To enter is to succumb to the Nightmare.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96670, 'enUS', 'Excuse me...', 'Excuse me...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96671, 'enUS', '<The demon hunter looks as pale as a ghost. He shivers uncontrollably.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96672, 'enUS', 'Be at peace.', 'Be at peace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96673, 'enUS', 'Arduen sent me. You\'re free.', 'Arduen sent me. You\'re free.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96674, 'enUS', '', 'We were in pursuit of a dangerous dreadlord. Unfortunately, he lured us right into a trap.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96675, 'enUS', '<The Illidari\'s body is nearly unrecognizable. He was tortured, then died from the wounds.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96676, 'enUS', '', 'Good luck. I will make my way up to the foothold as soon as I am done with this demon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96677, 'enUS', 'Nethrandamus, take to the skies. Warn us if enemies approach.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96678, 'enUS', '', 'Quickly! The archdruid\'s condition is quite serious...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96679, 'enUS', 'Completing Ritual', 'Completing Ritual', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96680, 'enUS', 'Use the crucible to complete the ritual.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96681, 'enUS', 'Take me to the nearest invasion point!', 'Take me to the nearest invasion point!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96682, 'enUS', 'I suppose we can offer some assistance.', 'I suppose we can offer some assistance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96683, 'enUS', 'There were few of our brethren who were worthy, but I do not return empty-handed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96686, 'enUS', 'As powerful as we are, if Faronaar is any indicatio we will not be able to stop the demons alone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96687, 'enUS', 'I will find any of our brethren with the courage to follow you into battle and do what needs to be done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96688, 'enUS', 'Ah, a champion arrives in our most dire hour. Welcome.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96689, 'enUS', 'Beliash is protected by those Spires of Woe. You\'ll want to deactivate them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96690, 'enUS', '', 'This bastard leads the guards in this prison. He must pay for his crimes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96691, 'enUS', 'How dare you attack a kaldorei noble? You will beg for mercy, which I will not give.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96692, 'enUS', 'You always did think too highly of yourself. Another foolish death, then, if you insist.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96693, 'enUS', '|TINTERFACE\\ICONS\\spell_fire_felflamestrike.blp:20|t Archimonde begins to cast |cFFF00000|Hspell:183254|h[Allure of Flames]|h|r!', '|TINTERFACE\\ICONS\\spell_fire_felflamestrike.blp:20|t Archimonde begins to cast |cFFF00000|Hspell:183254|h[Allure of Flames]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96695, 'enUS', 'A massive gronnling is heading for Rangari Refuge! We are going to require some assistance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96696, 'enUS', '', 'This is only the beginning.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96697, 'enUS', 'I wish to browse your goods.', 'I wish to browse your goods.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96698, 'enUS', 'All clear boys! Here come some extra birds. Set down and prepare to assault the citadel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96699, 'enUS', '', 'You heard the commander! Let\'s help clear a landing zone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96700, 'enUS', 'Charge!', 'Charge!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96701, 'enUS', '', 'Begging\'s never been one of my talents, Stareye. You should know that by now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96702, 'enUS', 'Lord Ravencrest himself gave me this post. I won\'t surrender it to you demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96703, 'enUS', 'Ravencrest? Impossible! And we\'re not demons. Don\'t you recognize me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96704, 'enUS', '', 'It\'s useless, brother. Like the other undead, he cannot see us for who we are.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96705, 'enUS', 'Never mind that. I heard what happened in the Vault of the Wardens. Luckily my hair can\'t get any grayer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96706, 'enUS', 'Why did you have to chase after Gul\'dan alone? Thank Elune you\'re still alive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96707, 'enUS', '', 'Well said, brother: we have bigger concerns. Let\'s go. Stareye has much to answer for.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96715, 'enUS', 'You broke ANOTHER drill!? Where did you get those, Venture Company?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96716, 'enUS', 'Keep your helmet on... we are practically through. Just need to kick it a few times and we got our payday!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96717, 'enUS', 'You\'re gonna need a sick note tomorrow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96718, 'enUS', 'Quickly, finish the summons.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96719, 'enUS', 'The Legion\'s will be done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96720, 'enUS', 'Kill the Illidari.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96721, 'enUS', 'For the Brood Queen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96722, 'enUS', 'Your world will be purged!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96723, 'enUS', 'Our ritual is interrupted.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96724, 'enUS', 'Defend the portal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96725, 'enUS', 'In Sargeras\'s name.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96726, 'enUS', 'Never should have come to this stinkin\' world...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96727, 'enUS', 'Don\'t be tricky now. I hate tricky!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96728, 'enUS', 'No tricks! I said no tricks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96729, 'enUS', 'Nothing here for you pal, honest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96730, 'enUS', 'No... my payday...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96731, 'enUS', 'Hold still a sec, I can fix that whole \"living\" thing for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96732, 'enUS', 'Ah man I\'m down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96733, 'enUS', 'The huntin\' party is here to help shoot down that cursed bird!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96734, 'enUS', 'You will not stop the spread of the Nightmare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96735, 'enUS', '', 'Altruis said you would only take the best. Here we are.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96736, 'enUS', 'The engravings on the shield depict a goddess of the shieldmaidens, watching over them in battle. \n\nAbove her is inscribed the name \"Eyir\".', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96737, 'enUS', 'This shield appears to show various trials undergone by the warriors of Skold-Ashil.\n\nA depiction of the nearby temple adorns the center.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96738, 'enUS', 'An image of a shieldmaiden is carved on this shield, kneeling before an elaborate spear. \n\nThe priestesses of the temple stand nearby, silently observing the rite.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96739, 'enUS', 'The scene on this shield shows one of the shieldmaidens hovering above a statue of an ancient champion of Skold-Ashil.\n\nThe shieldmaiden is glowing with a radiant aura, and is held aloft by golden wings.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96740, 'enUS', 'Upon this shield, an engraving of Eyir stands at the entrance to the titan vault. \n\nShe appears to be welcoming a winged champion of the shieldmaidens inside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96741, 'enUS', 'This shield bears an image of a winged shieldmaiden lifting the body of a fallen vrykul from a battlefield.\n\nThe shieldmaiden\'s image appears eerily similar to that of a Val\'kyr.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96742, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96743, 'enUS', '|TINTERFACE\\ICONS\\ability_fixated_state_red.blp:20|t A Doomfire Spirit is |cFFF00000|Hspell:182879|h[Fixated]|h|r on you!', '|TINTERFACE\\ICONS\\ability_fixated_state_red.blp:20|t A Doomfire Spirit is |cFFF00000|Hspell:182879|h[Fixated]|h|r on you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96746, 'enUS', 'We will find a Legion gateway or die trying.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96747, 'enUS', '', 'Spare us the theatrics, won\'t you, Altruis?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96748, 'enUS', 'Ah, get behind me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96749, 'enUS', 'I\'m flyin\'!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96750, 'enUS', 'Did someone say bomb?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96751, 'enUS', 'Special DELIVERY!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96752, 'enUS', 'You have no power over me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96753, 'enUS', 'You demons picked the wrong gnome to mess with!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96754, 'enUS', 'Our blades have been honed for millennia. This world will fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96758, 'enUS', 'Don\'t let them into the tower!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96759, 'enUS', 'Keep them out of here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96760, 'enUS', 'I\'ll be damned if those demons think they can take the tower!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96766, 'enUS', 'Aye commander, we can divert resources from any of your nearby outposts for use here in Tanaan. Ye\' just let me know which one ye\' want!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96767, 'enUS', 'Call to Arms.', 'Call to Arms.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96768, 'enUS', 'Guardian Orb.', 'Guardian Orb.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96769, 'enUS', 'Artillery Strike.', 'Artillery Strike.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96770, 'enUS', 'Champion\'s Honor.', 'Champion\'s Honor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96771, 'enUS', 'The Mechashredder.', 'The Mechashredder.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96772, 'enUS', 'DO NOT TRANSLATE', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96773, 'enUS', 'The structure cracks under pressure, exploding and releasing large amounts of fel energy into the sky.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96775, 'enUS', 'Aye, Commander, I\'d like to talk to you about bringing some of our reinforcements here to Tanaan, if ye\' have a minute.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96776, 'enUS', 'Hey there, Commander. We\'ve got some extra supplies I can call in to help out, if you give the order.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96777, 'enUS', 'We\'ve got supplies from our nearby outposts that we can call in, now that most of the heat is taking place here. Which supplies did you want to bring over?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96779, 'enUS', 'Time I stood up, got out there and helped! Ah, what\'s the use?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96780, 'enUS', 'Great spot, great view - just me and the end of the world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96781, 'enUS', 'Here we come! This is it! It\'s the end of the world as we know it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96785, 'enUS', 'You\'re gonna wear that hideous outfit today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96786, 'enUS', 'Do you really need to be carrying ALL that stuff around?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96787, 'enUS', 'You should write to your mother. She\'s worried sick about you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96788, 'enUS', 'I don\'t understand why you have critters fight other critters when you could do a much better job yourself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96789, 'enUS', 'If I get you to where you want to go, will you agree to leave me alone?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96790, 'enUS', 'You wanna get out of here? I\'ll get you out of here, but I\'m not making any promises on where you go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96791, 'enUS', 'Sure, I trust a demon to send me to a safe location.', 'Sure, I trust a demon to send me to a safe location.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96792, 'enUS', '', 'You found it! Thank you, traveler.$B$B<Syndrelle pauses for a moment.>$B$BOn second thought, I would rather you keep it and think of it as a reminder of our new friendship.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96793, 'enUS', '', 'Hello traveler. I\'ve lost my favorite charm somewhere around here. I don\'t suppose that you\'d be willing to help me look for it?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96794, 'enUS', 'I\'d be happy to help.', 'I\'d be happy to help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96795, 'enUS', 'You must go ahead - Nethrandamus stayed with the others so I will only slow you down. Go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96796, 'enUS', '', 'This is so exciting!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96797, 'enUS', '', 'Don\'t worry, I can talk and shoot at the same time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96798, 'enUS', '', 'Do rifles always have this much recoil?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96799, 'enUS', '', 'You make it look so easy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96803, 'enUS', 'It\'s a fine day for a hunt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96804, 'enUS', 'It doesn\'t get much better than this; the smell of fresh pine in the breeze, an abundance of game to be hunted.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96805, 'enUS', '', '$n has arrived. For the Illidari!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96806, 'enUS', 'Help! Please!', 'Help! Please!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96807, 'enUS', 'Please, save me!', 'Please, save me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96808, 'enUS', 'They won\'t leave me alone...', 'They won\'t leave me alone...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96809, 'enUS', 'Get me out of here!', 'Get me out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96810, 'enUS', 'Please... help me...', 'Please... help me...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96811, 'enUS', 'My services are yours if you help me escape!', 'My services are yours if you help me escape!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96812, 'enUS', 'I shouldn\'t have come out here all alone!', 'I shouldn\'t have come out here all alone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96813, 'enUS', 'I don\'t want to go out like this...', 'I don\'t want to go out like this...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96814, 'enUS', '|TInterface\\Icons\\ability_vehicle_siegeenginecannon.blp:20|t A %s is slightly damaged!', '|TInterface\\Icons\\ability_vehicle_siegeenginecannon.blp:20|t A %s is slightly damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96815, 'enUS', '|TInterface\\Icons\\ability_vehicle_siegeenginecannon.blp:20|t A %s is severely damaged!', '|TInterface\\Icons\\ability_vehicle_siegeenginecannon.blp:20|t A %s is severely damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96816, 'enUS', '|TInterface\\Icons\\ability_vehicle_siegeenginecannon.blp:20|t A %s is critically damaged!', '|TInterface\\Icons\\ability_vehicle_siegeenginecannon.blp:20|t A %s is critically damaged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96817, 'enUS', '', 'The Burning Legion will fall.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96818, 'enUS', 'You are spotted by the %s!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96819, 'enUS', 'My blades thirst for the blood of the Legion!', 'My blades thirst for the blood of the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96820, 'enUS', 'You shall perish!', 'You shall perish!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96821, 'enUS', 'Your advances are ill-advised.', 'Your advances are ill-advised.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96822, 'enUS', 'You will witness true power!', 'You will witness true power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96823, 'enUS', 'You will pray for a swift death!', 'You will pray for a swift death!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96824, 'enUS', '', 'I don\'t trust a gnome with a gun. \n\nAddie\'s more likely to shoot you than any of her intended targets.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96825, 'enUS', '', 'It... it must be around here somewhere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96826, 'enUS', '', 'Maybe the current moved it...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96827, 'enUS', '', 'Where could it have gone?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96828, 'enUS', '', 'By the antlers of Cenarius! Where is it?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96829, 'enUS', '', 'Don\'t you ever listen to your elders, $c? No matter, you did save my life.$B$BLet me rest a while. I\'ll be fine, just wait.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96830, 'enUS', '', 'I\'ll be fine, $c. Turn back. This is my score to settle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96831, 'enUS', 'Score? Don\'t be a fool. You are badly injured.', 'Score? Don\'t be a fool. You are badly injured.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96832, 'enUS', 'Gorefiend begins to cast |TInterface\\Icons\\spell_misc_zandalari_council_soulswap:20|t |cFFFF0404|Hspell:181973|h[Feast of Souls]|h|r!', 'Gorefiend begins to cast |TInterface\\Icons\\spell_misc_zandalari_council_soulswap:20|t |cFFFF0404|Hspell:181973|h[Feast of Souls]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96833, 'enUS', 'How ya doin\'?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96834, 'enUS', 'Time for a workout!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96835, 'enUS', 'Anyone up for sketch group?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96836, 'enUS', 'Oh ho ho. Well looky here. We got ourselves something new to hunt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96837, 'enUS', 'Now I find you spending your last moments with this, this beast.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96838, 'enUS', 'I shalll enjoy putting you down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96840, 'enUS', '', 'You should have listened... On your guard, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96841, 'enUS', 'This ancient moonwell has seen better days, but its waters still glow with the radiant  moonlight of Elune.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96842, 'enUS', '', 'Stand back. I do not need your assistance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96843, 'enUS', 'Welcome to sketch group! Grab an easel, get comfortable. No judgments here - unless you\'re a Paladin, of course.$b$bToday we\'re studying the nexus. Such beauty! Such chaos! So much... blue.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96844, 'enUS', '', 'Even though these woods have been lost to the corruption of Shaladrassil, this is still our home.$b$bThe fate of the forest is ours to share.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96846, 'enUS', '', 'That wasn\'t necessary of ya, but you have my thanks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96848, 'enUS', 'Charge!', 'Charge!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96849, 'enUS', 'Hyyeeaahhhaaay!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96850, 'enUS', 'Take up the blade, Arthas Menethil. Embrace your destiny!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96851, 'enUS', 'Yes... you are right... I will bear any curse to have my revenge...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96852, 'enUS', '', 'Insolent dog!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96853, 'enUS', '', 'You will suffer for this! Bring me his head!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96854, 'enUS', 'What? An intruder! You won\'t stop me! Frostmourne will be mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96855, 'enUS', 'To arms! Lord Felsoul must not be disturbed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96856, 'enUS', 'Face me, Banshee Queen. Answer for your crimes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96857, 'enUS', 'The demons underestimate your power. Punish them for their hubris!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96858, 'enUS', 'How dare you interfere! His soul belongs to ME!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96860, 'enUS', 'Argh! The darkness... I can\'t... I...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96861, 'enUS', 'ARRGHHHHHH!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96866, 'enUS', 'Bathe them in hellfire! Halt their advance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96867, 'enUS', '', 'It\'s about time I put you down, Greymane!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96868, 'enUS', '', 'Azshara kept her secrets well. By the time I saw the demons first-hand, it was already too late.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96869, 'enUS', '', 'The Queen\'s thirst for power doomed us all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96870, 'enUS', 'Ishnu-dal-dieb, traveler.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96871, 'enUS', 'So many worlds... so many layers of existence...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96872, 'enUS', 'In the end, they become nothingness... or, perhaps...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96873, 'enUS', 'They will BURN! Die! The master wills it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96874, 'enUS', 'No matter how it all ends, you will die here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96875, 'enUS', 'I have so much to show you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96876, 'enUS', 'Servants of the Void! Swallow this world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96877, 'enUS', 'All life must decay.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96878, 'enUS', 'Corruption! Flame! Ash!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96879, 'enUS', 'LEGION! Destroy all who stand before you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96880, 'enUS', 'I TEAR APART! AND WITH ME, YOU TOO SHALL DIE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96881, 'enUS', 'These gaps in reality... I must tear them wider!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96882, 'enUS', 'All will be swallowed! No life shall remain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96883, 'enUS', 'I cannot remain... I am becoming... EVERYTHING! NOTHING!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96884, 'enUS', 'BURN!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96885, 'enUS', 'RIP APART!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96886, 'enUS', 'I CANNOT... CONTAIN...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96887, 'enUS', 'IT OVERWHELMS ME!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96888, 'enUS', 'YES! YES! I SEE IT! I can see... the... end...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96889, 'enUS', 'This world will not fall so easily, demon. Its people will not give up without a fight. Our will is unbreakable! For Draenor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96890, 'enUS', 'Lok\'tar ogar! They are pushed back! To the portal! Gul\'dan is mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96891, 'enUS', 'The final hour is at hand, the master comes! This is your purpose, summoners. Mannoroth will be whole again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96892, 'enUS', 'The wrath of the legion will destroy you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96893, 'enUS', 'Your failure will please the master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96894, 'enUS', 'Death awaits...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96895, 'enUS', 'I\'d like to purchase Seals of Tempered Fate.', 'I\'d like to purchase Seals of Tempered Fate.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96896, 'enUS', 'Finish the portal, damn you! We must protect the wards!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96897, 'enUS', '', 'Thanks! Let\'s get to work. Here kitty, kitty, kitty...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96898, 'enUS', '', 'Seek out Maladaar in the west. I fear he may have encountered the brunt of Gul\'dan\'s forces.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96899, 'enUS', '', 'Find my husband in the battlefield to the west! I pray our losses were not too great.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96900, 'enUS', 'Let the darkness consume you! Feast upon the souls of the damned!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96902, 'enUS', 'Here! This is a perfect natural harbor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96903, 'enUS', 'Many of the souls freed by Frostmourne\'s destruction wander the halls of Icecrown, lashing out in eternal torment.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96904, 'enUS', '|TInterface\\Icons\\spell_shadow_deadofnight.blp:20|t Cull the scourge before they are sacrificed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96905, 'enUS', 'Use |TInterface\\Icons\\spell_shadow_antimagicshell.blp:20|t |cFF2959D3|Hspell:184366|h[Anti-Magic Shell]|h|r to avoid harmful spells!', 'Use |TInterface\\Icons\\spell_shadow_antimagicshell.blp:20|t |cFF2959D3|Hspell:184366|h[Anti-Magic Shell]|h|r to avoid harmful spells!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96906, 'enUS', 'Exposition', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96907, 'enUS', 'Our people look to us for aid. Make these foul creatures regret the day they invaded our world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96908, 'enUS', 'These demons think us easy prey. Today they will learn that the kaldorei stand down to no one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96909, 'enUS', 'Warriors, you pledged your blades to the House of Ravencrest. Today, we take up arms and fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96910, 'enUS', 'Rally to me, soldiers! No matter the foes we face, Black Rook Hold shall not fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96911, 'enUS', 'Stand firm, champions! Our might is tested, but we shall prevail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96912, 'enUS', 'You have gone far enough, mortal. This ends here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96913, 'enUS', 'Another prisoner for my cells? Looks like I\'ll need to break you first!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96914, 'enUS', 'Intruder! Sound the alarm!', 'Intruder! Sound the alarm!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96915, 'enUS', 'I\'ve been training for this moment!', 'I\'ve been training for this moment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96916, 'enUS', 'You\'ll get no further, invader!', 'You\'ll get no further, invader!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96917, 'enUS', 'This world has no place for you, demon!', 'This world has no place for you, demon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96918, 'enUS', 'Your kind is not welcome here!', 'Your kind is not welcome here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96919, 'enUS', 'I\'ll wipe your blood off my blade, just like the rest!', 'I\'ll wipe your blood off my blade, just like the rest!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96920, 'enUS', 'I have slain hundreds before you. You\'ll simply be one more notch on my belt.', 'I have slain hundreds before you. You\'ll simply be one more notch on my belt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96921, 'enUS', 'I will grant you a swift death, trespasser.', 'I will grant you a swift death, trespasser.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96922, 'enUS', '', 'I never miss!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96923, 'enUS', '', 'Stop, interloper. I\'ll make you bleed first!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96924, 'enUS', '', 'My strike is swift and true!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96925, 'enUS', '', 'If you won\'t return to your world, I\'ll send you there in pieces!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96926, 'enUS', '', 'Enjoy this moment. It will be your last!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96927, 'enUS', '', 'The lieutenant has ordered your execution.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96928, 'enUS', '$n... it\'s been years. You should know better than to stand against me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96929, 'enUS', 'My master has granted me many gifts. Witness the power of the Deceiver!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96930, 'enUS', 'Each Vision of Death rune needs someone within it to avoid raid damage!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96931, 'enUS', 'Over here, $c! Your help is needed inside!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96932, 'enUS', 'I will personally deal with their leader, my queen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96933, 'enUS', 'I almost... made it... out...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96934, 'enUS', 'Welcome to the Scribes\' Sacellum!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96935, 'enUS', 'Deucus said you owe him a favor. I have a tome in need of translation.', 'Deucus said you owe him a favor. I have a tome in need of translation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96937, 'enUS', 'Skyhorn, to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96938, 'enUS', 'Ha! Now that was a battle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96939, 'enUS', 'The differences that have set the Arakkoa apart for years seem so trivial today. We have squandered the might of our people on fear and mistrust of one another when we should have been looking beyond the safety of Skyreach. There is nothing to be done about the past, but know that there are some who wish for our people to be more than a footnote to what Gul\'dan has wrought.\n\nI will help you, $c. We must all help one another now, or this world is doomed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96940, 'enUS', 'I suppose I do owe that blowhard a favor. Lucky for you, translation is my specialty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96941, 'enUS', 'This is ancient vrykul script, the property of someone named \"Svena\". A rare and intriguing find indeed. Tell Deucus he is now in MY debt!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96942, 'enUS', '', 'I\'m sorry to hear that Arduen didn\'t make it. I will make sure the demons pay for his death.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96943, 'enUS', 'Who dares stand against the might of the Legion?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96944, 'enUS', 'Come, champion. Let us pluck this Witchqueen from her throne.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96945, 'enUS', 'You handle the Witchqueen. We will contend with her brood.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96946, 'enUS', 'Finally, I can rest.', 'Finally, I can rest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96947, 'enUS', '', 'See that you do.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96948, 'enUS', 'Quel\'Thalas will never fall!', 'Quel\'Thalas will never fall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96949, 'enUS', 'You are not welcome here!', 'You are not welcome here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96950, 'enUS', 'You shall not pass!', 'You shall not pass!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96951, 'enUS', 'The king may have fallen but I will never abandon Lordaeron!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96952, 'enUS', 'You an\' that prince of yours are gonna pay for what he did to ole\' Muradin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96953, 'enUS', 'The Light... no...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96954, 'enUS', 'You unholy monster! The righteous fury of the Light will tear you apart!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96955, 'enUS', 'Peace... at... last...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96956, 'enUS', 'The Silver Hand will remove your blight from this world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96957, 'enUS', 'The pain... is over...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96958, 'enUS', 'To arms soldiers of Quel\'Thalas! The enemy is at our very doorstep!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96959, 'enUS', 'Ah, $n. So the Lich King chose you to be his hand? I\'m not surprised. We have all heard of your exploits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96960, 'enUS', 'Our forces have gathered from all across Northrend. When the call to action comes... and it will come soon... the Knights of the Ebon Blade will be ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96961, 'enUS', 'Make no mistake, we are not bound to this new Lich King. However, he may prove a valuable ally against the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96962, 'enUS', 'The gates of Icecrown lie to the north. Do not keep our ally waiting!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96964, 'enUS', 'For now, our goals align with his. And in the meantime, it is in our best interests that the Scourge stay... contained.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96968, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\LFGFRAME\\LFGICON-SUNWELL.BLP:50:80|t$bI will have my revenge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96969, 'enUS', '|TINTERFACE\\ICONS\\ability_warlock_moltencoregreen.blp:20|t $n is marked with |cFFF00000|Hspell:186961|h[Nether Banish]|h|r!', '|TINTERFACE\\ICONS\\ability_warlock_moltencoregreen.blp:20|t $n is marked with |cFFF00000|Hspell:186961|h[Nether Banish]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96970, 'enUS', '|TINTERFACE\\ICONS\\spell_misc_zandalari_council_soulswap.blp:20|t You are marked with |cFFF00000|Hspell:186123|h[Wrought Chaos]|h|r!', '|TINTERFACE\\ICONS\\spell_misc_zandalari_council_soulswap.blp:20|t You are marked with |cFFF00000|Hspell:186123|h[Wrought Chaos]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96971, 'enUS', '|TINTERFACE\\ICONS\\spell_shadow_soulleech_1.blp:20|t You are marked with |cFFF00000|Hspell:185014|h[Focused Chaos]|h|r!', '|TINTERFACE\\ICONS\\spell_shadow_soulleech_1.blp:20|t You are marked with |cFFF00000|Hspell:185014|h[Focused Chaos]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96972, 'enUS', 'Your soul is mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96975, 'enUS', 'For Lordaeron!', 'For Lordaeron!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96976, 'enUS', 'Meet your second death, undead filth!', 'Meet your second death, undead filth!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96977, 'enUS', 'Repel the intruder!', 'Repel the intruder!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96978, 'enUS', 'To arms!', 'To arms!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96981, 'enUS', 'Death... really isn\'t so bad.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96982, 'enUS', 'What are you afraid of?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96983, 'enUS', 'Your death makes me stronger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96985, 'enUS', 'It\'s a fine day for a hunt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96986, 'enUS', 'The Wardens are searching every corner of the Broken Isles for Gul\'dan.$B$BThey have reason to believe the warlock hides some secret within Black Rook Hold. And if he is here, then so perhaps is my sister, Maiev.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96988, 'enUS', 'How strange... Ravencrest\'s army sees us as invaders. It\'s as if they are trapped within their own memories.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96989, 'enUS', 'Sira and the other Wardens are doing everything they can to find Maiev.$B$BThe attack on the Vault of the Wardens was devastating. Of hundreds, only a handful survived.$B$BBut the Wardens are tough as steel. They will survive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96990, 'enUS', 'I refuse to believe it. Lord Ravencrest cannot be working with Gul\'dan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96991, 'enUS', '', 'We must retrieve it. He\'s still my charge. We\'ll have to invade the tower. But it will be no easy task.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96992, 'enUS', '', 'Champion, return to your other duties for now. Soon we will face Lord Ravencrest himself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96993, 'enUS', 'Slay them all! For the Dark Lady!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96994, 'enUS', 'Hand over the Banshee Queen!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96995, 'enUS', 'Sylvanas! Come and face your executioner!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96996, 'enUS', 'I\'ll reduce this camp to rubble if I have to!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96997, 'enUS', '', 'Thank you, hero. I did not expect salvation from this place. Where is my brother?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96998, 'enUS', 'Show yourself, Sylvanas!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96999, 'enUS', 'You cannot hide from me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97000, 'enUS', 'You call us monsters? How rich.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97001, 'enUS', 'Monsters! You have no place in this world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97002, 'enUS', 'Stop holding back, old man! Show us the real you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97003, 'enUS', 'I will take out the guards inside the prison. Get those keys and meet me within the cells.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97004, 'enUS', 'Rrgghhh... ENOUGH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97005, 'enUS', 'I will stay to ease this prisoner\'s passing. It won\'t be long, now. Go ahead and find Maiev.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97006, 'enUS', 'Take me to Dreadmist Peak!', 'Take me to Dreadmist Peak!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97007, 'enUS', '', 'Lieutenant Stareye commands. Death to all intruders!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97008, 'enUS', 'We have secured the walls, demon! You will not escape here alive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97009, 'enUS', 'Test my hammer, and YOU will be the one to break!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97010, 'enUS', 'Ah, there you are!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97011, 'enUS', 'This ends here, Demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97012, 'enUS', 'Mo\'arg, to the front! This has gone on long enough.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97013, 'enUS', '', 'Over here! This sewer will take us back outside. We should go before our enemies regroup.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97014, 'enUS', 'What a tragic waste. Even in death, he only wanted to protect our people.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97015, 'enUS', '', 'The battle rages throughout Tanaan, Commander. Where shall we focus our attention today?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97016, 'enUS', '', 'Gul\'dan is creating his own army of zealots. Where should we strike back?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97017, 'enUS', '', 'Gul\'dan will not break us. The Legion will NEVER break us! Today we fight back.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97018, 'enUS', '', 'This jungle hides dangers untold. But if we are wise, we can use it to mask our next attack.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97019, 'enUS', '', 'I have the latest reports from the Rangari right here, commander. Where should we strike?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97020, 'enUS', '', 'Gul\'dan\'s fel minions are closing in around us. We MUST go on the offensive, commander!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97021, 'enUS', '', 'Our warriors are blessed by the light, sure of purpose and hardened by battle. They await your orders.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97022, 'enUS', '', 'If this is truly the end of the world, then I, for one, will go out fighting. For Samara, for Maraad, for all those who gave their lives to give us this day!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97023, 'enUS', 'DIE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97024, 'enUS', 'My forces are yours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97025, 'enUS', 'We will assist in establishing the volcano foothold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97026, 'enUS', 'Ashtongue, forward!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97027, 'enUS', 'You face not Malchezaar alone, but the legions I command!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97028, 'enUS', 'What, good $gsir:madam;, can I do to assist you today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97029, 'enUS', 'Where can I find a Leyflame Burner?', 'Where can I find a Leyflame Burner?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97030, 'enUS', 'Where can I find Advanced Corks?', 'Where can I find Advanced Corks?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97031, 'enUS', 'Where can I find a Dual-Chambered Mixing Flask?', 'Where can I find a Dual-Chambered Mixing Flask?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97032, 'enUS', 'Where can I find Precipitating Powder?', 'Where can I find Precipitating Powder?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97033, 'enUS', 'Where can I find a Shaldorei Silk Filter?', 'Where can I find a Shaldorei Silk Filter?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97034, 'enUS', 'Where can I find a Demonsbreath Crucible?', 'Where can I find a Demonsbreath Crucible?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97035, 'enUS', 'Where can I find Bendy Glass Tubes?', 'Where can I find Bendy Glass Tubes?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97036, 'enUS', 'Where can I find a Malloric Burette?', 'Where can I find a Malloric Burette?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97037, 'enUS', 'Where can I find a High-Capacity Decoction Conduit?', 'Where can I find a High-Capacity Decoction Conduit?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97038, 'enUS', 'I need help finding the replacement alchemy equipment.', 'I need help finding the replacement alchemy equipment.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97039, 'enUS', 'Our alchemy table remains woefully incomplete.$b$bI shall use my extensive knowledge to point you in the direction of replacement parts, but I fear you will have to do some legwork on your own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97040, 'enUS', 'I\'ve heard that someone over in the engineering shop is selling a burner that might be usable. You could likely negotiate a fairer price than I could!$b$bThe shop is just across the street. I\'ll make a note on your map for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97041, 'enUS', 'The naga have been known to hoard all manner of potions, so they might have the sort of corks we need. The best place to search is the Eye of Azshara itself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97042, 'enUS', 'We require a flask capable of mixing more than reagents. Thoughts, ideas, dreams...$b$bYou may have to search the Emerald Nightmare for such an item!$b$bPLAYTEST CHEAT: CI 127872', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97043, 'enUS', 'It\'s said the ancient vrykul collected such powderfrom the residue of dragon\'s breath. If it\'s true that Neltharion\'s Lair lies somewhere below us, it seems a likely source.$b$bPLAYTEST CHEAT: CI 127874', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97044, 'enUS', 'The delicate yet resilient fabric needed for this sort of filter would come from a place of majesty and power. The locals speak of something called the Halls of Valor... you might investigate.$b$bPLAYTEST CHEAT: CI 127875', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97045, 'enUS', 'Artifacts of such potency are supposedly kept within the Vault of the Wardens. There are whispers of trouble in those halls, so be careful!$b$bPLAYTEST CHEAT: CI 127876', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97046, 'enUS', 'The alchemists of old were known to be buried alongside their implements. Searching within Suramar Crypts may yield results.$b$bPLAYTEST CHEAT: CI 127877', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97047, 'enUS', 'That particular burette was designed by Alchemist Mallory of Stormwind. I\'ve heard that similarly valuable equipment might be stored in the Violet Hold.$b$bPLAYTEST CHEAT: CI 127878', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97048, 'enUS', 'Often rare finds only come from the darkest of places, $n. Dare I speak the name aloud? Venture into Helheim... if you dare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97049, 'enUS', 'Queue for dungeon', 'Queue for dungeon', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97050, 'enUS', 'Queue for heroic dungeon', 'Queue for heroic dungeon', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97051, 'enUS', 'Nicely done! Patricia has a new recipe for you, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97052, 'enUS', 'How dare you speak to me!', 'How dare you speak to me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97053, 'enUS', 'I can only laugh that you mortals keep holding on. The master even foresaw the coming of these days.', 'I can only laugh that you mortals keep holding on. The master even foresaw the coming of these days.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97054, 'enUS', 'Pathetic. Begone from my sight... I can\'t stand grovelers.', 'Pathetic. Begone from my sight... I can\'t stand grovelers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97055, 'enUS', 'You are afflicted with |TInterface\\Icons\\Spell_Shadow_ChillTouch.blp:20|t |cFFFF0404|Hspell:189434|h[Touch of Doom]|h|r!', 'You are afflicted with |TInterface\\Icons\\Spell_Shadow_ChillTouch.blp:20|t |cFFFF0404|Hspell:189434|h[Touch of Doom]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97056, 'enUS', '|cFFFF0000|Hspell:181295|h[Digest]|h|r killed you.  Run to the center to escape before the timer expires!', '|cFFFF0000|Hspell:181295|h[Digest]|h|r killed you.  Run to the center to escape before the timer expires!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97059, 'enUS', 'Grah - uh COMMANDER! Yes commander! Zug zug!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97063, 'enUS', '<This grunt is oblivious to your presence, and is slumped against the wall dozing.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97064, 'enUS', 'WAKE UP! Or I\'ll have you cleaning latrines before you can say \"ready to work!\"', 'WAKE UP! Or I\'ll have you cleaning latrines before you can say \"ready to work!\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97068, 'enUS', 'Master... I have... failed...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97070, 'enUS', '', 'This is pretty much the best day ever!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97071, 'enUS', 'Curse Helya! Curse her cowardly followers! I\'ll not rest until she is dead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97073, 'enUS', '', 'You can\'t beat the thrill of hunting something ten times your size!', 26124);
