INSERT INTO `broadcast_text_locale` VALUES (97106, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ENCOUNTERJOURNAL\\UI-EJ-BOSS-King Ymiron.blp:50:80|t$bReady are you? Let us see!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97107, 'enUS', 'Gladly, my master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97108, 'enUS', 'Send me to Destructor\'s Rise.', 'Send me to Destructor\'s Rise.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97109, 'enUS', 'You are victorious! Khadgar was watching the island, but the demons blocked us somehow for teleporting earlier. I should have known you would need no assistance.', '', 26124);
