INSERT INTO `broadcast_text_locale` VALUES (97176, 'enUS', '', 'Enjoy your last moments as my venom destroys you from the inside. I delight to know how much you will suffer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97177, 'enUS', '', 'Let us leave this place, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97178, 'enUS', 'For the Horde!', 'For the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97179, 'enUS', 'For Honor!', 'For Honor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97180, 'enUS', 'These lands will not succumb to your kind.', 'These lands will not succumb to your kind.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97181, 'enUS', 'Your presence is not welcome in our lands!', 'Your presence is not welcome in our lands!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97182, 'enUS', 'Dey not gone take dis land from us. Dat be the truth, mon.', 'Dey not gone take dis land from us. Dat be the truth, mon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97183, 'enUS', 'Maybe dey got de ships, but we got de lands, tools, and we got eacho\'da.', 'Maybe dey got de ships, but we got de lands, tools, and we got eacho\'da.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97184, 'enUS', '', 'Ysera is attacking the Temple of Elune!$b$bWe must stop her before the temple is completely destroyed... we don\'t have much time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97185, 'enUS', 'I am ready to begin the defense of the temple!', 'I am ready to begin the defense of the temple!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97186, 'enUS', 'Ain\'t nobody \'fraid o\' dem Legion.', 'Ain\'t nobody \'fraid o\' dem Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97187, 'enUS', 'If dey want war, we gonna bring \'em war.', 'If dey want war, we gonna bring \'em war.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97188, 'enUS', 'Ain\'t nobody gone sit at home durin\' these times. Everybody gonna fight.', 'Ain\'t nobody gone sit at home durin\' these times. Everybody gonna fight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97189, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97190, 'enUS', 'I know, Koru. I sense them too, but worry is for the weak. The Legion will not succeed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97191, 'enUS', 'We\'ve known these lands for so long...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97192, 'enUS', 'It is good that you arrived when you did. Our walls can\'t take much more of a beating from Greymane\'s cannons.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97193, 'enUS', 'Koru, you must remain calm. These are our lands. Our people... we will not be defeated.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97194, 'enUS', 'Excellent! With the cannons stopped, we can press our advance. \n\nNow we just need to find a way to take out his ship for good...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97195, 'enUS', 'I could feel that explosion from here! \n\nThat Gilnean bastard never knew what hit him!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97196, 'enUS', 'The disturbed tomb, demons here at Black Rook Hold, and the enemy night elves are no coincidence.$B$BThe Legion has resurrected Lord Ravencrest. My former master lives again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97197, 'enUS', '|TINTERFACE\\ICONS\\inv_enchant_voidsphere.blp:20|t A Void Star is |cFFF00000|Hspell:189894|h[Fixated]|h|r on you!', '|TINTERFACE\\ICONS\\inv_enchant_voidsphere.blp:20|t A Void Star is |cFFF00000|Hspell:189894|h[Fixated]|h|r on you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97198, 'enUS', 'Fun to smash small people.', '', 26124);
