INSERT INTO `broadcast_text_locale` VALUES (97545, 'enUS', 'Don\'t you have a building to build?', 'Hey commander, can I interest you in an investment plan that moves some of your assets offshore? Literally offshore, I know a guy that wants to build an oil rig.', 26124);
