INSERT INTO `broadcast_text_locale` VALUES (97621, 'enUS', '', 'No need to explain, Malfurion. I have seen what is coming.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97622, 'enUS', '', 'The Emerald Dream is being overtaken by the Nightmare. The Legion and the satyr Xavius are to blame.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97623, 'enUS', '', 'Shaladrassil, the World Tree, has fallen to the Nightmare\'s corruption and spreads its curse across our precious land.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97624, 'enUS', '', 'The Nightmare Lord must be vanquished, or Val\'sharah is lost.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97625, 'enUS', 'The Tears of Elune, stolen by Xavius! Then our champion\'s efforts were for nothing?', '', 26124);
