INSERT INTO `broadcast_text_locale` VALUES (97627, 'enUS', 'Time we do not have. The temple was saved, but Cenarius was lost to the Nightmare.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97628, 'enUS', '', 'Calm yourself, Malfurion. You have not seen the terrible power Xavius now possesses.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97629, 'enUS', 'I have wasted too much time here. No more!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97630, 'enUS', '', 'Cenarius\'s loss has left Malfurion unable to see reason.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97631, 'enUS', '', 'Make haste to the World Tree. He may be headed there, all too ready to take reckless risks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97632, 'enUS', '', 'Meanwhile, you and I will follow his trail from here. Malfurion must not fall.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97633, 'enUS', 'Insignificant gnat. I will enjoy breaking you.', '', 26124);
