INSERT INTO `broadcast_text_locale` VALUES (97696, 'enUS', '', 'Watch your step! It\'s a long way down.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97698, 'enUS', '', 'Vengeance will be mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97699, 'enUS', '', 'All night elf children learn to love the dark.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97700, 'enUS', '', 'My powers have grown since joining the Legion. Here, let me show you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97701, 'enUS', '', 'I will not be stopped! I have fought too long, suffered too much. Victory must be mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97702, 'enUS', '', 'Beaten? How! You are... nothing', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97703, 'enUS', '', 'Your death was inevitable.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97704, 'enUS', '', 'How utterly predictable! Maiev is nothing if not persistent.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97705, 'enUS', '', 'I knew she had that artifact secreted away after the events at the Black Temple.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97706, 'enUS', '', 'And now you fools have brought me the key. I\'ll thank you to hand it over... willingly or otherwise.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97707, 'enUS', 'Ooohh... New subjects.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97708, 'enUS', 'A pity.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97709, 'enUS', 'Death becomes you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97710, 'enUS', 'Let\'s see what\'s behind door number...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97711, 'enUS', 'Your souls are mine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97712, 'enUS', 'How terribly disappointing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97713, 'enUS', 'Playtime has ended.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97714, 'enUS', '', 'You have brought my armor and glaive...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97715, 'enUS', 'Trial of Might sequence concluded.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97716, 'enUS', 'That is enough, outsider...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97717, 'enUS', 'Unbelievable! Decades of waiting without a worthy challenger, only to be beaten twice in the same day!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97718, 'enUS', 'You have passed the Trial of Might, outsider. Seek me at the vault\'s entrance, and I will grant you the blessing of the Watchers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97719, 'enUS', 'The mortal approaches the Ashbringer. To arms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97722, 'enUS', '', 'We have failed... Lord Balna...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97723, 'enUS', 'Glaive Throwers cannot withstand the terrain outside of the Ravencourt. Return immediately or you will be ejected.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97724, 'enUS', '', 'Destroy them! Leave nothing but ash!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97727, 'enUS', '', 'Squaaak! I\'ll tear you apart!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97728, 'enUS', '', 'Rip the flesh from its bones!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97729, 'enUS', '', 'A prize for the Witchmother!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97730, 'enUS', '', 'Squaaak! You\'ll regret this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97731, 'enUS', '', 'Shred its flesh! Scraaawk!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97732, 'enUS', '', 'I\'ll enjoy killing you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97733, 'enUS', '', 'Scraaawk! Make it bleed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97734, 'enUS', '', 'None shall survive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97735, 'enUS', '', 'Craaawwkk!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97736, 'enUS', '', 'Sisters! Avenge me...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97737, 'enUS', '', 'No... Squaaggk!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97738, 'enUS', '', 'Curses upon you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97739, 'enUS', '', 'Coward! You\'ll pay for this...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97740, 'enUS', '', 'No! NO! Craaawwkk...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97741, 'enUS', '', 'The village is... ours...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97742, 'enUS', '', 'OH GOD, WHAT IS THAT THING?! Someone, help! Please! It\'s tearing my room apart!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97743, 'enUS', 'It\'s in here! I know it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97744, 'enUS', 'Why won\'t this blasted cabinet open up!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97745, 'enUS', 'Just a taste, that\'s all I need! Just, one... little... TASTE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97746, 'enUS', 'Nothing will save you from our master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97747, 'enUS', 'I will awaken the darkness inside you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97748, 'enUS', 'You\'ll beg for death when I\'m done with you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97749, 'enUS', 'Get away from me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97750, 'enUS', 'Let me see your entrails!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97751, 'enUS', 'These potions are just delightful...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97752, 'enUS', 'Your moon goddess has no power here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97753, 'enUS', 'Fool! This land is already ours!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97754, 'enUS', 'You are too late... this land is ours!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97755, 'enUS', 'Our master will burn this world... and you with it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97756, 'enUS', 'Terror is all that awaits... you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97757, 'enUS', 'Raaarrgh...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97758, 'enUS', 'Mine are not the only hands... stained in blood...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97759, 'enUS', 'Death will not... stop us...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97760, 'enUS', 'Give up! There is no hope for this world...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97761, 'enUS', 'Your world is... doomed...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97762, 'enUS', '', 'Illidari $n, I present the Coilskar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97763, 'enUS', '', 'Lord Illidan sends his best wishes for a speedy success.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97764, 'enUS', '', 'I assume the foothold has been established?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97765, 'enUS', '', 'Lovely... another fel volcano.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97766, 'enUS', 'The crystal pulses faintly, but fails to locate any nearby prey....', 'The crystal pulses faintly, but fails to locate any nearby prey.....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97767, 'enUS', 'Master...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97768, 'enUS', 'I was a fool to resist the call of Xavius.. behold my power!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97769, 'enUS', 'Val\'sharah will... perish!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97770, 'enUS', 'Insects! I will stomp you into the ground!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97771, 'enUS', 'GRRGHH... My leaves... have fallen...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97772, 'enUS', 'So, you think yourself a hunter, using that little toy to lead you around? Let me show you true strength....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97773, 'enUS', 'The shadows.... Everywhere....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97774, 'enUS', 'What?! Oh, it\'s you, $n. I was just looking for something. My apologies. I\'ll return to the shop immediately.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97777, 'enUS', 'None may challenge the Brotherhood!', 'None may challenge the Brotherhood!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97778, 'enUS', 'Lap dogs, all of you!', 'Lap dogs, all of you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97779, 'enUS', 'Socrethar summons aid from portals around the room!', 'Socrethar summons aid from portals around the room!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97782, 'enUS', 'I apologize for the ruse, but I did not wish to reveal my identity in front of a tribe of known dragonslayers.$b$bI don\'t think they would approve of the real reason I am here.$b$bChampion, do you stand ready to assist me? I shall make it worth your while.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97783, 'enUS', 'Ready.', 'Ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97784, 'enUS', 'Very well, champion. Stay close.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97785, 'enUS', 'Be on your guard. I have no doubt Deathwing left many traps behind to guard these treasures.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97786, 'enUS', '', 'Ah, there you are. Lord Illidan has placed my Shivarra at your disposal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97787, 'enUS', '', 'A small force has infiltrated the Black Temple, but the Sha\'tari army is still being repelled.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97788, 'enUS', '', 'We are to obtain the Sargerite Keystone with all due haste and return to aid in the defense.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97790, 'enUS', '', 'If you will excuse us. Come my dears. Up the volcano to the foothold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97794, 'enUS', 'Not all of these eggs shall survive. But those that do should be free of Deathwing\'s corruption.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97795, 'enUS', 'Keep them off me just a little longer. One of the eggs is starting to hatch.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97796, 'enUS', 'Look! One survived. Such a small thing, and with little black horns.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97797, 'enUS', 'I shall call you Ebonhorn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97804, 'enUS', 'I will search ahead for the trove. Search for what you are after but do not keep me waiting. This is a dangerous place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97805, 'enUS', 'Excellent. Meet me at Krasus\' Landing when you are ready. I\'ve assembled the Argent Crusade\'s finest to join us to rescue Highlord Tirion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97806, 'enUS', 'The vrykul champion is nowhere to be found. Oh well, the heads of the other champions will have to suffice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97807, 'enUS', 'You have shown true valor today, and for this I will grant you a boon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97808, 'enUS', 'Hold aloft your weapon, outsider, and receive a gift from the Titans!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97809, 'enUS', 'It is done. May our power aid you when you need it most.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97810, 'enUS', 'Go forth, champion of the vault!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97811, 'enUS', 'You have shown yourself as a challenger truly worthy of the trials. It was an honor to test you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97812, 'enUS', 'You have one more trial, then we can talk about the Bloodtotem joining Mayla\'s crusade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97813, 'enUS', 'Take this drogbar to the cavern below. Slay the ettin. If you survive, we\'ll talk. If he survives, I\'ll leave his group of drogbar alone. Anything goes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97814, 'enUS', 'I am ready to receive your gift, Yotnar.', 'I am ready to receive your gift, Yotnar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97815, 'enUS', 'Know that I am Balnazzar. Know that you are but a pawn in plans beyond your reckoning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97816, 'enUS', 'You have no reason to trust me, but we will have to work together to survive this cave.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97817, 'enUS', 'This ettin is formidable. Beware its massive club.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97818, 'enUS', 'I will turn you into a weapon of the Legion. I will use your hollow shell to corrupt and destroy everything you have ever loved, piece by piece.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97819, 'enUS', 'Impressive. I did not expect you to attune to the Ashbringer so quickly. No matter, I will finish you off personally.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97820, 'enUS', 'I heard you may have seen Lyndras?', 'I heard you may have seen Lyndras?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97821, 'enUS', 'Haha! Such fun!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97822, 'enUS', 'I can get us out of here. I know a secret path.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97823, 'enUS', 'Let\'s get out of here!', 'Let\'s get out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97824, 'enUS', 'The Bloodtotem are merciless and bloodthirsty. I don\'t think they mean for either of us to make it out of here alive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97825, 'enUS', 'The forest will prevail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97826, 'enUS', 'Nature shall endure!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97827, 'enUS', 'My branches may bend... but they will not break!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97828, 'enUS', 'The wrath of nature is upon you, demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97829, 'enUS', 'Commander, over here!  I have the plans you are looking for.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97830, 'enUS', 'Pathetic creatures.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97831, 'enUS', 'Where is your goddess now?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97832, 'enUS', 'Claw marks can be seen around the manhole cover and a trail of ley dust leads off towards Violet Hold.', 'Claw marks can be seen around the manhole cover and a trail of ley dust leads off towards Violet Hold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97833, 'enUS', 'Look what wandered into my lair.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97834, 'enUS', 'Look Razzok, more target practice!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97835, 'enUS', 'I will add your corpse to the pile!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97836, 'enUS', 'All will fall before Xavius.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97837, 'enUS', 'You think you have won?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97838, 'enUS', 'Into darkness...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97839, 'enUS', '', 'Yea, I\'ve seen him. That cursed elf friend of yours came in here and stole one of my ley flame burners! I chased him as far as the sewers before he slipped away. \n\nIf you find him, make sure he returns that burner, they\'re very rare.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97840, 'enUS', 'I can supply you with the battleship plans you need, but first, you need to prove your worth to the Hand of the Prophet here in Tannan Jungle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97841, 'enUS', 'Outsider, you will never lay your hands on the Hammer of Khaz\'goroth! It is my destiny to wield it, not yours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97842, 'enUS', 'As for you, drogbar... We will wipe out your little cave after we throw your corpses off the cliff.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97843, 'enUS', 'Bloodtotem, let\'s end this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97844, 'enUS', 'I can supply you with the battleship plans you need, but first, you need to prove your worth to the Vol\'jin\'s Headhunters here in Tannan Jungle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97845, 'enUS', '', 'Your gift is worthy, $r. Now let me see about that cloth you need repaired.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97846, 'enUS', 'Foolish mortal! The Tears of Elune are mine now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97847, 'enUS', 'If Malfurion wants them back so badly, he can seek me out. I\'ll be waiting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97848, 'enUS', 'You may enter the hold, but I will not put the lives of my guards at risk for your mission. Is that clear?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97849, 'enUS', 'Very well. Do not make me regret trusting you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97850, 'enUS', 'As crystal, warden.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97851, 'enUS', 'Lower the barrier and evacuate the hold. These Illidari wish to... exchange words... with the eredar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97852, 'enUS', 'Kirin Tor Protector bows its head slightly in agreement.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97853, 'enUS', 'Your ancient rites and rituals mean nothing to me, Yotnar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97854, 'enUS', 'I will conquer your trials and take the Aegis for my own!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97855, 'enUS', 'Cower here in your cave, while it still stands!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97856, 'enUS', 'The gods will not tolerate your actions, vrykul! You will answer for your heresy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97857, 'enUS', 'Remember, demon hunter... you were never here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97858, 'enUS', 'Our foes approach! Let\'s go boys, For Lordaeron!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97859, 'enUS', 'Follow me. I shall open the cell for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97860, 'enUS', '', 'This should help you see a bit better.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97861, 'enUS', '', 'What was torn has now been mended. I hope you put the Shaldorei Silk to good use. Safe travels.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97866, 'enUS', '', 'Hi again. My charm should be somewhere around this pond.$B$BThe mark I have placed on you should help you locate it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97867, 'enUS', '$p?! You would stand before me after so many of your kin have fallen? Release me now and I may yet spare you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97868, 'enUS', 'I knew the Bloodtotem would never let us live!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97869, 'enUS', 'You lookin for that crazy elf? Yea, I seen\'im. He was down here making a huge ruckus and trying to munch on some sorta magical dust or something.$b$bI chased him off though, can\'t have the likes of him hanging around the shop.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97870, 'enUS', 'Where did he run off to?', 'Where did he run off to?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97880, 'enUS', 'Well, I chased him around until he scrambled up a pipe that leads up top in front of Violet Hold. You\'ll probably wanna go looking around up there somewhere.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97884, 'enUS', '', 'How about makin\' this place a little more festive, Commander?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97885, 'enUS', '', 'All this fighting must wear on you. How about a party?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97886, 'enUS', '', 'Don\'t you just love holidays? I know I do!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97887, 'enUS', 'Make my garrison ready for a Hallow\'s Eve party.', 'Make my garrison ready for a Hallow\'s Eve party.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97888, 'enUS', 'Make my garrison ready for a Winter Veil party.', 'Make my garrison ready for a Winter Veil party.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97889, 'enUS', 'The party\'s over. Get rid of these decorations, Izzy.', 'The party\'s over. Get rid of these decorations, Izzy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97890, 'enUS', '', 'One last sip of wine...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97895, 'enUS', '$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B$B|TINTERFACE\\ENCOUNTERJOURNAL\\UI-EJ-BOSS-ARCHIMONDEWOD.blp:50:80|t$B[Taldath the Destroyer] The Twinblades... will destroy you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97896, 'enUS', 'Where is he?! Where is Varedis? Tell us or die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97897, 'enUS', 'The Twinblades of the Deceiver could be exactly what we need to strike down the most powerful Legion lords.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97898, 'enUS', 'Lord Illidan taught us to fight fire with fire, to use our enemies\' power against them. I believe we\'ve found our fire.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97899, 'enUS', '$p, I will meet you at Krasus\' Landing and prepare a fel bat to take you to Suramar. Let the hunt begin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97900, 'enUS', 'We have lost so many today, I did not think another champion would make it through. Come, we have a hard fight ahead of us. Are you ready?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97901, 'enUS', 'Death comes soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97902, 'enUS', 'Taste my blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97903, 'enUS', 'Hunt this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97904, 'enUS', 'Pitiful.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97905, 'enUS', 'Die in glorious battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97906, 'enUS', 'For Tyranna!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97907, 'enUS', 'I\'m going to destroy you, little demon hunter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97908, 'enUS', 'Your soul will be mine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97909, 'enUS', 'A new prisoner for the taking.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97910, 'enUS', 'Your armies are nothing, demon hunter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97911, 'enUS', 'I\'ll crack open your flesh and feed upon your soul.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97912, 'enUS', 'Into my cage you go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97913, 'enUS', 'So eager to be enslaved.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97914, 'enUS', 'In this place, you are the hunted.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97915, 'enUS', 'I am your judge, jury, and executioner.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97917, 'enUS', 'Master Moonclaw! What has become of your grove?', 'Master Moonclaw! What has become of your grove?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97920, 'enUS', 'I understand.', 'I understand.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97934, 'enUS', 'Bear witness, foolish elves. I will crush your champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97935, 'enUS', 'No! How can... this be?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97947, 'enUS', 'These druids were weak... I hope you have more fight in you than they did!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97948, 'enUS', '', 'Getting low on arrows!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97949, 'enUS', '', 'They\'re coming around again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97950, 'enUS', '', 'Back, you bastards!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97951, 'enUS', '', 'Bring down that bat!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97952, 'enUS', '', 'On your left!', 26124);
