INSERT INTO `broadcast_text_locale` VALUES (98232, 'enUS', 'Please, use my table in the middle of the room to perform your tasks. You\'ll find some Runic Catgut on it as well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98233, 'enUS', 'Torok will not trouble the Stonedark again. Let\'s head to the homeland of my people.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98234, 'enUS', '', 'I just got a fresh shipment of Runic Catgut, $n. If your supply is low, you can buy some from me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98235, 'enUS', 'Let me know when you are ready to continue, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98236, 'enUS', 'No rush, Champion. We are just in the middle of a field of ettins. I am sure nothing bad could possibly happen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98237, 'enUS', 'We should really keep moving.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98238, 'enUS', 'Is it wise to wait in a field of ettins?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98239, 'enUS', 'You have proven yourself the greatest living warrior on Azeroth. It is for this reason I have saved you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98240, 'enUS', 'Why have you brought me here?', 'Why have you brought me here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98241, 'enUS', 'Alright, bruls, get to work!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98242, 'enUS', 'I have foreseen the coming of a mighty champion to these shores. One who would slay countless foes and prove their valor time and again. One who would lead my armies to victory against the great darkness that seeks to devour this world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98243, 'enUS', 'What do you propose?', 'What do you propose?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98244, 'enUS', 'I name you Valarjar! Bring glory to these halls on the wings of my val\'kyr!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98245, 'enUS', '', 'Oh, $n, I have some Spritethorns for sale if you need any for your bag recipe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98246, 'enUS', 'Keep your wits about you, $p. Varedis will be a cunning adversary.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98247, 'enUS', 'For Sylvanas!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98248, 'enUS', '', 'For Sylvanas!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98249, 'enUS', 'Death to the living!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98250, 'enUS', 'I will feast on your bones!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98251, 'enUS', 'Join us in death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98252, 'enUS', 'Abandon hope!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98253, 'enUS', 'You will suffer as we have!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98254, 'enUS', 'For the Dark Lady!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98255, 'enUS', 'Now you die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98256, 'enUS', '', 'Death to the living!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98257, 'enUS', '', 'I will feast on your bones!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98258, 'enUS', '', 'Join us in death!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98259, 'enUS', '', 'Abandon hope!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98260, 'enUS', '', 'You will suffer as we have!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98261, 'enUS', '', 'For the Dark Lady!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98262, 'enUS', '', 'Now you die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98263, 'enUS', 'Oh... not this again...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98264, 'enUS', 'Ah... at last...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98265, 'enUS', 'Dark Lady...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98266, 'enUS', 'No... no...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98267, 'enUS', '', 'Oh... not this again...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98268, 'enUS', '', 'Ah... at last...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98269, 'enUS', '', 'Dark Lady...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98270, 'enUS', '', 'No... no...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98271, 'enUS', 'They won\'t escape us. Illidari, attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98272, 'enUS', 'The fel lord is just ahead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98273, 'enUS', 'We cannot launch our attack until we\'re sure we won\'t be flanked.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98274, 'enUS', 'I would have sundered your forces...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98275, 'enUS', '', 'We\'re doing important work here in Feralas.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98276, 'enUS', '', 'Yes. Let\'s be certain to take care of that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98277, 'enUS', 'Deucus sent me. He says you can help calibrate this burette.', 'Deucus sent me. He says you can help calibrate this burette.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98278, 'enUS', 'Your aid to the Thorignir will not go unrewarded, outsider. $B$BIf you truly wish the blessing of our brood, then help us to rid this scourge from our home.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98279, 'enUS', 'The deeds you have done are great, but we have not finished here yet. $B$BSpeak to me when you are ready to ascend to the mountain\'s summit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98280, 'enUS', 'When you have finished your business here, speak to me and I will fly you to the summit. $B$BOnce we leave, we may not return.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98281, 'enUS', 'God-King Skovald summons a squad of Felskorn vrykul!', 'God-King Skovald summons a squad of Felskorn vrykul!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98282, 'enUS', 'Fly me to the summit, Vethir.', 'Fly me to the summit, Vethir.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98283, 'enUS', 'God-King Skovald summons a Felblaze Brute!', 'God-King Skovald summons a Felblaze Brute!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98284, 'enUS', 'God-King Skovald summons a pack of felstalkers!', 'God-King Skovald summons a pack of felstalkers!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98285, 'enUS', 'Odyn believes you are worthy to wield the Armaments of the Black Wyrm. I am here for Magnar Icebreaker. Let us both be successful, eh?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98286, 'enUS', '', 'Your ties to this reality are but threads. Care to cut a few?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98287, 'enUS', '', 'Don\'t be scared. My brews only show you what was already there to begin with.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98288, 'enUS', 'Who is Magnar Icebreaker?', 'Who is Magnar Icebreaker?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98289, 'enUS', 'Welcome, my friends, to the Vault of Neltharion. Or as the world came to know him: Deathwing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98290, 'enUS', 'Magnar Icebreaker was one of the greatest heroes of our time. He won many victories, became king, and crafted the greatest weapons I have ever seen, including the Armaments of the Black Wyrm.\n\nHe lost only one battle, his last. We were betrayed by jealous warriors led by cultists of Helira, that dark witch! They killed me first before we could react and he would not command after that.\n\nThey crafted his tomb with reverence, but he did not move on to the Halls of Valor. I hope his rage has not driven him to madness. He deserves to be here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98291, 'enUS', 'We gave everything for the Ashbringer... you gave everything, $n. Tirion would be proud to see you wielding that blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98292, 'enUS', 'Let us go to the tomb.', 'Let us go to the tomb.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98293, 'enUS', 'Come champion, I will need your help while the time scars are fresh.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98294, 'enUS', 'Welcome, my friends, to the Vault of Neltharian.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98295, 'enUS', 'Our journey will be a quick one. A single leap from Skyhold will take us to Azeroth. Follow me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98296, 'enUS', 'Wrathion?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98297, 'enUS', 'No. But if this is indeed Neltharian\'s Vault, he will prove useful.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98298, 'enUS', '', 'Can we trust this creature?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98299, 'enUS', 'Commander, over here!  I have the plans you are looking for.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98300, 'enUS', '', 'If you deep-dwellers touched a hair on Moozy\'s head you\'ll never leave that cave alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98301, 'enUS', '', 'Give Moozy back! Where is he?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98302, 'enUS', 'Krimple\'s the name, precision\'s my game!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98303, 'enUS', 'The others should be waiting for you in the sanctum beneath Light\'s Hope Chapel, $n. You should make haste.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98304, 'enUS', 'Don\'t know what ya heard, but my numbers are exact. EXACT, I tell ya!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98305, 'enUS', 'What a wonderful time of year!', 'What a wonderful time of year!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98306, 'enUS', 'No matter how many presents there are, we could always use more!', 'No matter how many presents there are, we could always use more!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98307, 'enUS', '\'Tis the season!', '\'Tis the season!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98308, 'enUS', 'Happy holidays!', 'Happy holidays!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98309, 'enUS', 'Let\'s make this the most wonderful holiday ever!', 'Let\'s make this the most wonderful holiday ever!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98312, 'enUS', 'What\'s the holiday without a lil\' smooch?', 'What\'s the holiday without a lil\' smooch?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98313, 'enUS', '', 'Lifespring Cavern is to the west. Meet me there when you can.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98316, 'enUS', '', 'There is harmony in precision, $r.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98317, 'enUS', 'A glorious landing! Are you ready to find Magnar?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98318, 'enUS', 'His tomb is not far.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98319, 'enUS', 'They buried him here after his only defeat...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98320, 'enUS', 'Desecrators! We must slay these abominations!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98321, 'enUS', 'A good fight! Follow me. I will open the tomb.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98322, 'enUS', 'The guardians do not obey me! Seems we will have to do this the hard way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98323, 'enUS', 'We should be able to leap over this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98324, 'enUS', 'Champion, we must save him! I will find the words to spur him on to the Halls of Valor, but he may resist the truth and turn his guards against me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98325, 'enUS', 'Say the word and we shall begin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98326, 'enUS', 'Father, it\'s me... your son. Odyn sent me here to--', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98327, 'enUS', 'My son died before my very eyes. I FAILED to protect him! You will not--', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98328, 'enUS', 'Gotta start somewhere!$B$BNow if you\'ll excuse me, I have some very important business to attend to.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98329, 'enUS', 'It IS me. I fell before you in the battle of the Pass. We were betrayed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98330, 'enUS', 'I fell in battle. We ALL fell. None survived the onslaught. Not even you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98331, 'enUS', 'Lies! Can you not see my faithful soldiers?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98332, 'enUS', 'You died in glorious battle! The Halls of Valor await. Come with me, father!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98333, 'enUS', 'Who are you, demon? Why do you torment me so?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98334, 'enUS', 'LIAR! You will DIE for saying his name! I shall send you to Helheim myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98335, 'enUS', 'Four, five... six...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98336, 'enUS', 'You are not my son! He is in the Halls of Valor, called by Odyn before his time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98337, 'enUS', 'I am the son of Magnar the Great, Champion of the Valarjar. Father, look at me! I am Hruthnir. I am your son!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98338, 'enUS', 'His weapons are yours now, champion. There is nothing left for us here. Let us feast in the Halls of Valor, together!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98339, 'enUS', 'Hmm... eight? Aw, not again...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98340, 'enUS', 'One, two...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98341, 'enUS', 'Such a small, fragile body. Yet it holds such power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98342, 'enUS', '', 'What is it? Precision requires focus, you know.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98343, 'enUS', 'Break time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98344, 'enUS', 'Welp, better get back to it. These presents won\'t count themselves.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98345, 'enUS', '', 'Hello again. A true, true friend helps a friend indeed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98346, 'enUS', '$n, you\'ve arrived! The Sanctum of Light is just ahead. This way.', '$n, you\'ve arrived! The Sanctum of Light is just ahead. This way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98347, 'enUS', 'Go forth, Ysera! Destroy all who stand in your way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98351, 'enUS', 'I would like to build another destroyer.', 'I would like to build another destroyer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98352, 'enUS', '', 'Deucus is an old fla--er, friend. I\'d be happy to calibrate the burette for you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98353, 'enUS', 'We checked every bulb, didn\'t we?$B$BMaybe we oughta check \'em again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98354, 'enUS', '', 'Let\'s go, $n. This moisture makes my joints hurt; make it snappy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98355, 'enUS', 'This place can never have too much holiday spirit!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98356, 'enUS', 'Lousy holiday bonus. Who needs a Smokeywood Pastures lifetime membership anyway?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98357, 'enUS', 'You didn\'t happen to see a squirrel running about, did you? Damn thing came in with the trees...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98358, 'enUS', 'Could use a few more decorations around here. Wouldn\'t want our neighbors showing us up with their decorations this season, would we?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98359, 'enUS', 'I will not be undone by some mortal filth! I am the Earth Warder!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98360, 'enUS', '', 'There, much better. Please give my best to Deucus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98364, 'enUS', 'I would like to build a destroyer.', 'I would like to build a destroyer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98365, 'enUS', '', 'Deucus Valdera? A blustery fellow, but honorable. I will be pleased to assist you with calibration.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98367, 'enUS', '', 'I\'ve done my best to calibrate the burette. Give Deucus my regards.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98369, 'enUS', 'Deucus? That undead has some nerve! Called me a cheater once... but I suppose I can forgive him. Lemme see...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98370, 'enUS', '', 'Behind you lies the Eye of Odyn, which peers across all of Azeroth! This way lies the Forge, where the mightiest weapons of the valarjar are crafted by Helgar, the greatest smith in the Halls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98371, 'enUS', 'Here ya go! Tell Deucus I treated ya fair \'n square!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98372, 'enUS', '', 'Odyn awaits ahead. I will go forth and announce you. Be respectful!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98374, 'enUS', '', 'Deucus? I thought he was dead! Of course I would be happy to help my old friend.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98375, 'enUS', '', 'Perfect calibration! Tell Deucus I shall make a trip to Dalaran soon to say hello.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98376, 'enUS', 'The Data Extratinator 3000 might be able to decipher this core. Cross your fingers that it doesn\'t explode in the process.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98377, 'enUS', 'Deucus always sends me the most challenging puzzles to solve! Let\'s see that burette!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98378, 'enUS', 'This core contains a script written in an ancient Highborne dialect, which I can\'t read. Professor Pallin in the Scribes\' Sacellum may be able to translate this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98379, 'enUS', 'Perfection! You can now make potions with precise exactitude!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98380, 'enUS', 'I will signal my contacts on the other side of the tunnel. Meet me there as soon as you are able.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98381, 'enUS', 'Thank you again, hero. You have just given the Black Dragonflight a second chance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98382, 'enUS', '', 'I really must make a trip to Dalaran sometime.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98383, 'enUS', 'Start construction on our first transport.', 'Start construction on our first transport.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98385, 'enUS', 'Battlelord, your forces will assault The Doom Fortress to the southeast. Stop the mass of their demons from attacking us.', 'Battlelord, your forces will assault The Doom Fortress to the southeast. Stop the mass of their demons from attacking us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98386, 'enUS', 'The dead are rising! Something is not right...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98387, 'enUS', 'Rest well, my brother.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98388, 'enUS', 'These warriors are restless! They must sense some kind of disturbance...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98389, 'enUS', 'Onward! We are almost to Magnar\'s tomb.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98390, 'enUS', '', 'Watch yourself down there... that doesn\'t look like Westfall stew in those cauldrons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98391, 'enUS', '', 'Hop on. We\'ll check every ship if we have to!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98392, 'enUS', '', 'Slay these unholy creatures! For the Alliance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98393, 'enUS', '', 'No sign of her. Let\'s keep looking.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98394, 'enUS', '', 'Those are royal guards... This must be her flagship. Seize the captain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98395, 'enUS', '', 'The Skyfire is being overrun. Come on, we have to help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98396, 'enUS', 'My thanks, brul! I shall remember you always.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98397, 'enUS', 'We are free? Free? No more dragons? No more wyrms?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98398, 'enUS', 'Huln said he would free us. Are you with Huln?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98399, 'enUS', 'Death to the tyrants! Death to Deathwing and his brood!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98400, 'enUS', 'It will be as you say.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98402, 'enUS', 'Ashtongue with me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98403, 'enUS', 'Please forgive me: I must warn the others. Elune be with you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98404, 'enUS', '', 'Most powerful Odyn, I bring you $n. They have slain the great pit lord Malgalor showing true valor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98410, 'enUS', 'Behold my warriors! See my greatest champion, Skylord $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98414, 'enUS', '', 'Champion! It\'s good to see you. Vrykul aren\'t known for their hospitality.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98415, 'enUS', '', 'Psst... over here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98416, 'enUS', '', 'Champion, over here! Quick, before the drakes catch your scent!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98417, 'enUS', '', 'The God-King hasn\'t won yet. Let\'s take those dragons back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98418, 'enUS', '', 'For Gilneas!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98419, 'enUS', '', 'Hey, it\'s you! I\'m getting stir-crazy here - please tell me we\'re going in!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98420, 'enUS', '', 'This way, there\'s a good spot right here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98421, 'enUS', '', 'Hey, over here! I\'ve got a plan!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98422, 'enUS', '', 'I feel like that guy having a dragon is a bad thing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98423, 'enUS', '', 'Yes! Finally, some action!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98424, 'enUS', 'Lower your weapon, outsider. I do not wish to harm you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98425, 'enUS', 'Wait... you are not like the others...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98426, 'enUS', 'The demon binds me... please help...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98427, 'enUS', 'The Drekirjar have upset our ancient pact.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98428, 'enUS', 'It is time they face the consequences.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98429, 'enUS', 'Even as someone not of this land, you chose to save us from a wretched fate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98430, 'enUS', 'You have passed the Trial of Will, and you have also gained an ally.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98431, 'enUS', 'Hold aloft your weapon, outsider. Accept the power of storm and thunder as a gift for your selfless acts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98432, 'enUS', 'Take wing with Vethir, friend, and show these vrykul the true fury of the storm!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98433, 'enUS', 'The storm will purge Skovald from this land!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98434, 'enUS', 'The unworthy will be thrashed in the gale winds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98435, 'enUS', 'Suffer the wrath of the Thorignir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98436, 'enUS', 'The Thorignir owe you a debt of gratitude, outsider.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98437, 'enUS', 'Your trials are far from over. When you need me again, I shall be ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98438, 'enUS', 'It seems that a messenger awaits. May the gale winds carry you, friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98439, 'enUS', 'Two trials faced and one remains.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98440, 'enUS', 'You must seek the judgment of the ancient kings, but they are dead and you are not.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98441, 'enUS', 'There is one who can bridge the gap, if you indulge him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98442, 'enUS', 'Meet me at Valdisdall, and I will spin the tale.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98443, 'enUS', 'In Haustvald, the Bonespeakers keep the dead. They will not help you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98444, 'enUS', 'Their ways are twisted. They have forgotten the old vows.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98445, 'enUS', 'But there is one who remembers. One who is rooted in the old ways.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98446, 'enUS', 'Seek Vydhar! Earn your judgment, and pass the Trial of Valor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98447, 'enUS', 'Dying roots.. twisted leaves... the runestones...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98448, 'enUS', 'I can hear... the woods call to me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98449, 'enUS', 'Must break free... I can almost feel my leaves...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98450, 'enUS', 'I am unbound! My roots will seek the hearts of my betrayers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98451, 'enUS', 'Ahh... oohh...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98452, 'enUS', 'Who goes there? Your footsteps fall as acorns. Stand before me, little one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98453, 'enUS', 'I hear you\'re an expert on naval equipment.  Have some time to help me out?', 'I hear you\'re an expert on naval equipment.  Have some time to help me out?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98454, 'enUS', '', 'You will not force my judgment, Runeseer. This fel-tainted wretch will not befoul the Halls of Valor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98456, 'enUS', 'I grow impatient, Runeseer. Finish this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98459, 'enUS', 'Give in, Ashildir. If your spirit will not bend, then it will be broken!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98460, 'enUS', '', 'Champion! Destroy this usurper, and I will grant you my judgment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98461, 'enUS', 'You think to stop me? I am the master of the vengeful dead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98462, 'enUS', '', 'The runestone grants him power, champion. Destroy it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98463, 'enUS', 'These are just some of the artifacts collected by Deathwing in his day. Collect them and I will see they are properly investigated later.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98464, 'enUS', 'You were a fool to meddle. My armies are eternal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98465, 'enUS', '', 'Sisters, to arms! Show this coward the meaning of valor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98466, 'enUS', '', 'He\'s empowering another runestone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98467, 'enUS', 'Enough of this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98468, 'enUS', '', 'No! This power... it\'s too strong!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98469, 'enUS', 'You should have yielded, Ashildir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98470, 'enUS', 'The God-King will not need your judgment if you are no longer among the honored dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98471, 'enUS', '', 'No... you can\'t...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98472, 'enUS', 'I can. You have sealed your fate, shieldmaiden. You and this... outsider.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98473, 'enUS', 'Enjoy your eternity of damnation. Helheim awaits!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98474, 'enUS', '', 'I am sorry, hero. I have doomed us both.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98475, 'enUS', '', 'Perhaps this cowering wretch might be of some use.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98476, 'enUS', '', 'Disgusting! This plan better work, spirit.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98477, 'enUS', '', 'Such a revolting disguise. My armor still reeks of seaweed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98479, 'enUS', '', 'Remember our bargain, banshee. Do not dare to cross me...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98480, 'enUS', '', 'What have we here? Another one of Greymane\'s mercenaries?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98481, 'enUS', '', 'No matter. Soon you will witness the birth of a new age.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98482, 'enUS', '', 'If you ever make it out of here, that is.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98483, 'enUS', '', 'Ah, my loyal hero.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98484, 'enUS', '', 'You must not languish here. The Aegis cannot fall into enemy hands!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98485, 'enUS', '', 'The future of the Horde depends on you and me. Find your way back to Stormheim - I will be waiting.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98486, 'enUS', 'I can help ya out, $c.  I remember seeing some documents at the end of the docks.  Head down there and take a look.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98487, 'enUS', '', 'Unworthy! You will kneel before addressing the great Helya!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98488, 'enUS', '', 'Your spirit feels... weaker. What has she done?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98489, 'enUS', '', 'Have fun with my pet, outsider. I will enjoy watching him break you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98490, 'enUS', '', 'Our foe is great, champion, but we will win the day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98491, 'enUS', '', 'You are clever, outsider, but your efforts will only prove futile.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98492, 'enUS', '', 'Do not think you are close to winning, unworthy one.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98493, 'enUS', '', 'How resourceful you are. You might make a worthwhile plaything yet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98494, 'enUS', '', 'The time for planning is over, champion. Now, we take action!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98495, 'enUS', '', 'No! My darling pet! You will pay for this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98496, 'enUS', '', 'It is done, Helya! We have fulfilled your bargain, and we will collect our payment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98497, 'enUS', '', 'It is done, Helya! We have fulfilled your bargain, and we will collect our payment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98498, 'enUS', '', 'Such insolence! None will leave Helheim while I rule!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98499, 'enUS', '', 'What? This cannot be!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98500, 'enUS', '', 'Her hold is weakening. She cannot keep us here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98501, 'enUS', '', 'No one leaves Helheim! No one!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98502, 'enUS', '', 'Your terms were met, Helya. It seems even you must honor your word.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98503, 'enUS', '', 'Nooooo!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98504, 'enUS', 'What is it you seek?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98505, 'enUS', 'The $ct approaches! Knights, salute!', 'The $ct approaches! Knights, salute!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98506, 'enUS', 'The vault before you holds not gold, but wisdom.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98507, 'enUS', 'Yotnar is its lock, and glory is its key.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98508, 'enUS', 'Forge a key in the blood of champions, and perhaps the lock will turn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98509, 'enUS', 'Inside you will find paths... and more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98510, 'enUS', 'The trials are won, but your foe remains.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98511, 'enUS', 'His tainted flock bars the halls of the righteous.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98512, 'enUS', 'To sever this head, you must cripple the body.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98513, 'enUS', 'But first... Stormheim\'s savior has someone very important to save.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98514, 'enUS', 'I.. will not... yield!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98515, 'enUS', 'At last! It seems I owe you another life, friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98516, 'enUS', 'Let us stem this tide of filth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98517, 'enUS', 'No mercy for those who taint this land!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98518, 'enUS', 'The demons keep coming! There must be another source!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98519, 'enUS', 'I will fly you to the top when you are ready, friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98520, 'enUS', 'Let us put an end to Skovald\'s minions once and for all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98521, 'enUS', 'Fool! You cannot hope to stop my endless army.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98522, 'enUS', 'Hero, the eredar is shielding our foe!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98523, 'enUS', 'My soldiers are limitless!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98524, 'enUS', 'You cannot fight forever!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98525, 'enUS', 'The tide of destruction is unending!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98526, 'enUS', 'No! You cannot end... the endless...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98527, 'enUS', 'We are done here, champion. Let us return to Havi.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98528, 'enUS', 'The God-King\'s demon allies will no longer threaten Stormheim.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98529, 'enUS', 'It is time you pursue Skovald into the high halls.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98530, 'enUS', 'A great battle, a greater champion. Yes, you are worthy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98531, 'enUS', 'Enter Halls of Valor, champion. The greatest of glory awaits!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98532, 'enUS', '', 'They\'re trying to bring the ship down from the inside.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98533, 'enUS', 'Death Knights of Acherus, I send to you $n who will serve as the enforcer of my will. Follow his commands as you would my own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98534, 'enUS', 'For Huln, the War of the Ancients did not end at the Well of Eternity. With the Legion defeated, he focused on the monster that got away.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98535, 'enUS', 'Be warned, the path ahead is quite dangerous. I can only provide you guidance and healing. Nothing more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98536, 'enUS', 'History is forged by the hearts of heroes, child. This is the truth your father wanted you to see.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98537, 'enUS', 'Come now, High Chieftain. It is only an ettin. I\'m sure you will be fine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98538, 'enUS', 'Spiritwalker, have you any spells that might assist us with healing?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98539, 'enUS', 'Insidia is all that remains of the flight in this tomb. We must be wary, one does not lift a weapon against a daughter of Deathwing without suffering consequences.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98540, 'enUS', 'If it means freedom for my people, then I welcome any consequences she throws at us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98545, 'enUS', 'The High Chieftain asked for me to join him in this negotiation.$b$bThe Highmountain tribe is spoken of in our legends and remembered with reverence.$b$bIt was important that we show respect for such an ancient tribe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98546, 'enUS', 'It has been many years since my people swore our loyalty to the Horde.$b$bThe Horde aided my people in our time of need, and I am here to speak well of that accord.$b$bThe Highmountain tribe is known even to us in the North, and respect must be paid.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98547, 'enUS', '', 'I\'ll cover you. Go help Greymane!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98548, 'enUS', 'We make a good team, you and I. The alchemy table looks good as new!$b$bOne day we\'ll look back on this whole vrykul ghost mishap and have a good laugh. Thank you, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98549, 'enUS', 'Greetings champion.$b$bWe are dangerously low on allies in this land and the Highmountain tribe is the only tribe in known tauren history to lock horns with the Burning Legion.$b$bI am here to lend whatever aid they need so that they can unite under the same totem once again and join us in the fight against the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98550, 'enUS', 'The dead are everywhere! Their souls must be sworn to Magnar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98551, 'enUS', 'The Ashbringer...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98552, 'enUS', 'I have another question.', 'I have another question.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98553, 'enUS', '', 'A fine task for the Coilskar. I am in agreement.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98554, 'enUS', '', 'Let us be done with this business. A volcano is no place for naga.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98555, 'enUS', 'Milady, you will take your naga north. Disrupt the ritual the demons are performing at their Soul Engine.', 'Milady, you will take your naga north. Disrupt the ritual the demons are performing at their Soul Engine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98556, 'enUS', '', 'The Path of Huln was something I knew I might have to walk some day. I had just hoped I could wait until after dealing with Dargrul to walk it.$b$b', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98557, 'enUS', 'Let us observe a moment of silence for a great soldier, leader, and friend. In the Light we are one, brother. Always.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98558, 'enUS', 'So this is the Vault of Neltharian.$b$bNow I see why Wrathion was so eager to visit this place. The secrets here could easily turn the tide in any war.$b$bBut this place has been abandoned by the living for thousands of years.$B$bWe should be on our guard. There is no telling what sort of sorcery and traps were left behind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98559, 'enUS', 'We must stand together against the coming darkness. We must cast aside the shackles of race and kingdom. We must unite as one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98560, 'enUS', '', 'We will see it done. I urge you to remember that the Black Temple is under attack.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98561, 'enUS', '', 'Time is of the essence. Come, sisters, our former brethren await our tender mercies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98562, 'enUS', 'Matron Mother, I need you to take your shivan across the gap to the Forge of Corruption. Make them suffer.', 'Matron Mother, I need you to take your shivan across the gap to the Forge of Corruption. Make them suffer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98563, 'enUS', '', 'Your gift is appreciated, $r. These potions will be most useful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98564, 'enUS', 'Approach, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98565, 'enUS', 'Greetings, Leyweaver. I offer this gift in hopes that you will mend some silk for me.', 'Greetings, Leyweaver. I offer this gift in hopes that you will mend some silk for me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98566, 'enUS', 'I would be honored.', 'I would be honored.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98567, 'enUS', 'Alright, let\'s see what we can do with this ore. First things first: we heat the ore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98568, 'enUS', 'Now let\'s see what we can make with it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98569, 'enUS', 'And now for the simple step of applying the flux. Lean in closely and watch my technique.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98570, 'enUS', 'Then turn around and be recognized!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98571, 'enUS', '', 'I am in your debt, hero. Come. Let us destroy these demons together!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98572, 'enUS', 'Champion: The Skyfire is about to embark on an urgent mission. I must ask that you accompany them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98573, 'enUS', '', 'Thank you, champion! Lead on. We must help the other defenders!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98574, 'enUS', '', 'Enemy reinforcements headed this way!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98575, 'enUS', '', 'Our enemies are launching a new attack!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98576, 'enUS', 'Report to Stormwind Harbor as soon as possible - they are under strict orders not to leave without you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98577, 'enUS', '', 'Incoming! Defend the temple!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98578, 'enUS', '', 'Orders? Do you need anything repaired before we head out?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98579, 'enUS', 'I trust that your valor and discretion will serve the Alliance well, as it always has. With honor, King Anduin Wrynn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98580, 'enUS', 'Yes, thank you, Nightsong. Will you see to my equipment?', 'Yes, thank you, Nightsong. Will you see to my equipment?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98581, 'enUS', '', 'Another wave of attackers! Hold the line!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98582, 'enUS', '', 'Fresh adversaries approach! Fight on!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98583, 'enUS', '', 'New foes incoming! Brace yourselves!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98584, 'enUS', '', 'By Elune! Clearly Ysera is beyond saving. All we can do is end her suffering.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98585, 'enUS', '', 'We have no choice: Ysera must die. She would agree, were she in her right mind.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98586, 'enUS', '', 'Stand in Elune\'s light, and her blessing will strengthen your attacks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98587, 'enUS', '', 'Shine your light down, Mother Moon, and lend us your strength!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98588, 'enUS', '', 'O Elune, grant us your light and strengthen us for what we must do!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98589, 'enUS', '', 'Let me bear the brunt of Ysera\'s attacks, hero. You can rely on me: I am quite sturdy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98591, 'enUS', 'Shaping the ore...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98592, 'enUS', 'Look how easily it bends...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98593, 'enUS', 'The Light gives us strength.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98594, 'enUS', 'Please address the gathered paladins on my behalf, Gryan.', 'Please address the gathered paladins on my behalf, Gryan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98595, 'enUS', 'Come to me, my impish kin! We shall devour the power within!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98596, 'enUS', 'You seem delirious!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98597, 'enUS', 'But I am mysterious...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98598, 'enUS', 'You\'ll catch me soon, friend...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98599, 'enUS', 'Why... so serious...?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98600, 'enUS', '', 'The Blood Knights stand by you, Highlord $p. Azeroth will not fall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98601, 'enUS', '', 'Welcome to Skyhold! Here the brave live on forever!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98602, 'enUS', 'We pledge ourselves to your cause, Highlord!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98603, 'enUS', '', 'Champion, forgive the interruption. I bring an urgent message from the king.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98604, 'enUS', '', 'Stormheim is home to one of the Pillars of Creation - the Aegis of Aggramar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98605, 'enUS', '', 'Vrykul legends speak of a trial - to conquer this trial is to prove one\'s worth, and lay claim to the Aegis.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98606, 'enUS', '', 'Our official orders are to escort you to Stormheim and ensure your success on this trial.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98607, 'enUS', '', 'Impressive, aren\'t they? The new guns could bag a wyvern at full speed from three hundred meters. Go ahead, have a look.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98608, 'enUS', 'I\'ll just be upstairs so I can better observe your actions... at a safe distance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98609, 'enUS', '', 'Unofficially, however, we have another mission...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98610, 'enUS', '', 'The 7th Legion. The toughest men and women the Alliance has to offer. They, too, have come on direct orders from the king.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98611, 'enUS', '', 'Three days ago, the Forsaken fleet set sail from Durotar, heading straight for the Broken Isles. We think Sylvanas Windrunner herself may be among them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98612, 'enUS', '', 'We are to track them from a safe distance. We may engage, but only if the situation demands.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98613, 'enUS', '', 'I strongly suspect the situation will demand it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98614, 'enUS', 'It had better.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98615, 'enUS', 'I am not in the habit of tracking prey unless I intend on killing it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98616, 'enUS', '', 'Preparations are nearly complete. Please make yourselves comfortable.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98617, 'enUS', 'The spider fortress, the soul engine, and the forge are their primary targets. The Servants of Illidan must succeed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98618, 'enUS', 'It worked?! I mean... it worked! Of course it did. That\'s excellent news.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98619, 'enUS', 'Alright, let me see the gauntlet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98620, 'enUS', 'Are you ready to fly? You\'ll have our fastest mount.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98621, 'enUS', 'Take me to the Broken Shore.', 'Take me to the Broken Shore.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98622, 'enUS', '', 'Let me help you get where you want to go. As you travel through the Broken Isles I will be able to get new destinations from you as well! Keep exploring!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98623, 'enUS', 'Dalaran.', 'Dalaran.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98624, 'enUS', 'Stormheim.', 'Stormheim.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98625, 'enUS', 'Azsuna.', 'Azsuna.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98626, 'enUS', 'Val\'sharah.', 'Val\'sharah.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98627, 'enUS', 'Highmountain.', 'Highmountain.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98628, 'enUS', 'Suramar.', 'Suramar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98629, 'enUS', '', 'I have a wide selection of recipes available.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98630, 'enUS', 'What can you tell me about this alchemy table?', 'What can you tell me about this alchemy table?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98631, 'enUS', 'Gotcha!', 'Gotcha!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98632, 'enUS', 'Hehe!', 'Hehe!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98633, 'enUS', 'That one\'s free!', 'That one\'s free!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98634, 'enUS', 'Oof! That had to hurt.', 'Oof! That had to hurt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98635, 'enUS', '', 'Champion: As your new Warchief, I share with you the burden of securing our future.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98636, 'enUS', '', 'To that end, my fleet is preparing to sail for the Broken Isles. When you set foot in Stormheim, you shall have the full power of the Forsaken at your back.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98637, 'enUS', '', 'Make the journey with us. Together, we will ensure that no one can oppose the might of the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98638, 'enUS', '', 'Isn\'t our alchemy table wonderful? Deucus spent years assembling the components. He\'s so proud of it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98639, 'enUS', 'It\'s a shame what happened to the alchemy table.', 'It\'s a shame what happened to the alchemy table.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98640, 'enUS', 'Hey!', 'Hey!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98641, 'enUS', '', 'Are you familiar with the vrykul? Towering, brutish warriors from an ancient stock. Subtlety is not a value they hold in high regard.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98642, 'enUS', 'Wow, that\'s cold!', 'Wow, that\'s cold!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98643, 'enUS', 'Haha. This is great!', 'Haha. This is great!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98644, 'enUS', 'I\'m gonna get you next!', 'I\'m gonna get you next!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98645, 'enUS', '', 'That awful Svena made a mess of the alchemy table. Poor Deucus! I know he hides it, but he\'s heartbroken.$b$bI certainly hope you can help him finish rebuilding it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98646, 'enUS', '', 'Their legends speak of the Aegis of Aggramar. To claim it, one must undergo some kind of trial. One must be... worthy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98647, 'enUS', 'The alchemy table looks good as new!', 'The alchemy table looks good as new!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98648, 'enUS', '', 'The alchemy table looks terrific! Deucus is so relieved. Thank you for all the work you did restoring it.$b$bHere\'s hoping we don\'t see another vrykul ghost in the shop ever again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98649, 'enUS', 'I\'d like to buy from you.', 'I\'d like to buy from you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98650, 'enUS', '', 'The new catapults are faster and more powerful than ever before. I have our goblin allies to thank for that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98651, 'enUS', '', 'I trust you are more than capable of handling whatever trials stand between you and the Aegis.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98652, 'enUS', '', 'Truthfully, Stormheim holds another treasure. One that I will be pursuing by my own means.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98653, 'enUS', '', '<Setting her vigil aside for a moment, the majestic druid turns to look at you. Her dark eyes widen in recognition and warmth.>$b$b<Without a word, she makes it clear that you will always be welcome here.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98654, 'enUS', '', 'Nathanos has been training a new generation of rangers. They represent both our past, and our future.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98655, 'enUS', '', 'I think often about our future, hero. My people cannot reproduce. Are we not destined, then, to wither away eventually?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98656, 'enUS', '', 'No. The Forsaken will never stop fighting.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98657, 'enUS', '', 'Not while I reign. Not when there are still beings out there with the power to stop death.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98658, 'enUS', '', 'News from Captain Dreadwake, my lady. They have found the temple.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98659, 'enUS', '', 'The Oblivion has landed nearby. They are building fortifications now and will be ready to lay siege at your command.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98660, 'enUS', '', 'Good. Send a bat - tell them we are leaving shortly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98661, 'enUS', 'To hammer the metal is to hammer yourself. How far are you willing to bend, greenhorn?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98662, 'enUS', '%s eyes Reylar the Stormcaller cautiously.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98663, 'enUS', 'What sorts of things do you sell?', 'What sorts of things do you sell?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98664, 'enUS', 'Do not simply use your arm to swing the hammer. You have your torso, your legs, your spirit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98665, 'enUS', 'Do not be a slave to your hammer. It is an extension of your arm. YOU are its master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98666, 'enUS', '', 'I sell the recipes that Deucus and I research together. As we learn new ones, I add them to my inventory.$b$bBe sure to check back from time to time to see if anything new is available!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98667, 'enUS', 'We will send Gul\'dan\'s filth to the bottom of the sea!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98668, 'enUS', 'You there! I require a moment of your time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98669, 'enUS', '', 'I am the chosen of Eyir! What chance do think you have?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98670, 'enUS', '', 'I live. I die. I...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98671, 'enUS', 'We have a lot of work ahead of us to fix the alchemy table!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98672, 'enUS', 'We did it! The alchemy table looks good as new!$b$bWith my brains and your brawn, we make a great team. Thank you, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98673, 'enUS', 'Lord Shadowbreaker will be expecting you, $n. This way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98674, 'enUS', 'Lord Shadowbreaker, the Highlord would like to peruse the reconnaissance you\'ve gathered on the Broken Isles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98675, 'enUS', 'This is my favorite holiday!', 'This is my favorite holiday!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98676, 'enUS', 'Snow for everyone!', 'Snow for everyone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98677, 'enUS', 'Snowball incoming!', 'Snowball incoming!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98678, 'enUS', 'You go SQUISH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98679, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98680, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98681, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98682, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98683, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98684, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98685, 'enUS', 'These spirit winds are relentless! We cannot walk through.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98686, 'enUS', '%s ponders the situation at hand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98687, 'enUS', 'I can charge from here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98688, 'enUS', 'His madness infects his guards, they do not recognize me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98689, 'enUS', 'Watch out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98690, 'enUS', 'That door is sealed with lightning. We must find a way through!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98691, 'enUS', 'Like I always say, if you can\'t reach it, throw something at it. Never fails!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98692, 'enUS', 'Here we are... Magnar\'s chamber!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98694, 'enUS', 'I am ready.', 'I am ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98695, 'enUS', 'Knights of the Ebon Blade! Assemble as we honor one of our own!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98696, 'enUS', 'You have all heard the legends of countless battles fought... of trials overcome at great cost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98697, 'enUS', 'His exemplary deeds in the name of the Ebon Blade have shown him to be one of the best of us, worthy of our highest honor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98699, 'enUS', 'By the will of the Lich King and upon my authority as Highlord, I declare that $n, wielder of the Blades of the Fallen Prince, will henceforth be known as Deathlord of the Ebon Blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98700, 'enUS', 'By the will of the Lich King and upon my authority as Highlord, I declare that $n, wielder of the Maw of the Damned, will henceforth be known as Deathlord of the Ebon Blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98701, 'enUS', 'By the will of the Lich King and upon my authority as Highlord, I declare that $n, wielder of Apocalypse, will henceforth be known as Deathlord of the Ebon Blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98702, 'enUS', '<As you approach, the regal druid regards you with friendly curiosity.>$b$b<After a moment, he gives a chuff of approval and lowers his stately head before you.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98703, 'enUS', 'You will all bow to him as you once did to me, and carry out his will against the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98704, 'enUS', 'There is no time! Xavius must be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98705, 'enUS', '', 'Easy now. This remedy takes time. You must rest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98706, 'enUS', 'S - Save the temple! <choking sound> Hurry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98707, 'enUS', 'Ysera is going to destroy the Temple of Elune!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98708, 'enUS', '', 'The Wardens seek our help? Things must be desperate.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98709, 'enUS', '', 'Ahhhh, it feels good to MOVE again. Thank you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98710, 'enUS', '', '$n, it has been a long time since Mardum. I am ready for another fight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98711, 'enUS', '', 'We will need weapons and supplies if we are to break out of here. I am on it, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98712, 'enUS', '', 'Years and years with only the voices in my head. I thought I would go insane.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98713, 'enUS', 'What a wonderful time of year!', 'What a wonderful time of year!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98714, 'enUS', 'No matter how many presents there are, we could always use more!', 'No matter how many presents there are, we could always use more!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98715, 'enUS', '\'Tis the season!', '\'Tis the season!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98716, 'enUS', 'Happy holidays!', 'Happy holidays!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98717, 'enUS', 'Come here goaties!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98718, 'enUS', 'Stop moving! Me can\'t keep track.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98719, 'enUS', 'You too fast. Stay!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98720, 'enUS', 'Hello again, Linzy. What\'s new with you?', 'Hello again, Linzy. What\'s new with you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98721, 'enUS', '', 'Oh, you know, nothing much. Just helped move an entire city from the frozen north to a haunted keep, then on to a chain of demon-infested islands! All in a day\'s work, I suppose.$b$bI\'m semi-retired these days, so having Deucus take over has been wonderful. He\'s a good sort... if perhaps a bit overly fond of his alchemy table!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98722, 'enUS', 'Here to pillage our wares, demon?! You shall take NOTHING!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98723, 'enUS', 'Wait! Stop! You\'re... you\'re no demon...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98724, 'enUS', 'Sounds like an exciting life!', 'Sounds like an exciting life!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98725, 'enUS', 'The fog has lifted and I can see the truth once more. Thank you, $pc. If you would join me at my old shop, perhaps we can speak further.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98726, 'enUS', '', 'I admit, I never would have guessed that my work as an alchemist would be so thrilling!$b$bBut look who I\'m talking to... you\'ve seen far more adventure than I have!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98727, 'enUS', '', 'Wake up, demon hunter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98729, 'enUS', 'How have you fared since Northrend?', 'How have you fared since Northrend?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98730, 'enUS', '', 'I have been well, $n. The fall of the Lich King was a wonder to behold. If only all of Northrend\'s troubles had been solved by that victory.$b$bAh, but the past is the past. It feels great for Dalaran to be on the front lines of a new campaign! Though I admit, I\'m grateful you\'re out there fighting the battles instead of me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98731, 'enUS', '', 'Take up your weapons, $n. I trust you can still handle a blade.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98732, 'enUS', 'I will burn this world with my fury!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98734, 'enUS', '', 'I will need your help to stop Gul\'dan. Free the other Illidari, quickly!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98735, 'enUS', 'I also wanted to ask about...', 'I also wanted to ask about...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98736, 'enUS', 'The Highlord\'s will be done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98737, 'enUS', 'Time for answers later. There are demons to kill.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98738, 'enUS', 'The demons aim to destroy our world. We need to release our allies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98739, 'enUS', 'Well now, you have been turnin\' quite a lot of heads lately amongst the Explorer\'s League.$b$bOl\' Ebonhorn here seemed to know who I was and asked fer me to come help ya on this next journey.$b$bI hope ya don\'t mind but I dusted off me ol\' Talas here. I heard there were ettins involved and figured we could use us some heavy metal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98740, 'enUS', '', 'It worked! $GHe\'s:She\'s; alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98741, 'enUS', '', 'Of course it worked! I\'ve woken up things that were twice as dead for three times as long. This one was easy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98742, 'enUS', 'I will stay here and await Gryan\'s return, Highlord.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98743, 'enUS', 'The altar lies just behind you, Highlord.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98744, 'enUS', 'Thork - the northern portal is destroyed! We will push forward beyond Grol\'dom and make way toward the north.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98745, 'enUS', 'Place the blade on the altar when you are ready. The Light will be our forge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98746, 'enUS', '', 'Our way is clear! Let\'s get to the top of the tower!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98747, 'enUS', '', 'You go ahead, I\'ll report back to Greymane. Good luck, champion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98748, 'enUS', '', 'Now\'s our chance. Let\'s get up that tower!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98749, 'enUS', '', 'You\'re on your own from here. Good luck!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98750, 'enUS', 'I am free! You are a true savior, outsider.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98751, 'enUS', 'But we haven\'t time for pleasantries, I must rally the Thorignir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98752, 'enUS', 'Speak to Vethir, he will take you to the summit!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98753, 'enUS', '', 'I hope Maiev was right about freeing you, demon hunter. Go on - I will hold the line.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98754, 'enUS', 'Your efforts are fruitless, demon hunter. Lord Gul\'dan sees all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98755, 'enUS', 'Hey, you lot! If you need a quick pick-me-up, come and get a Rivermane Remedy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98756, 'enUS', 'I\'ll let the others know of your gift, $n. Thank you again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98757, 'enUS', '', 'So HE is the key to Azeroth\'s downfall.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98758, 'enUS', '', 'We\'ve arranged a flight directly to Dalaran for you, Highlord.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98759, 'enUS', 'I live to serve.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98760, 'enUS', 'My life for the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98761, 'enUS', 'You will not gain the keystone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98762, 'enUS', 'Brood Queen Tyranna orders your death.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98763, 'enUS', 'My blade will cut through you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98764, 'enUS', 'You are outmatched and outnumbered.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98765, 'enUS', 'This is where you die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98766, 'enUS', 'We will cleanse the universe in fire.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98767, 'enUS', 'Interested in representing your guild here in Frostwall?', 'Interested in representing your guild here in Frostwall?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98768, 'enUS', 'Azsuna was once home to the great city of Nar\'thalas - one of the centerpieces of night elven civilization. It is a fine place to start our efforts against the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98769, 'enUS', 'This portal will take us to the western shore of the island not far from the city. Let\'s get down there, shall we?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98770, 'enUS', 'Little candles burn bright!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98771, 'enUS', 'Hot Hot Hot!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98772, 'enUS', 'AIIIEEEEeeeee', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98773, 'enUS', 'You have proven yourself a powerful hero, outsider. \n\nToday, you may also consider yourself a friend of the Thorignir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98774, 'enUS', 'Candle burn too bright!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98775, 'enUS', 'Before we proceed, I would like to bestow upon you a gift of the Thorignir.\n\nRaise your weapon, outsider, and brace yourself. This may prove uncomfortable for your kind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98776, 'enUS', 'I am prepared to receive your gift.', 'I am prepared to receive your gift.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98777, 'enUS', 'Before us this day stands our comrade in arms, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98778, 'enUS', 'Patricia! Would you be so kind as to show our friend here the newest recipes we\'ve discovered?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98779, 'enUS', '', 'I\'d be happy to! Have a look at my stock, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98780, 'enUS', 'Just make sure $Ghe:she; doesn\'t make a mess on my alchemy table!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98781, 'enUS', '', 'Yes, yes... We all know how attached you are to your alchemy table!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98782, 'enUS', '', 'Hey, you\'re alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98783, 'enUS', 'Kayn and Altruis have already gone ahead. Take this felbat and join them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98785, 'enUS', 'Highlord, may I speak with you for a moment?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98786, 'enUS', 'Battle is upon us, $n. Prepare yourself!', 'Battle is upon us, $n. Prepare yourself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98787, 'enUS', 'These lands are in peril. Be careful.', 'These lands are in peril. Be careful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98788, 'enUS', 'The battlefield could use more heroes like you, $n.', 'The battlefield could use more heroes like you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98789, 'enUS', 'I don\'t remember what happened...', 'I don\'t remember what happened...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98790, 'enUS', 'Thank you. I need time to recover.', 'Thank you. I need time to recover.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98791, 'enUS', 'Are you ready to face Magnar with me, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98792, 'enUS', 'Feelin\' fine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98793, 'enUS', 'I will protect you, Hruthnir.', 'I will protect you, Hruthnir.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98794, 'enUS', '<Silgryn is shaking uncontrollably and can barely speak.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98795, 'enUS', 'I am, as you will be. Come, let us go to the Halls of Valor. This mortal warrior will find the revenge you crave...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98796, 'enUS', 'Come with me now, let us dine with Odyn tonight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98797, 'enUS', 'I... have fallen. Hruthnir... you are a valarjar?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98798, 'enUS', 'I see now. My weapons are yours, warrior. May they bring you glory as you strike down Helya\'s minions!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98799, 'enUS', 'Begone from here, trespassers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98800, 'enUS', 'ENOUGH OF THIS! Guards, punish these outsiders for their insolence!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98801, 'enUS', 'I work all this time to improve the forges and do I get a single word of thanks? No!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98802, 'enUS', 'Make the weapons better, Corvus! Saronite isn\'t good enough anymore, Corvus! .. Bah! I\'ll show them what I can do to a blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98803, 'enUS', 'This tropic air is too damp, makes me feel like there\'s flesh hanging from my bones again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98804, 'enUS', 'It wouldn\'t have taken years to rebuild the forges if I had some help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98805, 'enUS', 'What can Corvus do for you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98806, 'enUS', '', 'Hold the line, demon hunters. We fight in Lord Illidan\'s name!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98807, 'enUS', 'Stand firm. Do not let them break through!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98808, 'enUS', 'The Dreamgrove is spread thin these days, I fear.$b$bMalorne\'s Refuge is a bastion of druidic power in the Broken Isles. We cannot let it fall.', 'The Dreamgrove is spread thin these days, I fear.$b$bMalorne\'s Refuge is a bastion of druidic power in the Broken Isles. We cannot let it fall.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98809, 'enUS', 'Satyr have invaded our grove. They were once night elves, but pledged themselves to the Legion and became twisted by demonic magic. Such creatures have no place in nature.', 'Satyr have invaded our grove. They were once night elves, but pledged themselves to the Legion and became twisted by demonic magic. Such creatures have no place in nature.', 26124);
