INSERT INTO `broadcast_text_locale` VALUES (99014, 'enUS', 'This may seem like nothing more than a tiny hole that no army can fit down, but it leads to the very lair of Neltharion himself.$b$bWithin, you shall find yourself behind the main assault force of the drogbar loyal to Dargrul.$b$bProceed with caution though, as those you do find will be more formidable than the drogbar you have encountered thus far. Good luck, brul.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99015, 'enUS', 'Shoo! Get out of here!', '', 26124);
