INSERT INTO `broadcast_text_locale` VALUES (99177, 'enUS', '', 'The anchoring crystals are the key, just get us up there, and we\'ll take care of it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99178, 'enUS', 'Form up, Alliance! We push them back to the portal! For Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99179, 'enUS', '', 'Focus on the crystals!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99180, 'enUS', 'Heal the wounded, we can\'t rest here for long.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99181, 'enUS', '', 'What happened here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99182, 'enUS', 'It happened in seconds! We were fighting our way up the shore and then BOOM, explosions of fel energy all around us! And these buildings just grew up out of them, filled with demons! I don\'t know how we\'re going to be able to push them back at this rate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99183, 'enUS', 'One demon at a time. With me, Alliance, leave nothing standing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99184, 'enUS', 'You\'re from the Crusade! What happened? Where is Tirion?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99185, 'enUS', 'Don\'t know... they were on us in seconds... felfire everywhere...', 'Don\'t know... they were on us in seconds... felfire everywhere...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99186, 'enUS', 'Save your strength, we will find him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99187, 'enUS', '', 'Keep your eyes open for survivors! Save as many as you can!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99188, 'enUS', 'Back where you came from!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99189, 'enUS', 'We\'re pushing them back! Don\'t let up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99190, 'enUS', 'We\'re halfway there, take heart Alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99191, 'enUS', 'For the Alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99192, 'enUS', 'Taste blaster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99193, 'enUS', 'For Gnomeregan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99194, 'enUS', 'Back to the Nether with you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99195, 'enUS', 'For... the Alliance...', 'For... the Alliance...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99196, 'enUS', 'I can\'t... go on...', 'I can\'t... go on...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99197, 'enUS', '', 'Structural integrity at 0%%!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99198, 'enUS', '', 'They\'ve broken through my mechbot!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99206, 'enUS', 'They\'ve slain me, my queen...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99216, 'enUS', 'Yes, yes more trinkets, give them to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99217, 'enUS', 'Kill you! Take your things!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99218, 'enUS', 'Curse your soul!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99219, 'enUS', 'They took Tirion!', 'They took Tirion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99220, 'enUS', 'Light have mercy on us...', 'Light have mercy on us...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99221, 'enUS', 'I can still fight, I\'m with you.', 'I can still fight, I\'m with you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99222, 'enUS', 'They came out of nowhere!', 'They came out of nowhere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99223, 'enUS', 'By the light, it is good to see you.', 'By the light, it is good to see you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99224, 'enUS', 'Have you seen Tirion?', 'Have you seen Tirion?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99225, 'enUS', 'I can\'t feel the Light in this place...', 'I can\'t feel the Light in this place...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99226, 'enUS', 'You\'re a welcome sight!', 'You\'re a welcome sight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99227, 'enUS', 'Thank you, hero.', 'Thank you, hero.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99228, 'enUS', 'We couldn\'t hold them off...', 'We couldn\'t hold them off...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99229, 'enUS', 'They\'re retreating.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99230, 'enUS', 'I get the feeling this fight isn\'t over yet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99231, 'enUS', '', 'Varian, I found him! Look, across the chasm, Tirion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99232, 'enUS', 'All forces, to Jaina!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99233, 'enUS', 'How are we going to get across?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99234, 'enUS', '', 'I\'ve got this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99235, 'enUS', 'Tirion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99236, 'enUS', '', 'Gul\'dan! You will pay for this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99237, 'enUS', 'Stay back... it\'s a trap... the Light will protect me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99238, 'enUS', 'Ha, you fool! You stand before the temple of a GOD. Your pitiful Light cannot reach you here. How fortunate that your friends have arrived just in time to watch you die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99239, 'enUS', 'Destroy him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99240, 'enUS', 'The Light... will... ahh!  Aahhhh!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99241, 'enUS', 'Fordring!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99242, 'enUS', '', 'No!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99243, 'enUS', 'All you have worked for, all you have sacrificed, just to see your champions fall to ash one by one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99244, 'enUS', 'We will never give in to hopelessness!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99245, 'enUS', '', 'The armies of Azeroth will triumph this day, together.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99246, 'enUS', 'Together, for Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99247, 'enUS', 'Destroy them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99248, 'enUS', 'You will know fear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99249, 'enUS', 'Brace yourselves, Alliance, it\'s coming this way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99250, 'enUS', '', 'Monsters from the felfire! Horde forces, be ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99251, 'enUS', 'Focus your fire, bring him down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99252, 'enUS', '', 'He\'s going to breathe, look out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99253, 'enUS', 'Front line, avoid those fists!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99254, 'enUS', 'Fel magma incoming, watch yourselves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99255, 'enUS', 'My forces are decimated. I have failed you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99256, 'enUS', 'The magma erupts, watch yourselves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99257, 'enUS', '', 'Another wave incoming!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99258, 'enUS', 'More beasts from da flame!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99259, 'enUS', 'They come at us again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99260, 'enUS', 'I swear to give my life if that is what it takes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99261, 'enUS', 'He\'s heading your way, Windrunner!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99262, 'enUS', '', 'Let\'s go, Horde, it\'s time to show the Alliance how we slay demons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99263, 'enUS', '', 'Beasts from the felfire emerge, be careful!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99264, 'enUS', 'The next wave approaches, be ready!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99265, 'enUS', '', 'They\'re coming again, let\'s put them on ice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99266, 'enUS', 'More enemies emerge from the flames!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99267, 'enUS', 'That magma\'s going to erupt, be ready to duck!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99268, 'enUS', 'He breathes that foul breath again, look out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99269, 'enUS', '', 'All Horde forces on my target, we\'re going to bring down that demon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99270, 'enUS', 'Dat great ball o\' felfire is comin\' dis way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99271, 'enUS', 'His fists are like mountains! Keep moving!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99272, 'enUS', 'You\'ve got another shot at the kill, Sylvanas!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99273, 'enUS', '', 'Consider your job done then, Wrynn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99274, 'enUS', '', 'All yours, Alliance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99275, 'enUS', 'We\'ve got him this time! All forces, concentrate your fire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99276, 'enUS', 'Ha, the bigger they are...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99277, 'enUS', '', 'No time to gloat, Wrynn. We\'ve got to stop Gul\'dan before he summons another one of those things!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99278, 'enUS', 'Everyone, across the chasm!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99279, 'enUS', '', 'We\'ll take the ridge and cover your flank.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99280, 'enUS', 'Sylvanas, thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99281, 'enUS', '', 'Good luck, Varian.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99282, 'enUS', 'And you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99283, 'enUS', 'There is nowhere to run, Gul\'dan. Give up now, and I will grant you a swift death.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99284, 'enUS', 'Run? Ha. You are right, human. There is nowhere to run from my master.  Now, witness the true might of Lord Sargeras!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99285, 'enUS', 'Hold steady, we\'ve broken their lines before, we will again. Charge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99286, 'enUS', 'Down the ridge behind us, another portal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99287, 'enUS', 'We\'ve got to hold dis ridge, no matter da cost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99288, 'enUS', '', 'Take up positions. We hold them off. We have to.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99289, 'enUS', 'I cannot... take much more of this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99290, 'enUS', '', 'Pull Baine back to my position, close ranks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99291, 'enUS', 'We will never fall to you, Gul\'dan. The combined forces of Azeroth will triumph today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99292, 'enUS', '', 'Less chatting, Wrynn. Kill him already!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99293, 'enUS', 'I have seen the end of your pitiful world, Wrynn. You will perish in felfire, like ten thousand worlds before your own. The legion is ENDLESS.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99294, 'enUS', '', 'Shelds up, here they come!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99295, 'enUS', 'Even more demons be pouring from dat portal. Get ready!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99297, 'enUS', '', 'Vol\'jin! Pull him back! Keep your lines steady! We have to hold!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99298, 'enUS', 'There are... too many of them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99299, 'enUS', '', 'Everyone pull back to me! We make our stand here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99300, 'enUS', 'No matter how many demons you throw at us, we will cut you down, monster.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99301, 'enUS', 'And now you will fall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99302, 'enUS', 'Hold on, Alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99303, 'enUS', 'We return.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99304, 'enUS', 'To conquer this puny world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99305, 'enUS', 'And burn it to a husk.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99306, 'enUS', 'Do you see now, human? The fate of this world is sealed. The legion cannot be stopped.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99307, 'enUS', 'Together, we will defeat you, just as we always have.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99309, 'enUS', '', 'Varian, behind us!  The Horde is gone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99310, 'enUS', '', 'There are so many of them...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99311, 'enUS', '', 'There\'s nothing we can do! The ridge is lost! Horde forces, if you value your lives, cut a path through to the shore! We have to get out of here, or there won\'t be anyone left to fight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99312, 'enUS', 'It looks like our reinforcements have arrived.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99313, 'enUS', 'Good ting! We don\'t got much beach left ta retreat to.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99314, 'enUS', 'There\'s only one way out of this one, old friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99315, 'enUS', 'Listen up! Dere\'s only one way offa dis beach, and it be t\'rough alla dose demons ahead of us. But dey don\'t know who dey be dealin\' wit. Let\'s show dem what it means ta be Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99316, 'enUS', 'I return... to the elements...', 'I return... to the elements...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99317, 'enUS', 'Not... going to make it...', 'Not... going to make it...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99318, 'enUS', 'Da pain!', 'Da pain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99319, 'enUS', 'Dis be da end...', 'Dis be da end...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99320, 'enUS', 'Back to the Nether!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99321, 'enUS', 'The elements will destroy you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99322, 'enUS', 'Lok\'tar ogar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99323, 'enUS', 'Die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99324, 'enUS', 'Dis one\'s mine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99325, 'enUS', 'For da Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99326, 'enUS', 'Ha, the fools attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99327, 'enUS', 'Crush them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99328, 'enUS', 'Keep them away from the spires!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99329, 'enUS', 'There\'s something odd about the crystals surrounding that spire. They feel... wrong... like they\'re in two places at once. Hmm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99330, 'enUS', 'Horde, make for dem crystals! We gonna see if ya hunch is right.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99331, 'enUS', 'Yes... they must anchor it to this world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99332, 'enUS', 'Two more ta go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99333, 'enUS', 'You destroy but one of the master\'s endless instruments of your demise! INFERNO!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99334, 'enUS', 'Keep it up! We almost got dem!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99335, 'enUS', 'Weaklings. Must I do everything myself? Very well, prepare yourselves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99336, 'enUS', 'You gonna die just like all da others.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99337, 'enUS', 'Focus on the commander!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99338, 'enUS', 'You will all burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99339, 'enUS', 'Felfire rains from the sky! Look out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99340, 'enUS', 'Your corpses will feed my hounds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99341, 'enUS', 'Your souls will know endless agony.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99342, 'enUS', 'Looks like ya chose da wrong side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99343, 'enUS', 'The master will mend my flesh again and again. You have not won. You cannot win. We are endless! We are Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99344, 'enUS', 'I... will... return...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99345, 'enUS', 'It is good to fight with you again, Warchief.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99346, 'enUS', 'Ain\'t never gonna get used to you callin\' me dat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99347, 'enUS', 'There\'s only one way to the tomb, are you ready?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99348, 'enUS', 'Let\'s get movin\'.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99349, 'enUS', 'Sylvanas\' ship must \'av crashed just ahead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99350, 'enUS', 'They\'re expanding faster than we\'ve seen before.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99351, 'enUS', 'Ya, mon. Dis ain\'t da same Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99352, 'enUS', 'What?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99353, 'enUS', 'Dat\'s no invasion force. Dat\'s an army!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99354, 'enUS', '', 'Nock your arrows! Hold! Fire!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99355, 'enUS', 'They\'re alive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99356, 'enUS', 'Let\'s move!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99357, 'enUS', '', 'It\'s about time you showed up!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99358, 'enUS', 'You looked like you could use a hand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99359, 'enUS', 'Their reinforcements are endless!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99360, 'enUS', 'Those crystals again... if we can get close to them, we can bring this thing down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99361, 'enUS', '', 'You heard him, rangers, we cut those demons down! Fire a volley on my mark... NOW!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99362, 'enUS', 'Break the crystals!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99363, 'enUS', '', 'Well, that was invigorating. Now how did they manage to get so many demons here so quickly?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99364, 'enUS', 'This invasion is unlike any I have ever witnessed, and I don\'t think we\'ve seen the last of their surprises.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99365, 'enUS', 'These structures rose from the ground in moments, spewing forth demons in the hundreds in the blink of an eye.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99366, 'enUS', '', 'And now we know how to destroy them. It\'s them or us. Let\'s get moving so we make sure it\'s not us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99367, 'enUS', '', 'This is Argent Crusade insignia, can you speak? Where is the rest of the Crusade?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99368, 'enUS', 'Dead... dying... they descended on us in seconds... nowhere to run...', 'Dead... dying... they descended on us in seconds... nowhere to run...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99369, 'enUS', 'An no sign of Tirion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99370, 'enUS', 'We will find him. Horde forces, keep your eyes open for survivors, rescue as many as you are able!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99371, 'enUS', '', 'One shot, one kill.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99372, 'enUS', '', 'Too easy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99373, 'enUS', '', 'That\'s half of the city down, this is going better than expected.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99374, 'enUS', '', 'Right to the face!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99375, 'enUS', '', 'We\'ll never bow to you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99376, 'enUS', 'To the earth with you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99377, 'enUS', 'Take heart, heroes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99378, 'enUS', 'For the Earth Mother!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99379, 'enUS', 'Darkness... calls...', 'Darkness... calls...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99380, 'enUS', 'This is... the end...', 'This is... the end...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99381, 'enUS', 'I return... to the earth...', 'I return... to the earth...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99382, 'enUS', 'Earth Mother... guide me...', 'Earth Mother... guide me...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99383, 'enUS', 'We must find Tirion.', 'We must find Tirion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99384, 'enUS', 'I am singed, but I can still fight.', 'I am singed, but I can still fight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99385, 'enUS', 'Thank you for your aid.', 'Thank you for your aid.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99386, 'enUS', 'We were not prepared!', 'We were not prepared!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99387, 'enUS', 'It\'s good to see a friendly face.', 'It\'s good to see a friendly face.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99388, 'enUS', 'Where is Tirion?', 'Where is Tirion?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99389, 'enUS', 'I am wounded, but I can move.', 'I am wounded, but I can move.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99390, 'enUS', 'I am in your debt.', 'I am in your debt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99391, 'enUS', 'We couldn\'t stop them...', 'We couldn\'t stop them...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99392, 'enUS', 'There were so many of them.', 'There were so many of them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99393, 'enUS', 'Dey be retreatin\'.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99394, 'enUS', '', 'That was too easy, something\'s wrong.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99395, 'enUS', 'There, across the chasm, Gul\'dan! He has Tirion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99396, 'enUS', 'All da Horde, get ta Thrall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99397, 'enUS', '', 'Anyone have any ideas how we\'re going to get across this thing?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99398, 'enUS', 'Spirits of earth, aid me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99399, 'enUS', '', 'Convenient.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99400, 'enUS', '', 'You WILL be avenged, my love!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99401, 'enUS', '', 'Well, look who decided to show up. It\'s been a while since you\'ve been in the halls of Acherus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99402, 'enUS', 'It is good to see you as well, but we can reminisce later. I must speak with the $ct at once.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99403, 'enUS', 'I\'m not here to waste time with the \"help,\" I must speak with the $ct at once.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99404, 'enUS', 'Thank you, $ct.  I need to go see Corvis about getting my armor repaired. By your leave, $gsir:ma\'am;.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99405, 'enUS', 'Thank you, Deathlord. I will not let you down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99406, 'enUS', 'I swear, I don\'t know nothin\' about any demon flames! I was just buyin\' and sellin\' armor, is all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99407, 'enUS', 'Why should I believe you?', 'Why should I believe you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99408, 'enUS', 'I believe you.', 'I believe you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99409, 'enUS', 'I\'m just an honest trader, just lookin\' to buy all the goods on the auction house, sell \'em back to the people at a much higher price, make my ten million gold, and go home in peace! Is that so much to ask?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99410, 'enUS', 'I know your kind. A dirty auction house speculator.', 'I know your kind. A dirty auction house speculator.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99411, 'enUS', 'Very well. I believe you.', 'Very well. I believe you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99412, 'enUS', 'No... I\'m one of the good ones! I... I donated to charity, once! And I hardly ever litter!$b$bAnd I never, EVER undercut.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99413, 'enUS', 'Were there any other witnesses to what happened?', 'Were there any other witnesses to what happened?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99414, 'enUS', 'Fine, I believe you.', 'Fine, I believe you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99415, 'enUS', 'Sure, me mule was there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99416, 'enUS', 'Your MULE? You\'ve got to be kidding me.', 'Your MULE? You\'ve got to be kidding me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99417, 'enUS', 'OK, I believe you.', 'OK, I believe you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99418, 'enUS', 'I don\'t know why you\'re so intent on questioning me. I just lost my cart and everything I own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99419, 'enUS', 'I WANT to believe you, but you\'re not making it easy for me.', 'I WANT to believe you, but you\'re not making it easy for me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99420, 'enUS', 'Just being thorough. I believe you.', 'Just being thorough. I believe you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99421, 'enUS', 'Wait a minute... are you EXTORTING me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99422, 'enUS', 'Yes, I\'m extorting you.', 'Yes, I\'m extorting you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99423, 'enUS', 'Of course not! I believe your story.', 'Of course not! I believe your story.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99424, 'enUS', 'But... I spent every last copper I had on that cart full o\' gear! I mean it! I don\'t have any money to give you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99425, 'enUS', 'Your whole story is falling apart, Caelen.', 'Your whole story is falling apart, Caelen.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99426, 'enUS', 'I believe your story.', 'I believe your story.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99427, 'enUS', 'Wait, here, look...$b$b<Caelen removes a pile of receipts from his pockets.>$b$bSee! All the stuff I bought, purchased legitimately from reputable Dalaran arms dealers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99428, 'enUS', 'These look like counterfeit receipts.', 'These look like counterfeit receipts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99429, 'enUS', 'I see everything is on the up-and-up here. I believe you.', 'I see everything is on the up-and-up here. I believe you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99430, 'enUS', 'Counterfeit receipts? Really?$b$bLet me say this out loud, \'cause I wanna get it straight in my head.$b$bYou\'re saying that I brought a bunch of demonic... SOMETHING... into Dalaran, lit it on fire, hung around while it burned, and then produced a bunch of counterfeit receipts in case anybody asked me questions?$b$bI\'m right about that, right? That\'s correct? That\'s your story?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99431, 'enUS', 'Yes, that\'s what I\'m saying.', 'Yes, that\'s what I\'m saying.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99432, 'enUS', '', 'Stop fighting, Skyhorn beast!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99433, 'enUS', 'Well, when you put it that way, I believe you.', 'Well, when you put it that way, I believe you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99434, 'enUS', 'I\'ve spent the last eleven years huddled shoulder-to-shoulder in a humid, smelly auction house, trying to make a few silver off a crate o\' leather hats. I\'m not some demon-corrupted adventurer... I\'ve never even SEEN a demon. Hell, I can\'t even wield half the gear I sell.$b$b<Caelen looks at you through anguished tears. He is, by all external signs, telling the truth.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99435, 'enUS', 'FINE. I believe you.', 'FINE. I believe you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99436, 'enUS', '', 'You will soon be a roc!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99437, 'enUS', '', 'You\'re mine, eagle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99438, 'enUS', 'Thanks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99439, 'enUS', 'Now, what do you know about this powder?', 'Now, what do you know about this powder?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99440, 'enUS', 'That wasn\'t in my crates. I always do a thorough investigation of everything I buy, and I woulda\' noticed some glowy green stuff.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99441, 'enUS', 'That wasn\'t in my crates. I always do a thorough investigation of everything I buy, and I woulda\' noticed some glowy green stuff. Reminds me of enchanting dust. You should talk to the enchanters.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99442, 'enUS', 'Helya\'s massive presence looms in the distance from the platform. \n\nThis altar appears to be a means to communicate with her.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99443, 'enUS', 'I do not belong here! I wish to leave Helheim!', 'I do not belong here! I wish to leave Helheim!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99444, 'enUS', 'She was a fine cart, she was.$b$b<Caelen wipes a tear into his beard.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99445, 'enUS', 'Do you know why your cart exploded into fel flames?', 'Do you know why your cart exploded into fel flames?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99447, 'enUS', 'Powder\'s not really my thing, kid. I\'m more of an \"inks and scrolls\" kind of guy.$b$bNow, let\'s think this through logically:$b$bThis powder of yours... is it an INK?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99448, 'enUS', 'Is it a SCROLL?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99449, 'enUS', 'Are you wasting my time?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99450, 'enUS', 'Can I ask you a few questions about this powder?', 'Can I ask you a few questions about this powder?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99451, 'enUS', 'Bring that powder to Enchanter Nalthanis. Maybe he\'ll know what to do with it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99452, 'enUS', 'No.', 'No.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99453, 'enUS', '...no.', '...no.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99454, 'enUS', 'Goodbye, professor.', 'Goodbye, professor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99455, 'enUS', '', 'Our battle against the harpies is ongoing, but your actions have helped restore some balance to the basin.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99456, 'enUS', 'Let me know when you are ready, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99458, 'enUS', 'We\'re in luck, warbrave. The champion you seek should be here shortly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99459, 'enUS', 'Come champion. Journey with me and I shall show you the might of Highmountain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99460, 'enUS', '$p, the warbrave has good intentions - I think. Quickly, now, after him - his people are your best chance at an ally on that mountain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99461, 'enUS', 'Meet me in the cave below, $n. They\'ll want to see the corpse of the harpy you slew.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99462, 'enUS', 'These things... I must warn the others.', 'These things... I must warn the others.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99463, 'enUS', 'Let me see... it smells like demon fire, it has the color of demonic corruption, and it appeared in my shop only moments after screams and explosions rocked the streets of Dalaran.$b$bAre you trying to kill me, $c?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99464, 'enUS', 'Let me rephrase:$b$bIs this dust in any way related to the SMOLDERING BODY that is still lying on my shop\'s doorstep?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99465, 'enUS', 'Get it out of here, then! I\'m dead enough as it is.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99466, 'enUS', 'Get it out of here, then! Bring it to the enchanters\' shop. They know more about dusts than I do.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99467, 'enUS', 'I\'m not trying to kill you.', 'I\'m not trying to kill you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99468, 'enUS', 'Yes.', 'Yes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99469, 'enUS', 'What can you tell me about this powder?', 'What can you tell me about this powder?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99470, 'enUS', 'Today just might be our lucky day, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99471, 'enUS', 'I\'ve made contact with Malfurion Stormrage in Val\'Sharah. His men have already nearly completed their mission there - they just want some backup, just in case.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99472, 'enUS', 'What\'re you waiting for? It isn\'t often we\'re handed victory on a silver platter like this. Relish every moment of it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99473, 'enUS', 'We still tell the legends of when Highmountain stood against the tide of the Burning Legion many ages ago.$b$bIt will be an honor to stand against them again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99474, 'enUS', 'Lead me to your High Chieftain.', 'Lead me to your High Chieftain.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99475, 'enUS', 'You want my hammer? COME DIG FOR IT!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99476, 'enUS', 'They crunches!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99477, 'enUS', 'Squish! Ah he he he!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99478, 'enUS', 'Splatted under me feet!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99479, 'enUS', 'Now where is $n? They always show up right when we - ah, there you are!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99480, 'enUS', 'Good luck to the both of you. You\'ll need it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99481, 'enUS', 'I shouldn\'t have wandered off.', 'I shouldn\'t have wandered off.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99482, 'enUS', 'Let\'s get a move on. The Dark Lady\'s fleet is nearly ready to leave.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99483, 'enUS', '', 'Follow me, $n. The Skyfire is just about ready to leave.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99484, 'enUS', '$n is here to meet with Torok. $GHe:She; slew the Witch of the Wood.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99485, 'enUS', 'A challenger? Let\'s see what this one is capable of!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99486, 'enUS', 'Repurposing', 'Repurposing', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99487, 'enUS', '', 'Experimentation yields wondrous things! Attack minions!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99488, 'enUS', 'I watch over the valley and keep track of harpy activity... but they knew. They attacked here first so I would not see their main attack and warn the village. My wife and  I lived peacefully on this summit, but they injured me and took her life. I fear they will strike again for my son and I will not be able to protect him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99489, 'enUS', 'I think I hear them coming!', 'I think I hear them coming!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99490, 'enUS', 'Keep those hands where I can see them, $r.', 'Keep those hands where I can see them, $r.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99491, 'enUS', 'Hey! Hands off!', 'Hey! Hands off!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99492, 'enUS', 'Not on my watch, scum.', 'Not on my watch, scum.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99493, 'enUS', 'Back away and nobody gets hurt, $c.', 'Back away and nobody gets hurt, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99494, 'enUS', 'More souls! Come and accept your fate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99495, 'enUS', '', 'Your young will be a tasty snack!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99496, 'enUS', 'Thank you for saving my son. I am forever indebted to you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99499, 'enUS', 'Removing all of the annoying humans wasn\'t particularly difficult, but these demons...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99500, 'enUS', 'What? Can\'t you see I\'m busy! These demons won\'t just kill themselves!$B$BSomeone has to find out how to get rid of them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99501, 'enUS', 'As much as I love money, ain\'t no way I\'m sellin\' to demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99502, 'enUS', 'Ok I am here. I know this probably isn\'t what you\'d call opportune time, but hey - some of my wares can help stick it to these demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99503, 'enUS', '', 'I... I did it! I am the greatest warlock that ever lived!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99504, 'enUS', 'I will go ahead and secure the elevators.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99505, 'enUS', 'I sense demons nearby. After all that time in stasis, I am ready to hunt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99506, 'enUS', '', 'My blades are ready, $n. I will check on the situation ahead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99507, 'enUS', 'If those spires give you trouble, take out the stabilizers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99508, 'enUS', '', 'Gul\'dan could never access this chamber. One of my Wardens has betrayed me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99509, 'enUS', 'I, too, have felt betrayal by my own kind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99510, 'enUS', '%s looks over at Altruis the Sufferer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99511, 'enUS', '', 'Cordana! You will answer to the High Council for this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99512, 'enUS', 'Soon. The body is cold, but his soul is not done fighting. We have work to do before he is ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99516, 'enUS', 'We have defeated your champion. Now, release us from Helheim!', 'We have defeated your champion. Now, release us from Helheim!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99517, 'enUS', 'Illidan Stormrage. At last, I meet the great Betrayer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99518, 'enUS', '', 'Thank you, $n. I would\'ve been dead fel meat without you. He must have been attracted to the magic at this site.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99519, 'enUS', '', 'My first hunt on my own! I\'ve been tracking a beast all the way from the other side of Val\'sharah... The destruction he leaves in his wake is astounding! All signs point here. Will you help me track him down?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99520, 'enUS', 'Sure.', 'Sure.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99521, 'enUS', '', 'Alright. The tracks lead this way, up to the statue. Follow me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99522, 'enUS', 'Just like old times, eh Kayn?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99523, 'enUS', '', 'Look at this! The bite marks on this meat are nothing like I\'ve ever seen! Wait... why are they glowing green?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99524, 'enUS', 'Do not speak to me of old times, Altruis. You betrayed Illidan. You betrayed us all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99525, 'enUS', 'After ten-thousand years of imprisonment, Illidan succumbed to the fel energy within. When he was freed, he was not in his right mind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99526, 'enUS', 'He was fighting it, as we all do. He needed us to have faith in him, Altruis.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99527, 'enUS', 'You followed Illidan blindly, like a dog. Even when he lost his way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99528, 'enUS', 'He made the hard choices. He sacrificed EVERYTHING. What did you give?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99529, 'enUS', '', 'A demon?! I\'m not that kind of hunter! I\'M NOT THAT KIND OF HUNTER!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99530, 'enUS', 'You heard the Warden. We are the only ones who can defeat the Legion. We MUST survive this place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99531, 'enUS', '', 'This is sheer madness! The Wardens have lost control of their prison.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99532, 'enUS', '', 'Aside from us, the most powerful prisoners were sealed on this level. WERE being the operative word.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99533, 'enUS', '', 'I\'ll place these bracers into our improvised forge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99534, 'enUS', '', 'Now, add a little leyflame, and...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99535, 'enUS', '', 'Voila! It works!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99536, 'enUS', 'Simply brilliant, Ildine! How much obliterum did we get?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99537, 'enUS', '', 'About two grains. Two grains of obliterum.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99538, 'enUS', 'Well, it\'s a start.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99539, 'enUS', '', 'I cannot recommend this, Khadgar. Our knowledge of demonic forces is limited. We risk attracting the attention of the Legion, or worse.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99540, 'enUS', 'I agree. Our artisans lack the skills to build such a forge. The dangers of toying with demonic powers are simply too great.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99541, 'enUS', 'I agree. You of all people should know that dealing with this type of power carries great danger, demon hunter. I cannot allow it in my city.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99542, 'enUS', 'I\'m sorry, but your request has been den--', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99543, 'enUS', 'Actually, Khadgar, I believe I may know someone that can build this forge. An expert.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99544, 'enUS', 'Really? Who?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99545, 'enUS', 'Perhaps better you didn\'t know, sir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99546, 'enUS', 'How mysterious. Very well! Archmage Karlain will assist you, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99548, 'enUS', 'The gargoyles lift off from their pressure plates and the portcullis slams shut!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99549, 'enUS', '', 'Stop this foolish bickering. We do not have time for this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99550, 'enUS', 'Lord Illidan taught us to fight and to persevere. Together, we destroyed countless demons. YOU chose the coward\'s path.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99551, 'enUS', 'I could only trust my own sight, brother. Lord Illidan was becoming no better than our enemy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99552, 'enUS', 'You killed our own people! You spit in the face of our sacrifices!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99553, 'enUS', 'We\'ve discovered an exciting new way to extract demonic essence from our crafted armor, but to do so, we need your permission to build a forge.', 'We\'ve discovered an exciting new way to extract demonic essence from our crafted armor, but to do so, we need your permission to build a forge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99554, 'enUS', 'Enough! My colossal infernal will crush you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99555, 'enUS', 'I did not summon you for nothing. Destroy them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99556, 'enUS', 'I\'m gonna charge right over you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99557, 'enUS', 'Your experience doesn\'t match mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99558, 'enUS', 'You think you can match the strength of my fists?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99559, 'enUS', 'You should wear a helmet when you fight me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99560, 'enUS', 'Okay stop! STOP! Watch the jaw! You win! I\'ve never seen such speed before. You\'re a good kid, mac. Keep it up. I\'ll be back for my belt some day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99561, 'enUS', 'Might of the Legion? Hah! We\'ll see about that...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99562, 'enUS', 'Let this be a lesson to all who invade OUR lands!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99563, 'enUS', 'Hold the line! These treacherous creatures know NOTHING of war!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99564, 'enUS', 'Sylvanas has instructed me to take ALL of your heads! And I WILL deliver.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99565, 'enUS', 'The only blood drawn on this day will be the blood of the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99566, 'enUS', 'I\'m the heavyweight champion of Highmountain! You think you can take me on, kid? It\'ll be a punch out! Show me what you\'re worth... but when I knock you down DON\'T GET UP!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99567, 'enUS', 'I\'m ready!', 'I\'m ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99568, 'enUS', 'Things are not what they seem, $n. I suggest keeping a close eye on those around you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99569, 'enUS', 'Hail champion.$b$bI was sent by the High Chieftain Ulan to find you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99570, 'enUS', 'He went easy on you, outsider. Come, speak.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99571, 'enUS', 'Something about this doesn\'t seem right.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99572, 'enUS', 'I\'ve got a bad feeling about this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99573, 'enUS', '', 'Well that was terrifying. Thanks for helping, $p. Maybe I should find a demon hunter partner...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99574, 'enUS', '', 'Beware, $n. There is a dreadlord inside that Soul Engine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99575, 'enUS', 'Oh good, another soul for my engine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99576, 'enUS', 'Tyranna... help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99577, 'enUS', 'The portals are down! Their frontline has been crushed! Hold your defenses!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99578, 'enUS', '', 'At least your soul will now power my engine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99579, 'enUS', 'Signal the bat riders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99580, 'enUS', '', 'You have my thanks, outsider.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99581, 'enUS', '', 'I return to battle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99582, 'enUS', '', 'These wretches will know the wrath of the Valkyra!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99583, 'enUS', '', 'You have saved me from an unimaginable fate. Thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99584, 'enUS', '', 'We will show Helya no quarter!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99585, 'enUS', '', 'Uriah! I\'m here! Where are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99586, 'enUS', '', 'Oh Uriah, you\'re injured!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99587, 'enUS', '', '$n, we must do something to save him!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99588, 'enUS', 'By this hammer, I claim this mountain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99589, 'enUS', 'The curse is lifted! Finally I will battle in the Halls of Valor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99590, 'enUS', 'My soul belongs to Odyn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99591, 'enUS', 'I will never be a kvaldir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99592, 'enUS', 'Odyn smiles upon us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99593, 'enUS', 'Helya was a fool to think she could hold me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99594, 'enUS', 'To the Halls of Valor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99595, 'enUS', 'Dargrul the Underking! Come down here and face justice for those you have betrayed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99596, 'enUS', 'Welcome, champion, to Thunder Totem. In better days we would have warriors from the Bloodtotem tribe or eagle riders from the Skyhorn tribe. Today, we are so few we could barely keep a kobold out.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99597, 'enUS', 'You bring this back in one piece, ok? Go get \'em!', 'You bring this back in one piece, ok? Go get \'em!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99598, 'enUS', 'The ram on this one\'s supercharged. My handiwork! Bag a few demons for me!', 'The ram on this one\'s supercharged. My handiwork! Bag a few demons for me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99599, 'enUS', 'Just a few tons of Gnomeregan\'s finest anti-Demon engineering! Put it to good use.', 'Just a few tons of Gnomeregan\'s finest anti-Demon engineering! Put it to good use.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99600, 'enUS', 'I guess that isn\'t so bad...', 'I guess that isn\'t so bad...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99601, 'enUS', 'What must we give in return?', 'What must we give in return?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99602, 'enUS', 'Everything!', 'Everything!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99603, 'enUS', 'Loaded with munitions and ready to hit \'em hard. Fly safe!', 'Loaded with munitions and ready to hit \'em hard. Fly safe!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99604, 'enUS', 'Death from above is the best kind, wouldn\'t you agree? Give \'em hell!', 'Death from above is the best kind, wouldn\'t you agree? Give \'em hell!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99605, 'enUS', 'Be... careful... this is a... trap...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99606, 'enUS', 'Let\'s kick the tires and light the fires!', 'Let\'s kick the tires and light the fires!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99607, 'enUS', 'Guuuaaaahhh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99608, 'enUS', 'Please, defeat them! I\'ll protect my son!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99609, 'enUS', 'Thank you, $p. We are forever indebted to you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99610, 'enUS', 'I am beyond saving, Rosalyn. You need to focus on finding the demons that have been released from the soul chambers and trap them once again, before it\'s too late.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99611, 'enUS', 'Soulkeeper Uriah takes a slow, pained breath.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99612, 'enUS', '', 'I have precious little time to waste. Speak or leave.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99613, 'enUS', '', 'Open the wells and unleash a torrent of souls on him.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99614, 'enUS', 'Your soul will be ours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99615, 'enUS', 'Kill them before they can get up to the Fel Hammer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99616, 'enUS', 'Defend the Soul Engine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99617, 'enUS', 'Thank you for the rescue. I will see if any of the other warbraves survived the attack.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99618, 'enUS', 'Ooh! You have little creatures! Let\'s fight, and if I win I get to EAT one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99619, 'enUS', 'Ha ha ha! Now we show them our true power!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99620, 'enUS', 'To battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99621, 'enUS', 'Flummox no need pets! Flummox eats them NOW!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99622, 'enUS', 'I\'m still in training, Mac. But I\'m going to win my belt back from you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99623, 'enUS', 'This platform will take you into the sacred Hall of Chieftains where Mayla Highmountain awaits you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99624, 'enUS', '', 'But... but I don\'t know how, Uriah. I cannot do it without you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99625, 'enUS', 'You can, Rosalyn, and you will. You\'re more gifted than you let yourself believe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99626, 'enUS', 'Soulkeeper Uriah exhales tiredly as the last bit of breath leaves his lifeless body.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99627, 'enUS', 'Knead with your fists, not your face!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99628, 'enUS', 'Mix \'em up, mix \'em up! C\'mon now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99629, 'enUS', 'Winning isn\'t that bad. You should all try it sometime!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99630, 'enUS', 'Thanks, I thought I was going to die!', 'Thanks, I thought I was going to die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99631, 'enUS', 'There\'s a reason why Taurson is the best! Keep training!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99632, 'enUS', 'By the light of Elune, thank you!', 'By the light of Elune, thank you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99633, 'enUS', 'We need to defend the temple!', 'We need to defend the temple!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99634, 'enUS', 'Thank you for saving me!', 'Thank you for saving me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99635, 'enUS', 'Three hours in the air is too much. We need more pilots!', 'Three hours in the air is too much. We need more pilots!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99636, 'enUS', 'I always wanted to fly... but not during the end of the world.', 'I always wanted to fly... but not during the end of the world.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99637, 'enUS', 'I\'m exhausted... I don\'t know if I can take another flight today.', 'I\'m exhausted... I don\'t know if I can take another flight today.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99638, 'enUS', 'We\'ve got to stay sharp. Too much is riding on us, and we\'re going back up soon.', 'We\'ve got to stay sharp. Too much is riding on us, and we\'re going back up soon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99639, 'enUS', 'We\'re in this together, ok? You\'re doing great up there! We\'re gonna make it.', 'We\'re in this together, ok? You\'re doing great up there! We\'re gonna make it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99640, 'enUS', 'Azeroth depends on us fighting in the skies. Stay strong, for all of us.', 'Azeroth depends on us fighting in the skies. Stay strong, for all of us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99641, 'enUS', 'Go, Champion. I will rally what forces I can to assault the drogbar stronghold to our south. Meet me at the cavern\'s mouth when you are ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99642, 'enUS', '', 'I can\'t believe he\'s gone...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99643, 'enUS', 'The eagle nods his thanks before flying away, free.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99644, 'enUS', 'This is a disaster... but we can win this! Keep the tanks rolling. Keep our birds in the air. The world won\'t end without tasting some Gnome ingenuity!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99645, 'enUS', '', 'Let\'s keep moving. The others are in the chamber just ahead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99646, 'enUS', 'I respect our past, but I cannot forgive Illidan\'s methods. The Illidari deserve a better leader.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99647, 'enUS', 'Then we stand at an impasse. $n, you must resolve this conflict.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99648, 'enUS', '', '<Thalyssra is shaking uncontrollably and can barely speak. |cFFF92304Her hunger must be sated before all else.|r>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99649, 'enUS', '', 'The Nightwell is a font of incredible power. We must not allow the Legion to make use of it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99650, 'enUS', '', 'Yes! I can feel the power coursing through me!\n\nI am whole again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99651, 'enUS', 'What\'s this? Helya will surely reward me for this offering.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99652, 'enUS', 'Impressive you have made it this far, demon hunters. Now, I will extinguish your souls like a candle\'s flame!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99653, 'enUS', 'We\'ve done it, Illidari! FREEDOM IS OURS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99656, 'enUS', 'I am Archmage Khadgar, leader of the Kirin Tor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99658, 'enUS', 'Demon hunters, Azeroth needs your help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99659, 'enUS', 'Helya is defeated; it is time to call in the val\'kyr!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99660, 'enUS', 'Once that witch is defeated I can free the stolen souls of the valejar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99661, 'enUS', 'This one\'s a bit rough on the stick. Be careful goin\' downhill boss.', 'This one\'s a bit rough on the stick. Be careful goin\' downhill boss.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99662, 'enUS', 'Spikes... big rocks... everything looks ok. Smash those Demons good!', 'Spikes... big rocks... everything looks ok. Smash those Demons good!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99663, 'enUS', 'Take the fight to \'em! Demons will burn by Horde might!', 'Take the fight to \'em! Demons will burn by Horde might!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99664, 'enUS', 'You have my thanks, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99665, 'enUS', 'Captains, report!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99666, 'enUS', '', 'We\'ve encountered heavy resistance near the Soul Engine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99667, 'enUS', '', 'Our assault is underway. I\'ll join in when you attack the overseer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99668, 'enUS', 'Be careful givin\' it gas. The tanks are a bit creaky!', 'Be careful givin\' it gas. The tanks are a bit creaky!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99669, 'enUS', '... Battlelord, report!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99670, 'enUS', 'Big pile of bombs for ya. Make sure they land on the demons, ok?', 'Big pile of bombs for ya. Make sure they land on the demons, ok?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99671, 'enUS', 'The balloon\'s patched up, but I left a parachute in there just in case! At least, I think I did.', 'The balloon\'s patched up, but I left a parachute in there just in case! At least, I think I did.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99672, 'enUS', '', 'We\'ve regained control of Southpoint Gate! I\'m sending ground patrols toward Tarren Mill!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99673, 'enUS', '', 'Hold your defenses! I\'m headed your way!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99674, 'enUS', 'Feed: 100x |TINTERFACE\\ICONS\\INV_MISC_ANCIENT_MANA.BLP:20|t|Hcurrency:1155|h|cFFFFFFFF Ancient Mana|h|r', 'Feed: 100x |TINTERFACE\\ICONS\\INV_MISC_ANCIENT_MANA.BLP:20|t|Hcurrency:1155|h|cFFFFFFFF Ancient Mana|h|r', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99675, 'enUS', 'The Legion has begun its invasion of our world. From sea to shore, brave defenders of the Alliance and Horde are answering the call to arms.\n \nHowever, they will ultimately fail without the aid of the demon hunters. You were uniquely trained as ruthless weapons against the Legion. It is time to go on the offensive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99676, 'enUS', 'Spend |TINTERFACE\\ICONS\\INV_OFFHAND_PVEALLIANCE_D_01.BLP:20|t|Hitem:129094|h|cFF0070DD Nightwell Ambrosia|r|h to Empower this Nightfallen?', 'Spend |TINTERFACE\\ICONS\\INV_OFFHAND_PVEALLIANCE_D_01.BLP:20|t|Hitem:129094|h|cFF0070DD Nightwell Ambrosia|r|h to Empower this Nightfallen?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99677, 'enUS', 'Feed Ancient Mana |TINTERFACE\\ICONS\\INV_MISC_ANCIENT_MANA.BLP:20|t x100', 'Feed Ancient Mana |TINTERFACE\\ICONS\\INV_MISC_ANCIENT_MANA.BLP:20|t x100', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99678, 'enUS', 'Thank you champion. But be careful, the drogbar Underking wields a tool of the titans.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99679, 'enUS', '', 'My... my village. It is gone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99680, 'enUS', 'In the name of the Dark Lady!', '', 26124);
