INSERT INTO `broadcast_text_locale` VALUES (99682, 'enUS', 'Come, rest your weary soul on a feathery bed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99683, 'enUS', 'Good, you made it! Defeat Helya and I will set these souls free!', '', 26124);
