INSERT INTO `broadcast_text_locale` VALUES (99695, 'enUS', 'What, me bein\' all hidden ain\'t enough of a clue that I wanna be left alone?$b$bGet lost, if ya know what\'s good for ya!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99696, 'enUS', '', 'Bring it on!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99697, 'enUS', '', 'I\'ve already lost two sisters to the demons. You want to investigate that cart? It\'s all yours.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99698, 'enUS', '', 'Don\'t tell anyone, ok?', 26124);
