INSERT INTO `broadcast_text_locale` VALUES (99710, 'enUS', 'Yeah? So what do you want? I\'m tryin\' to do business here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99711, 'enUS', 'Ildine sent me. Where\'s the shipment you owe her?', 'Ildine sent me. Where\'s the shipment you owe her?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99712, 'enUS', 'Tell Ildine I got a better offer. Now scram, before I have to get rough!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99713, 'enUS', 'If you\'re thinking about bribing me, think again. I have a duty to keep these sewers safe!$b$b<Raethan lowers his voice to a whisper:>$b$bCome back in a few minutes and we\'ll talk, $c.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99714, 'enUS', 'Yes, I know that people are fighting out there. My guards are simply... taking a break.$b$b<Raethan lowers his voice to a whisper:>$b$bYou want us back on duty? Come back in a few minutes, and bring some Sightless Eyes with you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99715, 'enUS', 'Quick tip on bribes, $r: make sure you actually have the money before you make the offer. Now get out of my sight, unless you\'re looking for a room in Violet Hold!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99716, 'enUS', '<Threaten Belamy.>', '<Threaten Belamy.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99717, 'enUS', 'Okay, okay! You called my bluff. I\'ll tell you what you want to know, tough $Gguy:gal;.$b$bI sold the enchanting supplies to a fence up in Dalaran. Grimbore, Boargrim... somethin\' orcish like that.$b$bNow beat it, will ya? I\'ve got a customer on the way.', '', 26124);
